          v_counter := v_counter + 1;
          l_cur_ut(v_counter) := rec.table_name;
              mgr_utl.p_triggers_disable            (l_cur_ut(v_counter));
              mgr_utl.p_constraints_disable         (l_cur_ut(v_counter));
              mgr_utl.p_ref_constraints_disable     (l_cur_ut(v_counter));
