        trace('выполняем вставку в accounts');
        --
        BEGIN
        v_count       := 0;
        c_n           := 0;
        l_migration_start_time := sysdate;
        l_start_time := current_timestamp;

        mgr_utl.mantain_error_table(l_tab);

        OPEN l_cur FOR
              'select
                      rukey(acc) as acc
                     ,kf
                     ,nls
                     ,kv
                     ,branch
                     ,nlsalt
                     ,nbs
                     ,nbs2
                     ,daos
                     ,dapp
                     ,ruuser(isp) as isp
                     ,nms
                     ,lim
                     ,ostb
                     ,ostc
                     ,ostf
                     ,ostq
                     ,dos
                     ,kos
                     ,dosq
                     ,kosq
                     ,pap
