                            ,ip_task_start_time                   => l_start_time
                            ,ip_task_end_time                     => l_end_time
                            ,ip_time_duration                     => l_time_duration
                            ,ip_log_message                       => 'Error'
                            );

    end;
    --
    commit;
    --
  bc.home();

  mgr_utl.finalize();
  --
  trace('%s: finished', p);
  --
