--                 branch=c.branch;
            update branch_parameters
            set    val=to_char(l_rnk)
            where  tag='RNK' and
                   branch=c.branch;

            if SQL%ROWCOUNT=0 then
--        exception when no_data_found then
             insert
             into   branch_parameters (branch,
                                       tag   ,
                                       val)
                               values (c.branch,
                                       'RNK'   ,
                                       to_char(l_rnk));
            end if;
          end;
        exception when others then
          bc.set_context;
        end;
        bc.set_context;
      end loop;
    end;

