    tokf;
    l_okpo := f_ourokpo;
    toroot;

    bars_alerter(0);

    mfog_ := f_ourmfo_g;

    l_ins_ := 0;
    p_open := 0;
    p_code := 0;

    select data_length
    into   len_adr_
    from   user_tab_columns
    where  table_name='CUSTOMER' and
           column_name='ADR';

    select data_length
    into   len_organ_
    from   user_tab_columns
    where  table_name='PERSON' and
           column_name='ORGAN';

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=mfog_                  and
           logname like 'U'||migraSK.kodobl(mfog_)||'_%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=mfog_ and
             logname like 'U'||migraSK.kodobl(mfog_)||'_%';
    end if;

--  Аня for Offline

    begin
      for c in (select branch,
                       name
                from   branch
                where  branch like '/'||mfog_||'/%'
                order  by branch)
      loop
        begin
          bc.subst_branch(c.branch);
          begin
            select rnk
            into   l_rnk
            from   customer
            where  branch=c.branch and
                   notes='BRANCH<->RNK';
          exception when no_data_found then

--          select s_customer.nextval
--          into   l_rnk
--          from   dual;

            execute immediate 'select s_customer.nextval from dual' into l_rnk;

            insert
            into   customer (rnk      ,
