                             custtype ,
                             codcagent,
                             okpo     ,
                             country  ,
                             nmk      ,
                             nmkk     ,
                             notes    ,
                             ise      ,
                             fs       ,
                             oe       ,
                             ved      ,
                             sed      ,
                             k050     ,
                             branch)
                     values (l_rnk                    ,
                             1                        ,
                             1                        ,
                             1                        ,
                             l_okpo                   ,
                             804                      ,
                             trim(substr(c.name,1,70)),
                             trim(substr(c.name,1,38)),
                             'BRANCH<->RNK'           ,
                             '12201'                  ,
                             '31'                     ,
                             '96130'                  ,
                             '65120'                  ,
                             '80  '                   ,
                             '610'                    ,
                             c.branch);
          end;

          begin
            update branch_parameters
            set    val=to_char(l_rnk)
            where  tag='RNK' and
                   branch=c.branch;

            if SQL%ROWCOUNT=0 then
             insert
             into   branch_parameters (branch,
                                       tag   ,
                                       val)
                               values (c.branch,
                                       'RNK'   ,
                                       to_char(l_rnk));
            end if;
          end;
        exception when others then
          null;
        end;
        bc.set_context;
      end loop;
    end;

    commit;

    select count(*)
    into   p_open
    from   customer
    where  notes='BRANCH<->RNK';

    MFO_ := null; -- на этой херне задватраивало клиентов

    sql_:='select distinct
                  c.TAXF                                                                        TAXF     ,  -- Код платника податкiв
                  nvl(c.DATE_ON,add_months(trunc(sysdate,''YEAR''),-12))                        DATEON   ,  -- Дата реєстрацiї
                  c.DATEA                                                                       DATEA    ,  -- Дата реєстрацiї в адмiнiстрацiї
