                  migraSK.valid_symbol(trim(substr(trim(nvl(c.NMK,c.NMKK)),1,70)))              NMK      ,  -- Повне найменування
                  c.RNK                                                                         IDCLIENT ,  -- Код
                  0                                                                             GROUP_C  ,  -- Код+
                  migraSK.valid_symbol(c.OKPO)                                                  OKPO     ,  -- Iдентифiкацiйний код
                  nvl(nullif(nvl(c.COUNTRY,0),0),804)                                           COUNTRY  ,  -- Країна
                  nullif(c.C_DST,0)                                                             CDST     ,  -- Податкова iнспекцiя
                  nullif(c.C_REG,0)                                                             CREG     ,  -- Регiон
                  c.PRINSIDER                                                                   PRINSIDER,  -- Iнсайдер
