                  migraSK.valid_symbol(trim(substr(trim(nvl(c.NMKK,c.NMK)),1,38)))              NMKK     ,  -- Коротке найменування
                  substr(''00000''||trim(c.OE),-5)                                              OE       ,  -- Галузь
                  decode(c.CUSTTYPE,0,decode(c.COUNTRY,804,5,6),1,decode(c.COUNTRY,804,3,4),0)  CODCAGENT,  -- Характеристика
                  substr(''00000''||trim(c.ISE),-5)                                             ISE      ,  -- Iнстит.сектор економiки
