            --3. INSERT or UPDATE SPECPARAM

            if fl_ = 0 then
              begin
                insert
                into   SPECPARAM (acc ,
                                  R011,
                                  R013,
                                  S240,
                                  S080,
                                  S180,
                                  S190,
                                  S181,
                                  S260,
                                  S200,
                                  S120,
                                  S031,
                                  S182,
--                                S130,
--                                S090,
                                  NKD)
                          values (accR_,
