                  update SPECPARAM
                  set    R011=R011_,
                         R013=R013_,
                         S240=S240_,
                         S080=S080_,
                         S180=S180_,
                         S190=S190_,
                         S181=S181_,
                         S260=S260_,
                         S200=S200_,
                         S120=S120_,
                         S031=S031_,
                         S182=S182_,
--                       S130=S130_,
--                       S090=S090_,
                         NKD=NKD_
                  where  acc=accR_;
                EXCEPTION WHEN OTHERS THEN
                  bars_audit.error('open_accountS6 - err(4u): '||sqlerrm||', '||Chr(13)||Chr(10)||
