                p_code := p_code+1;
                fl_ := 1;
              end;
            end if;

            if substr(nls_,1,1) in ('2','3','9') then

--            Процентные карточки

              if fl_=0 then
                begin
                  select acc
                  into   ACRdb_
                  from   accounts
                  where  nls=serv_db_s_ and kv=serv_i_va_;
