                      else
                        ACRB_ := ACRkr_;
                      end if;
                      ACRA_ := null;
                    else
                      if substr(serv_kr_s_,1,4)='3800' then
                        ACRA_ := null;
                      else
                        ACRA_ := ACRkr_;
                      end if;
                      ACRB_ := null;
                    end if;
                  end if;
                  if ACRdb_ is not null and ACRkr_ is null then
                    if substr(serv_db_s_,1,1)>'2' then
                      if substr(serv_db_s_,1,1)='3800' then
                        ACRB_ := null;
                      else
                        ACRB_ := ACRdb_;
                      end if;
                      ACRA_ := null;
                    else
                      if substr(serv_db_s_,1,1)='3800' then
                        ACRA_ := null;
                      else
