--                                   NAZN   ,
--                                   IO     ,
--                                   BRANCH    -- branch_
                                    )
                             values (accR_     ,
                                     id_       ,
                                     0         ,
--                                   null      ,
                                     0         ,
                                     1         ,
--                                   null      ,
                                     bankdate  ,
--                                   bankdate-1,
--                                   null      ,
                                     ACRA_     ,  -- ACRA ???
                                     ACRB_
--                                   null      ,
--                                   null      ,
--                                   null      ,
--                                   null      ,
--                                   null      ,
--                                   null      ,
--                                   null      ,
--                                   null      ,
--                                   branch_
                                    );
                  EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                    update INT_ACCN
                    set    METR   =0         ,
--                         BASEM  =null      ,
                           BASEY  =0         ,
                           FREQ   =1         ,
--                         STP_DAT=null      ,
                           ACR_DAT=bankdate  ,
--                         APL_DAT=bankdate-1,
--                         TT     =null      ,
                           ACRA   =ACRA_     ,  -- ???
                           ACRB   =ACRB_
--                         S      =null      ,
--                         TTB    =null      ,
--                         KVB    =null      ,
--                         NLSB   =null      ,
--                         MFOB   =null      ,
--                         NAMB   =null      ,
--                         NAZN   =null      ,
--                         IO     =null      ,
--                         BRANCH =branch_
                    where  acc=accR_ and
                           id=id_;
                            WHEN OTHERS THEN
                    bars_audit.error('open_accountS6 - err(5): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                     'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                     'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                     'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                    p_code := p_code+1;
                    fl_ := 1;
                  end;

--                5. INSERT or UPDATE INT_RATN

                  if fl_=0 then
                    begin
                      insert
                      into   INT_RATN (acc   ,
                                       id    ,
                                       BDAT  ,
                                       IR    ,
                                       BR    ,
                                       OP    ,
                                       IDU --,
--                                     BRANCH
                                      )
                               values (accR_,
                                       id_  ,
                                       bdat_,
                                       IR_  ,
                                       null ,
                                       null ,
                                       1  --,
--                                     branch_
                                      );
                    EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                      update INT_RATN
                      set    IR=IR_ ,
                             BR=null,
                             OP=null,
                             IDU=1 --,
--                           BRANCH=branch_
                      where  acc=accR_ and
                             id=id_    and
                             BDAT=bdat_;
                              WHEN OTHERS THEN
                      bars_audit.error('open_accountS6 - err(6):' ||sqlerrm||', '||Chr(13)||Chr(10)||
                                                       'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                       'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                       'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                      p_code := p_code+1;
                      fl_ := 1;
                    end;
                  end if;
                end if;
              end if;
            end if;
          end if;
        end if;
      end if;

<<kakaxer>> null;

      bc.set_context;

      if fl_=0 then
        commit;
        p_open := p_open+1;
      else
        rollback;
      end if;

      open_ := p_open;

      if l_ins_>0 and mod(l_ins_,10000)=0 then

--      сбор статистики по таблицам счетов (ACCOUNTS и связанные)

        BEGIN
          DBMS_JOB.SUBMIT (job       => Job_                    ,
                           what      => 'migraSK.tableAC_stats;',
                           next_date => sysdate                 ,
                           interval  => null                    ,
                           no_parse  => FALSE);
        exception when others then
          bars_audit.error('open_accountS6 - err(J): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        END;

      end if;

      p_open := open_;

    end loop;
    close cur_;

    dbms_application_info.set_client_info(' ');

    begin
      execute immediate 'alter trigger tbu_accounts_dazs enable';
    exception when OTHERS then
      null;
    end;

    begin
      execute immediate 'alter trigger tbu_accounts_1004 enable';
