                              dapp_   ,
                              ostc_   ,
                              0       ,
                              0);
            EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
               update saldoa
               set    pdat=dapp_,
                      ostf=ostc_,
                      dos=0     ,
                      kos=0
               where  acc=accR_ and
                      fdat=gl.bdate;
