                exception when others then
                  null;
                end;
              end if;
            end if;

            if fl_ = 0 then
              begin
                insert
                into   SPECPARAM (acc ,
                                  R011,
                                  R013,
                                  S240,
                                  S080,
                                  S180,
                                  S190,
                                  S181,
--                                S260,
                                  S200,
                                  S120,
                                  S031,
                                  S182,
                                  S130,
                                  S090,
                                  NKD)
                          values (accR_,
                                  R011_,
                                  R013_,
                                  S240_,
                                  S080_,
                                  S180_,
                                  S190_,
                                  S181_,
--                                S260_,
                                  S200_,
                                  S120_,
                                  S031_,
                                  S182_,
                                  S130_,
                                  S090_,
                                  NKD_);
              EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                begin
                  update SPECPARAM
                  set    R011=R011_,
                         R013=R013_,
                         S240=S240_,
                         S080=S080_,
                         S180=S180_,
                         S190=S190_,
                         S181=S181_,
--                       S260=S260_,
                         S200=S200_,
                         S120=S120_,
                         S031=S031_,
                         S182=S182_,
                         S130=S130_,
                         S090=S090_,
                         NKD=NKD_
                  where  acc=accR_;
                EXCEPTION WHEN OTHERS THEN
                  bars_audit.error('open_accountUN - err(4u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                    'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                    'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                    'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                  p_code := p_code+1;
                  fl_ := 1;
                end;
                        WHEN OTHERS THEN
                bars_audit.error('open_accountUN - err(4): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                p_code := p_code+1;
                fl_ := 1;
              end;
            end if;

            if substr(nls_,1,1) in ('2','3','9') then

--            Процентные карточки ???

              if fl_=0 then

                if IR_ is not null then

                  if pap_=1 then
                     id_ := 0;
                  else
                     id_ := 1;
                  end if;

                  if NLSNPROC_ is not null then
                    begin
                      select acc
                      into   acra_
                      from   accounts
                      where  nls=NLSNPROC_ and
                             kv=kv_;
                    exception when no_data_found then
                      acra_ := null;
                    end;
                  else
                    acra_ := null;
                  end if;

                  if NLSDR_ is not null then
                    begin
                      select acc
                      into   acrb_
                      from   accounts
                      where  nls=NLSDR_ and
