--                4. INSERT or UPDATE INT_ACCN

                  begin
                    insert
                    into   INT_ACCN (ACC    ,  -- accR_
                                     ID     ,  -- id_
                                     METR   ,  -- 0
                                     BASEY  ,  -- 0
                                     FREQ   ,  -- 1
                                     ACR_DAT,  -- bankdate
                                     ACRA   ,  -- ???
                                     ACRB)
                             values (accR_   ,
                                     id_     ,
                                     0       ,
                                     0       ,
                                     1       ,
                                     bankdate,
                                     acra_   ,
                                     acrb_);
                  EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                    update INT_ACCN
                    set    METR=0          ,
                           BASEY=0         ,
                           FREQ=1          ,
                           ACR_DAT=bankdate,
                           ACRA=acra_      ,
                           ACRB=acrb_
                    where  acc=accR_ and
                           id=id_;
                            WHEN OTHERS THEN
                    bars_audit.error('open_accountUN - err(5): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                     'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                     'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                     'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                    p_code := p_code+1;
                    fl_ := 1;
                  end;

--                5. INSERT or UPDATE INT_RATN

                  if fl_=0 then
                    begin
                      insert
                      into   INT_RATN (acc ,
                                       id  ,
                                       BDAT,
                                       IR  ,
                                       IDU
                                      )
                               values (accR_              ,
                                       id_                ,
                                       nvl(BDAT_,gl.bdate),
                                       IR_                ,
                                       1
                                      );
                    EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                      update INT_RATN
                      set    IR=IR_ ,
