                           interval  => null                    ,
                           no_parse  => FALSE);
        exception when others then
          bars_audit.error('open_accountUN - err(J): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        END;

      end if;

      p_open := open_;

