    end;

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 enable';
    exception when OTHERS then
      null;
    end;

    gl.frcvr := 0;

--  гривна по всем бранчам в cur_rates$base

    migraSK.grn11tocurrates;

    bars_alerter(1);

  end open_accountUN;

