--

  procedure create_customerS5f (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type        cur is ref cursor;
    cur_        cur;
    sql_        varchar2(32000);
    rnk_        number;
    fl_         number;
    ispg_       varchar2(32);

    TAXF_       varchar2(12);
    DATEON_     date;
    DATEA_      date;
    DATET_      date;
    BDAY_       date;
    FS_         varchar2(3);
    SED_        char(4);
    NMK_        varchar2(70);
    IDCLIENT_   number;
    GROUP_C_    number;
    OKPO_       varchar2(12);
    COUNTRY_    number;
    CREG_       number;
    CDST_       number;
    PRINSIDER_  number;
    ISP_        number;
    NMKK_       varchar2(38);
    OE_         varchar2(5);
    CODCAGENT_  number;
    ISE_        varchar2(5);
    SEX_        number;
    TGR_        number;
    CUSTTYPE_   number;
    VED_        varchar2(5);
    ND_         varchar2(10);
    CRISK_      number;
    PHONE_      varchar2(20);
    POSTIND_    varchar2(10);
    OBLAST_     varchar2(40);
    RAYON_      varchar2(25);
    CITY_       varchar2(40);
    STREET_     varchar2(40);
    HOUSE_      varchar2(20);
    KVARTIRA_   varchar2(20);
    TEL_        varchar2(20);
    EMAIL_      varchar2(100);
    SAB_        varchar2(6);
    STMT_       number;
    RGADM_      varchar2(30);
    MFO_        varchar2(12);
    BIC_        varchar2(12);

--  l_ins_      number;

    SERIA_      VARCHAR2(10);
    NOMBER_     VARCHAR2(20);
    ORGAN_      VARCHAR2(50);
    DOTE_       date;

    fi3_        char(3);
    Adr_        customer.adr%type;
    K050_       char(3);
    MFOg_       varchar2(12);

  begin

    bars_alerter(0);

--  l_ins_ := 0;
    p_open := 0;
    p_code := 0;

    MFOg_ := f_ourmfo_g;

    execute immediate 'select substr(min(branch),-4,3)
                       from   s6_s5_filial'
                       into   fi3_;

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=MFOg_                              and
           logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=MFOg_ and
             logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%';
      if ispg_ is null then
        select to_char(min(id))
        into   ispg_
        from   staff$base
        where  bc.extract_mfo(branch)=MFOg_ and
               tabn is not null;
        if ispg_ is null then
          select to_char(min(id))
          into   ispg_
          from   staff$base
          where  bc.extract_mfo(branch)=MFOg_;
        end if;
      end if;
    end if;

    MFO_ := null;

    sql_:='select distinct
                  TAXF     ,
                  DATEON   ,
                  DATEA    ,
                  DATET    ,
                  BDAY     ,
                  FS       ,
                  SED      ,
                  K050     ,
                  NMK      ,
                  IDCLIENT ,
                  GROUP_C  ,
                  OKPO     ,
                  COUNTRY  ,
                  CDST     ,
                  CREG     ,
                  PRINSIDER,
                  ISP      ,
                  NMKK     ,
                  OE       ,
                  CODCAGENT,
                  ISE      ,
                  SEX      ,
                  TGR      ,
                  CUSTTYPE ,
                  VED      ,
                  ND       ,
                  CRISK    ,
                  PHONE    ,
                  POSTIND  ,
                  OBLAST   ,
                  RAYON    ,
                  CITY     ,
                  STREET   ,
                  HOUSE    ,
                  KVARTIRA ,
                  TEL      ,
                  EMAIL    ,
                  SAB      ,
                  STMT     ,
                  RGADM    ,
                  BIC      ,
                  SERIA    ,
                  NOMBER   ,
                  ORGAN    ,
                  DOTE     ,
                  Adr
           from   (select distinct
                          to_char(c.C_NALOG)                                            TAXF     ,  -- Код платника податкiв                   smallint
                          nvl(c.D_CREATE,add_months(trunc(sysdate,''YEAR''),-12))       DATEON   ,  -- Дата реєстрацiї                         smalldatetime
                          c.D_REG_SA                                                    DATEA    ,  -- Дата реєстрацiї в адмiнiстрацiї         smalldatetime
                          c.D_REG_STI                                                   DATET    ,  -- Дата реєстрацiї в податковiй iнспекцiї  smalldatetime
                          c.B_DATE                                                      BDAY     ,  -- Дата народження                         smalldatetime
                          decode(substr(''00''||to_char(c.F_PRIVAT),-2),''99'',''ZZ'',
                                 substr(''00''||to_char(c.F_PRIVAT),-2))                FS       ,  -- Форма власностi                         tinyint
                          nvl(k.K051,''00'')||''  ''                                    SED      ,  -- Форма господарювання                    char(4)
                          substr(''000''||to_char(c.K050),-3)                           K050     ,  -- Параметр K050                           char(3)
                          trim(substr(c.NAME,1,70))                                     NMK      ,  -- Повне найменування                      varchar(150)
                          c.CLI_KOD                                                     IDCLIENT ,  -- Код                                     int
                          c.GROUP_C                                                     GROUP_C  ,  -- Код+                                    int
                          trim(substr(migraSK.valid_symbol(c.ID),1,10))                 OKPO     ,  -- Iдентифiкацiйний код                    varchar(10)
                          nvl(nullif(nvl(c.COUNTRY,0),0),804)                           COUNTRY  ,  -- Країна                                  smallint
                          mod(c.C_DST,100)                                              CDST     ,  -- Податкова iнспекцiя                     tinyint
                          trunc(c.C_DST/100)                                            CREG     ,  -- Регiон                                  tinyint
                          decode(nvl(c.INSIDER,99),21,2,22,3,23,5,30,4,c.INSIDER)       PRINSIDER,  -- Iнсайдер                                tinyint
                          nvl(u.UBARS,'||ispg_||')                                      ISP      ,  -- Код користувача                         smallint
                          trim(substr(c.NAME_S,1,38))                                   NMKK     ,  -- Коротке найменування                    varchar(40)
                          substr(''00000''||to_char(c.OTRASL),-5)                       OE       ,  -- Галузь                                  int
                          decode(c.REZIDENT,0,decode(c.GROUP_C,3,4,5,6,2),c.GROUP_C)    CODCAGENT,  -- Характеристика
                          substr(''00000''||nvl(to_char(trim(c.SECTOR)),''''),-5)       ISE      ,  -- Iнстит.сектор економiки                 int
                          null                                                          SEX      ,  -- Стать                                   tinyint
                          decode(nvl(c.TIN_KEY,0),0,3,c.TIN_KEY)                        TGR      ,  -- Тип держреєстру                         tinyint
                          decode(k.K051,''91'',3,decode(c.GROUP_C,3,2,5,3,4,2,6,3,1))   CUSTTYPE ,  -- Тип клiєнта                             tinyint
                          substr(''00000''||to_char(c.VID_ECO),-5)                      VED      ,  -- Вид економ.дiяльностi                   int
                          to_char(c.CLI_KOD)                                            ND       ,
                          decode(c.CLASS,''А'',1,
                                         ''Б'',2,
                                         ''В'',3,
                                         ''Г'',4,
                                         ''Д'',5,null)                                  CRISK    ,
                          trim(substr(trim(migraSK.valid_symbol(c.PHONE)),1,20))        PHONE    ,  -- Телефон                                 varchar(20)
                          null                                                          POSTIND  ,  -- Поштовий iндекс                         varchar(10)
                          null                                                          OBLAST   ,  -- Область                                 varchar(40)
                          null                                                          RAYON    ,  -- Район                                   varchar(25)
                          null                                                          CITY     ,  -- Мiсто, насел.пункт                      varchar(40)
                          null                                                          STREET   ,  -- Вулиця                                  varchar(40)
                          null                                                          HOUSE    ,  -- Дiм                                     varchar(20)
                          null                                                          KVARTIRA ,  -- Квартира                                varchar(20)
                          trim(substr(trim(migraSK.valid_symbol(c.PHONE)),1,20))        TEL      ,  -- Телефон                                 varchar(20)
                          trim(substr(trim(migraSK.valid_symbol(c.E_MAIL)),1,100))      EMAIL    ,  -- Електронна адреса                       varchar(100)
                          null                                                          SAB      ,  -- SAB
                          null                                                          STMT     ,
                          null                                                          RGADM    ,
                          null                                                          BIC      ,
                          c.P_SERIA                                                     SERIA    ,  -- Серия паспорта
                          c.P_NUMBER                                                    NOMBER   ,  -- Номер паспорта
                          trim(substr(c.P_GIVE,1,50))                                   ORGAN    ,  -- Кем выдан
                          c.P_DATE                                                      DOTE     ,  -- Когда выдан
                          c.ADRESS                                                      Adr
                   from   S6_S5_CLIENT                           c,
                          (select K051,
                                  substr(K050,1,3) K050
                           from   KL_K050
                           where  k051 is not null
                          union all
                           select k051,
                                  substr(K050,1,3) K050
                           from   KL_K051
                           where  k050 in (select k050
                                           from   kl_k050
                                           where  k051 is null)) k,
                          USERS_SKARB6_BARS                      u
                   where  substr(k.K050(+),1,3)=
--                        substr(''00''||to_char(nvl(c.k050,0)),-3) and
                          substr(''000''||to_char(c.K050),-3)       and
                          c.ISP=u.USKARB6(+)                        and
                          u.FILIAL(+)='''||p_filial||''')
           order  by IDCLIENT,GROUP_C';

--  bars_audit.info('create_customerS5f - sql_='||substr(sql_,   1,3000));
--  bars_audit.info('create_customerS5f - sql_='||substr(sql_,3001,3000));
--  bars_audit.info('create_customerS5f - sql_='||substr(sql_,6001,3000));
--  bars_audit.info('create_customerS5f - sql_='||substr(sql_,9001,3000));

    open cur_ for sql_;

    loop
      fetch cur_ into TAXF_     ,
                      DATEON_   ,
                      DATEA_    ,
                      DATET_    ,
                      BDAY_     ,
                      FS_       ,
                      SED_      ,
                      K050_     ,
                      NMK_      ,
                      IDCLIENT_ ,
                      GROUP_C_  ,
                      OKPO_     ,
                      COUNTRY_  ,
                      CDST_     ,
                      CREG_     ,
                      PRINSIDER_,
                      ISP_      ,
                      NMKK_     ,
                      OE_       ,
                      CODCAGENT_,
                      ISE_      ,
                      SEX_      ,
                      TGR_      ,
                      CUSTTYPE_ ,
                      VED_      ,
                      ND_       ,
                      CRISK_    ,
                      PHONE_    ,
                      POSTIND_  ,
                      OBLAST_   ,
                      RAYON_    ,
                      CITY_     ,
                      STREET_   ,
                      HOUSE_    ,
                      KVARTIRA_ ,
                      TEL_      ,
                      EMAIL_    ,
                      SAB_      ,
                      STMT_     ,
                      RGADM_    ,
                      BIC_      ,
                      SERIA_    ,
                      NOMBER_   ,
                      ORGAN_    ,
                      DOTE_     ,
                      Adr_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        select c.rnk
        into   rnk_
        from   customerw w,
               customer  c
        where  w.tag='RS6S5'                                                    and
               w.value=to_char(IDCLIENT_)||to_char(GROUP_C_)||to_char(p_filial) and
               w.rnk=c.rnk                                                      and
               bc.extract_mfo(c.branch)=MFOg_                                   and
               rownum<2;
      exception when no_data_found then
        RNK_ := null;
      end;

      bc.subst_mfo(MFOg_);

      begin

        kl.setCustomerAttr(RNK_,                     -- Rnk_  IN OUT customer.rnk%type        -- Customer number
                           CUSTTYPE_,                -- Custtype_    customer.custtype%type   -- Тип клиента: 1-банк, 2-юр.лицо, 3-физ.лицо
                           ND_,                      -- Nd_          customer.nd%type         -- № договора
                           NMK_,                     -- Nmk_         customer.nmk%type        -- Наименование клиента
                           null,                     -- Nmkv_        customer.nmkv%type       -- Наименование клиента международное
                           NMKK_,                    -- Nmkk_        customer.nmkk%type       -- Наименование клиента краткое
                           Adr_,                     -- Adr_         customer.adr%type        -- Адрес клиента
                           CODCAGENT_,               -- Codcagent_   customer.codcagent%type  -- Характеристика
                           COUNTRY_,                 -- Country_     customer.country%type    -- Страна
                           PRINSIDER_,               -- Prinsider_   customer.prinsider%type  -- Признак инсайдера
                           TGR_,                     -- Tgr_         customer.tgr%type        -- Тип гос.реестра
                           OKPO_,                    -- Okpo_        customer.okpo%type       -- ОКПО
                           STMT_,                    -- Stmt_        customer.stmt%type       -- Формат выписки
                           SAB_,                     -- Sab_         customer.sab%type        -- Эл.код
                           DATEON_,                  -- DateOn_      customer.date_on%type    -- Дата регистрации
                           TAXF_,                    -- Taxf_        customer.taxf%type       -- Налоговый код
                           CREG_,                    -- CReg_        customer.c_reg%type      -- Код обл.НИ
                           CDST_,                    -- CDst_        customer.c_dst%type      -- Код район.НИ
                           null,                     -- Adm_         customer.adm%type        -- Админ.орган
                           null,                     -- RgTax_       customer.rgtax%type      -- Рег номер в НИ
                           RGADM_,                   -- RgAdm_       customer.rgadm%type      -- Рег номер в Адм.
                           DATET_,                   -- DateT_       customer.datet%type      -- Дата рег в НИ
                           DATEA_,                   -- DateA_       customer.datea%type      -- Дата рег. в администрации
                           ISE_,                     -- Ise_         customer.ise%type        -- Инст. сек. экономики
                           FS_,                      -- Fs_          customer.fs%type         -- Форма собственности
                           OE_,                      -- Oe_          customer.oe%type         -- Отрасль экономики
                           VED_,                     -- Ved_         customer.ved%type        -- Вид эк. деятельности
                           SED_,                     -- Sed_         customer.sed%type        -- Форма хозяйствования
                           'Imported from SKARB5f',  -- Notes_       customer.notes%type      -- Примечание
                           null,                     -- Notesec_     customer.notesec%type    -- Примечание для службы безопасности
                           CRISK_, /* null */        -- CRisk_       customer.crisk%type      -- Категория риска
                           null,                     -- Pincode_     customer.pincode%type    --
                           null,                     -- RnkP_        customer.rnkp%type       -- Рег. номер холдинга
                           null,                     -- Lim_         customer.lim%type        -- Лимит кассы
                           null,                     -- NomPDV_      customer.nompdv%type     -- № в реестре плат. ПДВ
                           '9',                      -- MB_          customer.mb%type         -- Принадл. малому бизнесу
                           null,                     -- BC_          customer.bc%type         -- Признак НЕклиента банка
