    from   user_tab_columns
    where  table_name='PERSON' and
           column_name='ORGAN';

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=MFOg_                              and
           logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=MFOg_ and
             logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%';
      if ispg_ is null then
        select to_char(min(id))
        into   ispg_
        from   staff$base
        where  bc.extract_mfo(branch)=MFOg_ and
               tabn is not null;
        if ispg_ is null then
          select to_char(min(id))
          into   ispg_
          from   staff$base
          where  bc.extract_mfo(branch)=MFOg_;
        end if;
      end if;
    end if;

    begin
      execute immediate 'create or replace view S6_S6_CONTRACT_V as
              select   IDCLIENT                        ,
                       GROUP_C                         ,
                       min(trim(IDCONTRACT)) IDCONTRACT,
                       min(C_RISK)           C_RISK
              from     S6_S6_CONTRACT
              group by IDCLIENT,
                       GROUP_C';
    exception when OTHERS then
      null;
    end;

    MFO_ := null;

    sql_:='select distinct
                  TAXF     ,
                  DATEON   ,
                  DATEA    ,
                  DATET    ,
                  BDAY     ,
                  FS       ,
                  SED      ,
                  K050     ,
                  NMK      ,
                  IDCLIENT ,
                  GROUP_C  ,
                  OKPO     ,
                  COUNTRY  ,
                  CDST     ,
                  CREG     ,
                  PRINSIDER,
                  ISP      ,
                  NMKK     ,
                  OE       ,
                  CODCAGENT,
                  ISE      ,
                  SEX      ,
                  TGR      ,
                  CUSTTYPE ,
                  VED      ,
                  ND       ,
                  CRISK    ,
                  PHONE    ,
                  POSTIND  ,
                  OBLAST   ,
                  RAYON    ,
                  CITY     ,
                  STREET   ,
                  HOUSE    ,
                  KVARTIRA ,
                  TEL      ,
                  EMAIL    ,
                  SAB      ,
                  STMT     ,
                  RGADM    ,
                  BIC
           from   (select distinct
                          to_char(c.CODNAL)                                                    TAXF     ,  -- Код платника податкiв                   smallint
                          nvl(c.D_OPEN,add_months(trunc(sysdate,''YEAR''),-12))                DATEON   ,  -- Дата реєстрацiї                         smalldatetime
                          c.D_REGSA                                                            DATEA    ,  -- Дата реєстрацiї в адмiнiстрацiї         smalldatetime
                          c.D_REGSTI                                                           DATET    ,  -- Дата реєстрацiї в податковiй iнспекцiї  smalldatetime
                          c.DATEBIRTH                                                          BDAY     ,  -- Дата народження                         smalldatetime
                          nvl(nullif(substr(''00''||to_char(c.FORMPRIVAT),-2),''99''),''ZZ'')  FS       ,  -- Форма власностi                         tinyint
                          substr(''00''||to_char(fx.K051),-2)||''  ''                          SED      ,  -- Форма господарювання                    char(4)
                          substr(''000''||to_char(c."FORMXOZ"),-3)                             K050     ,  -- параметр K050                           char(3)
                          migraSK.valid_symbol(trim(substr(nvl(c.FULLNAME,c.NAME),1,70)))      NMK      ,  -- Повне найменування                      varchar(150)
                          c.IDCLIENT                                                           IDCLIENT ,  -- Код                                     int
                          c.GROUP_C                                                            GROUP_C  ,  -- Код+                                    int
                          migraSK.valid_symbol(c.IDCODE)                                       OKPO     ,  -- Iдентифiкацiйний код                    varchar(10)
                          c.IDCOUNTRY                                                          COUNTRY  ,  -- Країна                                  smallint
                          c.IDNAL                                                              CDST     ,  -- Податкова iнспекцiя                     tinyint
                          c.IDREG                                                              CREG     ,  -- Регiон                                  tinyint
                          decode(nvl(c.INSIDER,99),21,2,22,3,23,5,30,4,c.INSIDER)              PRINSIDER,  -- Iнсайдер                                tinyint
                          nvl(u.UBARS,'||ispg_||')                                             ISP      ,  -- Код користувача                         smallint
                          migraSK.valid_symbol(trim(substr(nvl(c.NAME,c.FULLNAME),1,38)))      NMKK     ,  -- Коротке найменування                    varchar(40)
                          substr(''00000''||to_char(c.OTRASL),-5)                              OE       ,  -- Галузь                                  int
                          decode(c.REZIDENT,0,
                          decode(decode(c.TYPECLIENT,0,3,1,2,1),3,6,2,4,2),
                          decode(decode(c.TYPECLIENT,0,3,1,2,1),3,5,2,3,1))                    CODCAGENT,  -- Характеристика
                          substr(''00000''||nvl(to_char(trim(c.SECTOR)),''''),-5)              ISE      ,  -- Iнстит.сектор економiки                 int
                          c.SEX                                                                SEX      ,  -- Стать                                   tinyint
                          decode(nvl(c.TINKEY,0),0,3,4,2,c.TINKEY)                             TGR      ,  -- Тип держреєстру                         tinyint
                          decode(substr(''00''||to_char(fx.K051),-2),''91'',3,
                                 decode(c.TYPECLIENT,0,3,1,2,1))                               CUSTTYPE ,  -- Тип клiєнта                             tinyint
                          substr(''00000''||to_char(c.VIDECO),-5)                              VED      ,  -- Вид економ.дiяльностi                   int
                          migraSK.valid_symbol((select TRIM(SUBSTR(IDCONTRACT,1,10))
                                                from   S6_S6_CONTRACT_V
                                                where  IDCLIENT=c.IDCLIENT and
                                                       group_c=c.group_c))                     ND       ,
                          decode(pc.CLASS,''А'',1,
                                          ''Б'',2,
                                          ''В'',3,
                                          ''Г'',4,
                                          ''Д'',5,null)                                        CRISK    ,
                          migraSK.valid_symbol(ad.PHONE)                                       PHONE    ,  -- Телефон                                 varchar(20)
                          migraSK.valid_symbol(ad.POSTIND)                                     POSTIND  ,  -- Поштовий iндекс                         varchar(10)
                          migraSK.valid_symbol(ad.REGION)                                      OBLAST   ,  -- Область                                 varchar(40)
                          migraSK.valid_symbol(ad.RAYON)                                       RAYON    ,  -- Район                                   varchar(25)
                          migraSK.valid_symbol(ad.CITY)                                        CITY     ,  -- Мiсто, насел.пункт                      varchar(40)
                          migraSK.valid_symbol(ad.STREET)                                      STREET   ,  -- Вулиця                                  varchar(40)
                          migraSK.valid_symbol(ad.HOUSE)                                       HOUSE    ,  -- Дiм                                     varchar(20)
                          migraSK.valid_symbol(ad.APARTMENT)                                   KVARTIRA ,  -- Квартира                                varchar(20)
                          migraSK.valid_symbol(ad.PHONE)                                       TEL      ,  -- Телефон                                 varchar(20)
                          migraSK.valid_symbol(ad.EMAIL)                                       EMAIL    ,  -- Електронна адреса                       varchar(20)
                          NVL(ad.TYPEADDRES,0)                                                 TA       ,
                          null                                                                 SAB      ,  -- SAB
                          null                                                                 STMT     ,
                          null                                                                 RGADM    ,
                          migraSK.valid_symbol(sw.BIC_IN)                                      BIC
                   from   S6_S6_CLIENTS     c ,
                          S6_S6_P__CLIENTS  pc,
                          S6_S6_ADDRESSES   ad,
                          S6_S6_SWIFT_BIC   sw,
                          S6_S6_FORM_XOZ    fx,
                          USERS_SKARB6_BARS u
                   where  c.BIC='||p_filial||'           and
                          c.IDCLIENT=pc.IDCLIENT(+)      and
                          c.GROUP_C=pc.GROUP_C(+)        and
                          c.BIC=pc.BIC(+)                and
                          c.ISP_OWNER=u.USKARB6(+)       and
                          u.FILIAL(+)='''||p_filial||''' and
                          c.ADDRESS=ad.ID(+)             and
                          ad.BIC(+)='||p_filial||'       and
                          c.D_CLOSE is null              and
                          c.BIC=sw.BIC(+)                and
                          c.FORMXOZ=fx.ID(+))
           where  (IDCLIENT,GROUP_C,TA) in
                  (select IDCLIENT,GROUP_C,min(TA) from (
                   select distinct
                          to_char(c.CODNAL)                                                    TAXF     ,  -- Код платника податкiв                   smallint
                          c.D_OPEN                                                             DATEON   ,  -- Дата реєстрацiї                         smalldatetime
                          c.D_REGSA                                                            DATEA    ,  -- Дата реєстрацiї в адмiнiстрацiї         smalldatetime
                          c.D_REGSTI                                                           DATET    ,  -- Дата реєстрацiї в податковiй iнспекцiї  smalldatetime
                          c.DATEBIRTH                                                          BDAY     ,  -- Дата народження                         smalldatetime
                          nvl(nullif(substr(''00''||to_char(c.FORMPRIVAT),-2),''99''),''ZZ'')  FS       ,  -- Форма власностi                         tinyint
                          substr(''00''||to_char(fx.K051),-2)                                  SED      ,  -- Форма господарювання                    char(3)
                          substr(''000''||to_char(c."FORMXOZ"),-3)                             K050     ,  -- параметр K050                           char(3)
                          migraSK.valid_symbol(trim(substr(nvl(c.FULLNAME,c.NAME),1,70)))      NMK      ,  -- Повне найменування                      varchar(150)
                          c.IDCLIENT                                                           IDCLIENT ,  -- Код                                     int
                          c.GROUP_C                                                            GROUP_C  ,  -- Код+                                    int
                          migraSK.valid_symbol(c.IDCODE)                                       OKPO     ,  -- Iдентифiкацiйний код                    varchar(10)
                          c.IDCOUNTRY                                                          COUNTRY  ,  -- Країна                                  smallint
                          c.IDNAL                                                              CDST     ,  -- Податкова iнспекцiя                     tinyint
                          c.IDREG                                                              CREG     ,  -- Регiон                                  tinyint
                          decode(nvl(c.INSIDER,99),21,2,22,3,23,5,30,4,c.INSIDER)              PRINSIDER,  -- Iнсайдер                                tinyint
                          nvl(u.UBARS,'||ispg_||')                                             ISP      ,  -- Код користувача                         smallint
                          migraSK.valid_symbol(trim(substr(nvl(c.NAME,c.FULLNAME),1,38)))      NMKK     ,  -- Коротке найменування                    varchar(40)
                          substr(''00000''||to_char(c.OTRASL),-5)                              OE       ,  -- Галузь                                  int
                          decode(c.REZIDENT,0,
                          decode(decode(c.TYPECLIENT,0,3,1,2,1),3,6,4),
                          decode(decode(c.TYPECLIENT,0,3,1,2,1),3,5,3))                        CODCAGENT,  -- Характеристика
                          substr(''00000''||nvl(to_char(trim(c.SECTOR)),''''),-5)              ISE      ,  -- Iнстит.сектор економiки                 int
                          c.SEX                                                                SEX      ,  -- Стать                                   tinyint
                          decode(nvl(c.TINKEY,0),0,3,4,2,c.TINKEY)                             TGR      ,  -- Тип держреєстру                         tinyint
                          decode(c.TYPECLIENT,0,3,1,2,1)                                       CUSTTYPE ,  -- Тип клiєнта                             tinyint
                          substr(''00000''||to_char(c.VIDECO),-5)                              VED      ,  -- Вид економ.дiяльностi                   int
                          migraSK.valid_symbol((select TRIM(SUBSTR(IDCONTRACT,1,10))
                                                from   S6_S6_CONTRACT_V
                                                where  IDCLIENT=c.IDCLIENT and
                                                       group_c=c.group_c))                     ND       ,
                          decode(pc.CLASS,''А'',1,
                                          ''Б'',2,
                                          ''В'',3,
                                          ''Г'',4,
                                          ''Д'',5,null)                                        CRISK    ,
                          migraSK.valid_symbol(ad.PHONE)                                       PHONE    ,  -- Телефон                                 varchar(20)
                          migraSK.valid_symbol(ad.POSTIND)                                     POSTIND  ,  -- Поштовий iндекс                         varchar(10)
                          migraSK.valid_symbol(ad.REGION)                                      OBLAST   ,  -- Область                                 varchar(40)
                          migraSK.valid_symbol(ad.RAYON)                                       RAYON    ,  -- Район                                   varchar(25)
                          migraSK.valid_symbol(ad.CITY)                                        CITY     ,  -- Мiсто, насел.пункт                      varchar(40)
                          migraSK.valid_symbol(ad.STREET)                                      STREET   ,  -- Вулиця                                  varchar(40)
                          migraSK.valid_symbol(ad.HOUSE)                                       HOUSE    ,  -- Дiм                                     varchar(20)
                          migraSK.valid_symbol(ad.APARTMENT)                                   KVARTIRA ,  -- Квартира                                varchar(20)
                          migraSK.valid_symbol(ad.PHONE)                                       TEL      ,  -- Телефон                                 varchar(20)
                          migraSK.valid_symbol(ad.EMAIL)                                       EMAIL    ,  -- Електронна адреса                       varchar(20)
                          NVL(ad.TYPEADDRES,0)                                                 TA       ,
                          null                                                                 SAB      ,  -- SAB
                          null                                                                 STMT     ,
                          null                                                                 RGADM    ,
                          migraSK.valid_symbol(sw.BIC_IN)                                      BIC
                   from   S6_S6_CLIENTS     c ,
                          S6_S6_P__CLIENTS  pc,
                          S6_S6_ADDRESSES   ad,
                          S6_S6_SWIFT_BIC   sw,
                          S6_S6_FORM_XOZ    fx,
                          USERS_SKARB6_BARS u
                   where  c.BIC='||p_filial||'           and
                          c.IDCLIENT=pc.IDCLIENT(+)      and
                          c.GROUP_C=pc.GROUP_C(+)        and
                          c.BIC=pc.BIC(+)                and
                          c.ISP_OWNER=u.USKARB6(+)       and
                          u.FILIAL(+)='''||p_filial||''' and
                          c.ADDRESS=ad.ID(+)             and
                          ad.BIC(+)='||p_filial||'       and
                          c.D_CLOSE is null              and
                          c.BIC=sw.BIC(+)                and
                          c.FORMXOZ=fx.ID(+)
                  )
           group  by IDCLIENT,GROUP_C)';

    open cur_ for sql_;

    loop
      fetch cur_ into TAXF_     ,
                      DATEON_   ,
                      DATEA_    ,
                      DATET_    ,
                      BDAY_     ,
                      FS_       ,
                      SED_      ,
                      K050_     ,
                      NMK_      ,
                      IDCLIENT_ ,
                      GROUP_C_  ,
                      OKPO_     ,
                      COUNTRY_  ,
                      CDST_     ,
                      CREG_     ,
                      PRINSIDER_,
                      ISP_      ,
                      NMKK_     ,
                      OE_       ,
                      CODCAGENT_,
                      ISE_      ,
                      SEX_      ,
                      TGR_      ,
                      CUSTTYPE_ ,
                      VED_      ,
                      ND_       ,
                      CRISK_    ,
                      PHONE_    ,
                      POSTIND_  ,
                      OBLAST_   ,
                      RAYON_    ,
                      CITY_     ,
                      STREET_   ,
                      HOUSE_    ,
                      KVARTIRA_ ,
                      TEL_      ,
                      EMAIL_    ,
                      SAB_      ,
                      STMT_     ,
                      RGADM_    ,
                      BIC_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        select c.rnk
        into   rnk_
        from   customerw w,
               customer  c
        where  w.tag='RS6S6'                                                    and
               w.value=to_char(IDCLIENT_)||to_char(GROUP_C_)||to_char(p_filial) and
               w.rnk=c.rnk                                                      and
               bc.extract_mfo(c.branch)=MFOg_                                   and
               rownum<2;
      exception when no_data_found then
        RNK_ := null;
      end;

      bc.subst_mfo(MFOg_);

      begin

        select length(OBLAST_||' '||
                      RAYON_ ||' '||
                      CITY_  ||' '||
                      STREET_||' '||
                      HOUSE_ ||' '||
                      KVARTIRA_)-len_adr_
        into   len_raz_
        from   dual;

        if len_raz_>0 and length(CITY_)>=len_raz_ then
          CITY_ := trim(substr(CITY_,1,length(CITY_)-len_raz_));
        else
          if len_raz_>0 and length(STREET_)>=len_raz_ then
            STREET_ := trim(substr(STREET_,1,length(STREET_)-len_raz_));
          end if;
        end if;

        kl.setCustomerAttr(RNK_                   ,  -- Rnk_ IN OUT customer.rnk%type        -- Customer number
                           CUSTTYPE_              ,  -- Custtype_   customer.custtype%type   -- Тип клиента: 1-банк, 2-юр.лицо, 3-физ.лицо
                           ND_                    ,  -- Nd_         customer.nd%type         -- № договора
                           NMK_                   ,  -- Nmk_        customer.nmk%type        -- Наименование клиента
                           null                   ,  -- Nmkv_       customer.nmkv%type       -- Наименование клиента международное
                           NMKK_                  ,  -- Nmkk_       customer.nmkk%type       -- Наименование клиента краткое
                           trim(
            /*FGOBL*/      OBLAST_||' '||
            /*FGDST*/      RAYON_ ||' '||
            /*FGTWN*/      CITY_  ||' '||
            /*FGADR(+3)*/  STREET_||' '||
                           HOUSE_ ||' '||
                           KVARTIRA_)             ,  -- Adr_        customer.adr%type        -- Адрес клиента
                           CODCAGENT_             ,  -- Codcagent_  customer.codcagent%type  -- Характеристика
                           COUNTRY_               ,  -- Country_    customer.country%type    -- Страна
                           PRINSIDER_             ,  -- Prinsider_  customer.prinsider%type  -- Признак инсайдера
                           TGR_                   ,  -- Tgr_        customer.tgr%type        -- Тип гос.реестра
                           OKPO_                  ,  -- Okpo_       customer.okpo%type       -- ОКПО
                           STMT_                  ,  -- Stmt_       customer.stmt%type       -- Формат выписки
                           SAB_                   ,  -- Sab_        customer.sab%type        -- Эл.код
                           DATEON_                ,  -- DateOn_     customer.date_on%type    -- Дата регистрации
                           TAXF_                  ,  -- Taxf_       customer.taxf%type       -- Налоговый код
                           CREG_                  ,  -- CReg_       customer.c_reg%type      -- Код обл.НИ
                           CDST_                  ,  -- CDst_       customer.c_dst%type      -- Код район.НИ
                           null                   ,  -- Adm_        customer.adm%type        -- Админ.орган
                           null                   ,  -- RgTax_      customer.rgtax%type      -- Рег номер в НИ
                           RGADM_                 ,  -- RgAdm_      customer.rgadm%type      -- Рег номер в Адм.
                           DATET_                 ,  -- DateT_      customer.datet%type      -- Дата рег в НИ
                           DATEA_                 ,  -- DateA_      customer.datea%type      -- Дата рег. в администрации
                           ISE_                   ,  -- Ise_        customer.ise%type        -- Инст. сек. экономики
                           FS_                    ,  -- Fs_         customer.fs%type         -- Форма собственности
                           OE_                    ,  -- Oe_         customer.oe%type         -- Отрасль экономики
                           VED_                   ,  -- Ved_        customer.ved%type        -- Вид эк. деятельности
                           SED_                   ,  -- Sed_        customer.sed%type        -- Форма хозяйствования
                           'Imported from SKARB6f',  -- Notes_      customer.notes%type      -- Примечание
                           null                   ,  -- Notesec_    customer.notesec%type    -- Примечание для службы безопасности
                           CRISK_                 ,  -- CRisk_      customer.crisk%type      -- Категория риска
                           null                   ,  -- Pincode_    customer.pincode%type    --
                           null                   ,  -- RnkP_       customer.rnkp%type       -- Рег. номер холдинга
                           null                   ,  -- Lim_        customer.lim%type        -- Лимит кассы
                           null                   ,  -- NomPDV_     customer.nompdv%type     -- № в реестре плат. ПДВ
                           '9'                    ,  -- MB_         customer.mb%type         -- Принадл. малому бизнесу
                           null                   ,  -- BC_         customer.bc%type         -- Признак НЕклиента банка
                           '/'||MFOg_||'/'        ,  -- Tobo_       customer.tobo%type       -- Код безбалансового отделения
                           ISP_                      -- Isp_        customer.isp%type        -- Менеджер клиента (ответ. исполнитель)
                          );
        begin
          kl.setCustomerEN(RNK_ ,  -- customer.rnk%type
                           ISE_ ,  -- customer.ise%type
                           FS_  ,  -- customer.fs%type
                           VED_ ,  -- customer.ved%type
                           OE_  ,  -- customer.oe%type
                           K050_,  -- customer.k050%type
                           SED_);  -- customer.sed%type
        exception when others then
          bars_audit.error('create_customerS6f - err(1): '||sqlerrm      ||' - '  ||Chr(13)||Chr(10)||
                           'RNK       = '||RNK_           ||', '||Chr(13)||Chr(10)||
                           'CUSTTYPE  = '||CUSTTYPE_      ||', '||Chr(13)||Chr(10)||
                           'ND        = '||ND_            ||', '||Chr(13)||Chr(10)||
                           'NMK       = '||NMK_           ||', '||Chr(13)||Chr(10)||
                           'NMKK      = '||NMKK_          ||', '||Chr(13)||Chr(10)||
                           'CODCAGENT = '||CODCAGENT_     ||', '||Chr(13)||Chr(10)||
                           'COUNTRY   = '||COUNTRY_       ||', '||Chr(13)||Chr(10)||
                           'PRINSIDER = '||PRINSIDER_     ||', '||Chr(13)||Chr(10)||
                           'TGR       = '||TGR_           ||', '||Chr(13)||Chr(10)||
                           'OKPO      = '||OKPO_          ||', '||Chr(13)||Chr(10)||
                           'STMT      = '||STMT_          ||', '||Chr(13)||Chr(10)||
                           'SAB       = '||SAB_           ||', '||Chr(13)||Chr(10)||
                           'DATEON    = '||DATEON_        ||', '||Chr(13)||Chr(10)||
                           'TAXF      = '||TAXF_          ||', '||Chr(13)||Chr(10)||
                           'CDST      = '||CDST_          ||', '||Chr(13)||Chr(10)||
                           'CREG      = '||CREG_          ||', '||Chr(13)||Chr(10)||
                           'RGADM     = '||RGADM_         ||', '||Chr(13)||Chr(10)||
                           'DATET     = '||DATET_         ||', '||Chr(13)||Chr(10)||
                           'DATEA     = '||DATEA_         ||', '||Chr(13)||Chr(10)||
                           'ISE       = '||ISE_           ||', '||Chr(13)||Chr(10)||
                           'FS        = '||FS_            ||', '||Chr(13)||Chr(10)||
                           'OE        = '||OE_            ||', '||Chr(13)||Chr(10)||
                           'VED       = '||VED_           ||', '||Chr(13)||Chr(10)||
                           'SED       = '||SED_           ||', '||Chr(13)||Chr(10)||
                           'CRISK     = '||CRISK_         ||', '||Chr(13)||Chr(10)||
                           'ISP       = '||ISP_           ||', '||Chr(13)||Chr(10)||
