                  nvl(s.kfil,0)                                                                                                                        kfil   ,
                  to_char(nvl(s.GROUP_C,0))                                                                                                            group_c
           from   customerw         cw,
                  S6_S5_SALDO       s ,
                --S6_S5_CLIENT      c ,
                  S6_S5_SPECPARA    p ,
                  S6_S5_FILIAL      f ,
                  (select nbs,
                          min(taxotype) taxotype
                   from   DPA_nbs
                   where  type=''DPA''
                   group  by nbs)   n ,
                  USERS_SKARB6_BARS u
           where  cw.tag(+)=''RS6S5''                                                        and
                  to_number(cw.value(+))=
                  nvl(s.CLI_KOD,0)*10000000+nvl(s.GROUP_C,0)*1000000+'||p_filial||'          and
--                n.nbs(+)=substr(''0000''||to_char(s.BLS),-4)                               and
                  n.nbs(+)=substr(to_char(s.NLS),1,4)                                        and
                  s.DCL is null                                                              and
                  p.NLS_SP(+)=s.NLS                                                          and
                  p.I_VA_SP(+)=s.I_VA                                                        and
                  f.kfil(+)=s.kfil                                                           and
--                (s.DCL is null                                                          or
--                 (s.DCL is not null and s.DCL>=add_months(trunc(sysdate,''YEAR''),-12)) or
--                 (s.DCL is not null and
--                  substr(''0000''||to_char(s."BalS"),-4) in (select NBS
--                                                             from   DPA_NBS)))             and
--                substr(s.NLS,1,4)||s.KSS||substr(s.NLS,6)=
--                vkrzn(substr(mfog_,1,5),
--                substr(s.NLS,1,4)||s.KSS||substr(s.NLS,6))                                 and
                  s.ISP=u.USKARB6(+)                                                         and
                  u.filial(+)='''||p_filial||'''                                             and
                  ((mod(nvl(s.GROUP_U,0),10)=0 and substr(to_char(s.NLS),1,1)<>''8'') or
                   (mod(nvl(s.GROUP_U,0),10) in (1,2) and substr(to_char(s.NLS),1,2)=''89''))
--                ((s.GROUP_U=0 and substr(s.NLS,1,1)<>''8'') or
--                ((s.GROUP_U=1 or s.GROUP_U=2) and substr(s.NLS,1,1)=''8''))                and
--         order  by substr(''0000''||to_char(s.BLS),-4) desc
           order  by substr(to_char(s.NLS),1,4) desc';
    open cur_ for sql_;

    LOOP

      fetch cur_ into rnk_    ,
                      DAPP_   ,
                      dazs_   ,
                      daos_   ,
                      kv_     ,
                      isp_    ,
                      nbs_    ,
                      pos_    ,
                      ostc_   ,
                      ostq_   ,
                      lim_    ,
                      vid_    ,
                      nms_    ,
                      nls_    ,
                      nlsalt_ ,
                      branch_ ,
                      OB22_   ,
                      OB88_   ,
                      P080_   ,
                      MFO_    ,
                      R020_FA_,
                      R011_   ,
                      R013_   ,
                      S240_   ,
                      S080_   ,
                      S180_   ,
                      S190_   ,
                      S181_   ,
                      S260_   ,
                      S200_   ,
                      S120_   ,
                      S031_   ,
                      S182_   ,
--                    S090_   ,
                      NKD_    ,
                      pap_    ,
                      NLSs6_  ,
                      I_VAs6_ ,
                      kfil_   ,
                      group_c_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

--    выгребаем процентную ставку

      begin
        execute immediate 'select date_st,
                                  proc_st
                           from   (select date_st,
                                          proc_st
                                   from   S6_S5_HIST_PR
                                   where  NLS='||nls_||' and
                                          I_VA='||to_char(kv_)||'
                                   order  by 1 desc, 2 desc)
                           where  rownum<2'
                           into   bdat_,
                                  IR_;
      exception when no_data_found then
        bdat_ := null;
        IR_   := null;
      end;

      fl_ := 0;
      op_ := 0;

--    определяем импортили уже счёт или нет (выборка из S6_MIGRNLS)

      begin
        select nls_bars
        into   newnls_
        from   S6_MIGRNLS
        where  nls_fil=nls_ and
               kv=kv_       and
               filial=p_filial;
      exception when no_data_found then
        newnls_ := null;
      end;

--    здесь вычисляем newnls_ на основании информации из таблиц
--    S6_S5_SHABLON, S6_S5_DEPO, S6_S5_GROUP_C, S6_S5_FILIAL

      if newnls_ is null then -- будем открывать новый счёт по маске

--      1. определяем FF_, FFF_, BRANCH_ из S6_S5_FILIAL

        begin
          execute immediate 'select trim(branch)                 ,
                                    substr(''0000''||trim(ff),-4),
                                    substr(''000''||trim(ff),-3) ,
                                    substr(''00''||trim(ff),-2)
                             from   S6_S5_FILIAL
                             where  kfil='||to_char(kfil_)||' and
                                    rownum<2'
                             into   branch_,
                                    ffff_  ,
                                    fff_   ,
                                    ff_;
        exception when no_data_found then
          ffff_ := substr(branch_,-5,4);
          fff_  := substr(branch_,-4,3);
          ff_   := substr(branch_,-3,2);
        end;

--      2. ищем счёт в S6_S5_DEPO, если нашли - newnls_ := vkrzn(KOTL) и ВСЁ

        begin
          execute immediate 'select kotl,
                                    ob22,
                                    branch
                             from   S6_S5_DEPO
                             where  trim(nls)='''||nls_||'''           and
                                    i_va='||to_char(kv_)||'            and
                                    substr(kotl,1,4) not in (''1001'',
                                                             ''1101'',
                                                             ''1011'') and
                                    rownum<2'
                             into   newnls_,
                                    OB22_  ,
                                    branch_;
          nms_    := 'Котловой счёт '||substr(nls_,1,4)||' ОБ22 '||OB22_;
          newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);
          goto BCE;
        exception when OTHERS then
          null;
        end;

--      3. иначе - ищем балансовый в S6_S5_SHABLON, если есть, то берём
--         соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_S5_SHABLON
                             where  ((length(trim(BALS))=4 and
                                      substr('''||nls_||''',1,4)=trim(BALS)) or
                                     (length(trim(BALS))=3 and
                                      substr('''||nls_||''',1,3)=trim(BALS)) or
                                     (length(trim(BALS))=2 and
                                      substr('''||nls_||''',1,2)=trim(BALS)) or
                                     (length(trim(BALS))=1 and
                                      substr('''||nls_||''',1,1)=trim(BALS))) and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      4. иначе - ищем характеристку контрагента счёта в S6_S5_GROUP_C,
--         если есть, то берём соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_S5_GROUP_C
                             where  group_c='''||group_c_||''' and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      5. иначе - добавляем к счёту арифметически 1 (цикл) и определяем
--         первый попавший счёт, его и присваиваем в newnls_

        newnls_ := nls_;

        if substr(newnls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(newnls_,1,3)||'2'||substr(newnls_,5);
        end if;

        newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);

        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

        goto BCE; -- cчёт определен!

<<MASKA>> null; -- здесь разобрать маску СБЕРБАНКА, выродить шаблон в
                -- модификаторах БАРСа и определить счёт по ентому шаблону!

--      BBBB          - балансовый счёт    (В могут быть случайно русские)
--      GGGGGG, GGGGG - RNK
--      HHH, HH или H - номер по порядку   (Н могут быть случайно русские)
--      K             - контрольный разряд (К могут быть случайно русские)
--      FFF или FF    - код отделения
--      AAA или AA    - код ОБ22           (А могут быть случайно русские)

        newnls_ := replace(upper(newnls_),'В','B');
        newnls_ := replace(upper(newnls_),'К','K');
        newnls_ := replace(upper(newnls_),'А','A');
        newnls_ := replace(upper(newnls_),'Н','H');
        newnls_ := replace(upper(newnls_),'R',substr(group_c_,-1));

        OB22_   := replace(OB22_,'А','A');
        OB22_   := replace(OB22_,'В','B');
        OB22_   := replace(OB22_,'С','C');
        OB22_   := replace(OB22_,'Н','H');
        OB22_   := replace(OB22_,'К','K');
        OB22_   := replace(OB22_,'М','M');
        OB22_   := replace(OB22_,'Е','E');
        OB22_   := replace(OB22_,'Х','X');
        OB22_   := replace(OB22_,'Р','P');
        OB22_   := replace(OB22_,'О','O');
        OB22_   := replace(OB22_,'Т','T');

        OB22aaa_:= (case when upper(substr(nvl(OB22_,'00'),1,1))>'9' then
                      to_char(ascii(upper(substr(nvl(OB22_,'00'),1,1)))-55)||
                      substr(nvl(OB22_,'00'),-1)
                    else
                      '0'||to_char(substr(nvl(OB22_,'00'),1,1))||
                      substr(nvl(OB22_,'00'),-1)
                    end);
        OB22aa_ := substr(OB22aaa_,-2);

        newnls_ := replace(upper(newnls_),'BBBB',substr(nls_,1,4));
        newnls_ := replace(upper(newnls_),'G','R');
        newnls_ := replace(upper(newnls_),'H','№');
        newnls_ := replace(upper(newnls_),'K','_');
        newnls_ := replace(upper(newnls_),'FFFF',ffff_);
        newnls_ := replace(upper(newnls_),'FFF',fff_);
        newnls_ := replace(upper(newnls_),'FF',ff_);
        newnls_ := replace(upper(newnls_),'AAA',OB22aaa_);
        newnls_ := replace(upper(newnls_),'AA',OB22aa_);

        if substr(nls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(nls_,1,3)||'2'||substr(newnls_,5);
        end if;

        toroot;
        delete
        from   nlsmask
        where  maskid='TemporaryM';

        insert
        into   nlsmask (MASKID,
                        DESCR ,
                        MASK)
                values ('TemporaryM'                           ,
                        'Временная маска для шаблона счетов СБ',
                        newnls_);
        commit;

--      получаем РЕАЛЬНЫЙ номер счёта

        tokf;
        newnls_ := f_newnls2(null               ,
                             'TemporaryM'       ,
                             substr(newnls_,1,4),
                             rnk_               ,
                             null               ,
                             kv_);

--      нижний блок добавлен, чтобы не было одинаковых счетов, вычисленных по
--      маске (f_newnls2 может таких наделать, если маска счёта без номера №,
--      или с номером №, но все счета с номером № уже были открыты ранее)
--      в этом случае к вычисленному по маске счёту в цикле арифметически
--      добавляется 1 до тех пор, пока не будет найден нужный свободный счёт

        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

<<BCE>> null;

      end if;

--    если информация по счёту NLS/KV СКАРБа5 есть в accountsw, то это значит,
--    что соответствующий счёт в БАРСе уже открыт - меняем только параметры
--    счёта
--    иначе выполняем процедуру определения НОВОГО счёта и открываем его
--    !!! если счёт определяется из маски, то строго берётся "масочный" счёт,
--    и если он уже открыт, то меняем только параметры этого счёта
--    !! иначе так: подменяем контрольный разряд и смотрим, если счёт есть, то
--    делаем арифметически +1 к счёту и пробуем этот счёт (и так до упора)

      bc.subst_branch(branch_); -- устанавливаем бранч для открываемого счёта

--    здесь имеем newnls_ и kv_ для открытия НОВОГО счёта или изменения старого

      begin
        select acc
        into   accR_
        from   accounts
        where  nls=newnls_ and
               kv=kv_;
      exception when no_data_found then
        accR_ := null;
                when OTHERS then
        bars_audit.error('open_accountS5f - err(1): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                          'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                          'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                          'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                          'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end;

      if accR_ is null then
        ndz_ := null;
        --1. OP_REG_ex
        begin

          tip_ := 'ODB';

          if    substr(newnls_,1,3)='100' and substr(newnls_,1,4)<>'1007' then
            if kv_=980 then
              tip_ := 'KAS';
            else
              tip_ := 'KAV';
            end if;
          elsif substr(newnls_,1,3) in ('604','603','602') and kv_=980 then
              tip_ := 'SD ';
          elsif substr(newnls_,1,4) in ('2625','2605','2655') then
              tip_ := 'PK1';
          end if;

          if trunc(sysdate)<p_datx then
            begin
              insert
              into   fdat (fdat)
                   values (p_datx-4);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx-4);
          else
            begin
              insert
              into   fdat (fdat)
                   values (p_datx);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx);
          end if;

          gl.amfo := MFOg_;

          OP_REG_exfl(99,       -- mod_       INTEGER,   -- Opening mode :
                      null   ,  -- p1_        INTEGER,   -- 1st Par      :
                      0      ,  -- p2_        INTEGER,   -- 2nd Par      :
                      0      ,  -- p3_        INTEGER,   -- 3rd Par (Grp):
                      ndz_   ,  -- p4_ IN OUT INTEGER,   -- 4th Par      :
                      rnk_   ,  -- rnk_       INTEGER,   -- Customer number
                      newnls_,  -- p_nls_     VARCHAR2,  -- Account  number
                      kv_    ,  -- kv_        SMALLINT,  -- Currency code
                      nms_   ,  -- nms_       VARCHAR2,  -- Account name
                      tip_   ,  -- tip_       CHAR,      -- Account type
                      isp_   ,  -- isp_       SMALLINT,
                      accR_  ,  -- accR_  OUT INTEGER,
                      '1'    ,  -- nbsnull_   VARCHAR2 DEFAULT '1',
                      null   ,  -- pap_       NUMBER   DEFAULT NULL,
                      vid_   ,  -- vid_       NUMBER   DEFAULT NULL,
                      pos_   ,  -- pos_       NUMBER   DEFAULT NULL,
                      null   ,  -- sec_       NUMBER   DEFAULT NULL,
                      null   ,  -- seci_      NUMBER   DEFAULT NULL,
                      null   ,  -- seco_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkd_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkk_      NUMBER   DEFAULT NULL,
          /* lim_? */ null   ,  -- lim_       NUMBER   DEFAULT NULL,
                      null   ,  -- ostx_      VARCHAR2 DEFAULT NULL,
                      nlsalt_,  -- nlsalt_    VARCHAR2 DEFAULT NULL,
                      branch_,  -- tobo_      VARCHAR2 DEFAULT NULL,
                      null   ,  -- accc_      NUMBER   DEFAULT NULL,
                      0);
          op_ := 1;
          begin
            update accounts
            set    dapp=dapp_
            where  acc=accR_;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountS5f - err(2): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
--          fl_ := 1;
          end;
          begin
            update ree_tmp
            set    ot=6
            where  nls=newnls_ and
                   kv=kv_      and
                   ot=1        and
                   fn_o is null;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountS5f - err(9): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
          end;
--        l_ins_ := l_ins_+1;
        exception when others then
          bars_audit.error('open_accountS5f - err(3): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                            'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                            'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                            'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                            'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
          p_code := p_code+1;
          fl_ := 1;
        end;
      end if;

      if fl_=0 then
        select count(*)
        into   flS6_
        from   accountsw
        where  acc=accR_ and
               tag='IS6S5';
        if flS6_=0 then
          select count(1)
          into   ugosaldoa_
          from   saldoa
          where  acc=accr_;

          if ugosaldoa_=0 then
            begin
              insert
              into   saldoa (acc ,
                             fdat,
                             pdat,
                             ostf,
                             dos ,
                             kos)
                     values (accR_   ,
                             gl.bdate,
                             dapp_   ,
                             0       ,
                             0       ,
                             0);
            EXCEPTION WHEN OTHERS THEN
              null;
            end;
          end if;
        else
          update accounts
          set    daos=daos_
          where  acc=accR_;
        end if;

        if op_=1 then
          begin
            insert
            into   accountsw (acc,
                              tag,
                              value)
                      values (accR_  ,
                              'IS6S5',
                              'Imported from SKARB5f');
          exception when others then
            bars_audit.error('open_accountS5f - err(4): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --2. INSERT or UPDATE SPECPARAM_INT

        if fl_=0 then
          begin
            insert
            into   SPECPARAM_INT (acc    ,
                                  p080   ,
                                  ob22   ,
                                  mfo    ,
                                  r020_fa,
                                  ob88)
                          values (accR_   ,
                                  P080_   ,
                                  OB22_   ,
                                  MFO_    ,
                                  R020_FA_,
                                  OB88_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM_INT
              set    p080=P080_      ,
                     ob22=OB22_      ,
                     mfo=MFO_        ,
                     r020_fa=R020_FA_,
                     ob88=OB88_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountS5f - err(7u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountS5f - err(7): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --3. INSERT or UPDATE SPECPARAM

        if fl_=0 then
          begin
            insert
            into   SPECPARAM (acc ,
                              R011,
                              R013,
                              S240,
                              S080,
                              S180,
                              S190,
                              S181,
                              S260,
                              S200,
                              S120,
                              S031,
                              S182,
--                            S130,
--                            S090,
                              NKD)
                      values (accR_,
                              R011_,
                              R013_,
                              S240_,
                              S080_,
                              S180_,
                              S190_,
                              S181_,
                              S260_,
                              S200_,
                              S120_,
                              S031_,
                              S182_,
--                            S130_,
--                            S090_,
                              NKD_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM
              set    R011=R011_,
                     R013=R013_,
                     S240=S240_,
                     S080=S080_,
                     S180=S180_,
                     S190=S190_,
                     S181=S181_,
                     S260=S260_,
                     S200=S200_,
                     S120=S120_,
                     S031=S031_,
                     S182=S182_,
--                   S130=S130_,
--                   S090=S090_,
                     NKD=NKD_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountS5f - err(8u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountS5f - err(8): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        if substr(nls_,1,1) in ('2','3','9') then

--        Процентные карточки

          if fl_=0 then

            if IR_ is not null /* and ACRA_ is not null */ then

              if pap_=1 then
                 id_ := 0;
              else
                 id_ := 1;
              end if;

--            4. INSERT or UPDATE INT_ACCN

              begin
                insert
                into   INT_ACCN (ACC    ,  -- accR_
                                 ID     ,  -- id_
                                 METR   ,  -- 0
--                               BASEM  ,
                                 BASEY  ,  -- 0
                                 FREQ   ,  -- 1
--                               STP_DAT,
                                 ACR_DAT,  -- bankdate
--                               APL_DAT,  -- bankdate-1
--                               TT     ,
                                 ACRA   ,  -- ???
                                 ACRB
--                               S      ,
--                               TTB    ,
--                               KVB    ,
--                               NLSB   ,
--                               MFOB   ,
--                               NAMB   ,
--                               NAZN   ,
--                               IO     ,
--                               BRANCH    -- branch_
                                )
                         values (accR_     ,
                                 id_       ,
                                 0         ,
--                               null      ,
                                 0         ,
                                 1         ,
--                               null      ,
                                 bankdate  ,
--                               bankdate-1,
--                               null      ,
                                 accR_     ,  -- ACRA_ ???
                                 null         -- ACRB_ ???
--                               null      ,
--                               null      ,
--                               null      ,
--                               null      ,
--                               null      ,
--                               null      ,
--                               null      ,
--                               null      ,
--                               branch_
                                );
              EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                update INT_ACCN
                set    METR   =0         ,
--                     BASEM  =null      ,
                       BASEY  =0         ,
                       FREQ   =1         ,
--                     STP_DAT=null      ,
                       ACR_DAT=bankdate  ,
--                     APL_DAT=bankdate-1,
--                     TT     =null      ,
                       ACRA   =accr_     ,  -- ACRA_ ???
                       ACRB   =null         -- ACRB_ ???
--                     S      =null      ,
--                     TTB    =null      ,
--                     KVB    =null      ,
--                     NLSB   =null      ,
--                     MFOB   =null      ,
--                     NAMB   =null      ,
--                     NAZN   =null      ,
--                     IO     =null      ,
--                     BRANCH =branch_
                where  acc=accR_ and
                       id=id_;
                        WHEN OTHERS THEN
                bars_audit.error('open_accountS5f - err(6): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                  'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                  'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                  'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                  'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                p_code := p_code+1;
                fl_ := 1;
              end;

--            5. INSERT or UPDATE INT_RATN

              if fl_=0 then
                begin
                  insert
                  into   INT_RATN (acc   ,
                                   id    ,
                                   BDAT  ,
                                   IR    ,
                                   BR    ,
                                   OP    ,
                                   IDU --,
--                                 BRANCH
                                  )
                           values (accR_,
                                   id_  ,
                                   bdat_,
                                   IR_  ,
                                   null ,
                                   null ,
                                   1  --,
--                                 branch_
                                  );
                EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                  update INT_RATN
                  set    IR=IR_  ,
                         BR=null ,
                         OP=null ,
                         IDU=1 --,
--                       BRANCH=branch_
                  where  acc=accR_ and
                         id=id_    and
                         BDAT=bdat_;
                          WHEN OTHERS THEN
                  bars_audit.error('open_accountS5f - err(5):'||sqlerrm||', '||Chr(13)||Chr(10)||
                                                   'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                   'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                   'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                   'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
                  p_code := p_code+1;
                  fl_ := 1;
                end;
              end if;
            end if;
          end if;
        end if;
      end if;

<<kakaxer>> null;

      bc.set_context;

      if fl_=0 then
        update S6_MIGRNLS
        set    NLS_BARS=newnls_
        where  NLS_FIL=nls_ and
               KV=kv_       and
               FILIAL=p_filial;
        if sql%rowcount=0 then
          insert
          into S6_MIGRNLS (NLS_FIL ,
                           KV      ,
                           NLS_BARS,
                           FILIAL)
                   values (nls_   ,
                           kv_    ,
                           newnls_,
                           p_filial);
        end if;
        commit;
        p_open := p_open+1;
      else
        rollback;
      end if;

    end loop;
    close cur_;

    dbms_application_info.set_client_info(' ');

    begin
      execute immediate 'alter trigger TIU_ACCOUNT67 enable';
    exception when OTHERS then
      null;
    end;

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 enable';
    exception when OTHERS then
      null;
    end;

    gl.frcvr := 0;

    bars_alerter(1);

--  сбор статистики по таблицам счетов (ACCOUNTS и связанные)

--  BEGIN
--    DBMS_JOB.SUBMIT (job       => Job_                    ,
--                     what      => 'migraSK.tableAC_stats;',
--                     next_date => sysdate                 ,
--                     interval  => null                    ,
--                     no_parse  => FALSE);
--  exception when others then
--    bars_audit.error('open_accountS5f - err(J): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
--  END;

  end open_accountS5f;

--

  procedure open_accountS6f (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    fl_          number;
    type         cur is ref cursor;
    cur_         cur;
    sql_         varchar2(32000);
    accR_        number;
    ACRA_        number;
    ACRB_        number;
    ACRdb_       number;
    ACRkr_       number;
    ndz_         number;
    id_          number;
    acc_3800_    number;
    acc_3801_    number;
    acc_6204_    number;
    ostc_3800_   number;
    ostc_3801_   number;
    ispg_        varchar(32);
    kvv_         accounts.kv%type;
    NLS_3800_    accounts.nls%type;
    NLS_3801_    accounts.nls%type;

    rnk_         customer.rnk%type;
    DAPP_        date;
    daos_        date;
    dazs_        date;
    kv_          accounts.kv%type;
    isp_         number;
    nbs_         char(4);
    tip_         char(3);
    vid_         number;
    ostc_        number;
    ostq_        number;
    lim_         number;
    nms_         varchar2(70);
    nls_         accounts.nls%type;
    nlsalt_      accounts.nlsalt%type;
    branch_      accounts.branch%type;
    OB22_        SPECPARAM_INT.ob22%type;
    OB88_        SPECPARAM_INT.ob88%type;
    P080_        SPECPARAM_INT.P080%type;
    MFO_         SPECPARAM_INT.MFO%type;
    R020_FA_     SPECPARAM_INT.R020_FA%type;
    R011_        SPECPARAM.R011%type;
    R013_        SPECPARAM.R013%type;
    S080_        SPECPARAM.S080%type;
    S180_        SPECPARAM.S180%type;
    S190_        SPECPARAM.S190%type;
    S181_        SPECPARAM.S181%type;
    S240_        SPECPARAM.S240%type;
    S260_        SPECPARAM.S260%type;
    S200_        SPECPARAM.S200%type;
    S120_        SPECPARAM.S120%type;
    S031_        SPECPARAM.S031%type;
    S182_        SPECPARAM.S182%type;
--  S130_        SPECPARAM.S130%type;
--  S090_        date;
    NKD_         SPECPARAM.NKD%type;
    IR_          int_ratn.ir%type;
    bdat_        int_ratn.bdat%type;
    pap_         ps.pap%type;
    serv_db_s_   accounts.nls%type;
    serv_kr_s_   accounts.nls%type;
    serv_i_va_   accounts.kv%type;
    NLSs6_       varchar2(15);
    I_VAs6_      number;
    PercenRate_  number;
    flS6_        number;
--  open_        number;
    s7_          number;
    s8_          number;
    s9_          number;
--  l_ins_       number;
    len_S080_    varchar2(4);
    mrnk_        varchar2(32);

    pos_         number;
    kfil_        number;
    newnls_      accounts.nls%type;
    nn_          int;
    ffff_        char(4);
    fff_         char(3);
    ff_          char(2);
    i_           int;
    group_c_     varchar2(2);
    OB22aaa_     varchar2(3);
    OB22aa_      varchar2(3);

    fi3_         char(3);
    ugosaldoa_   int;
    op_          int;

    Job_         number;
    MFOg_        varchar2(12);

  begin

    bars_alerter(0);

    gl.frcvr := 1;

    p_open := 0;
    p_code := 0;

    MFOg_ := f_ourmfo_g;

    execute immediate 'select substr(min(branch),-4,3)
                       from   s6_s6_filial'
                       into   fi3_;

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=MFOg_                              and
           logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=MFOg_ and
             logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%';
      if ispg_ is null then
        select to_char(min(id))
        into   ispg_
        from   staff$base
        where  bc.extract_mfo(branch)=MFOg_ and
               tabn is not null;
        if ispg_ is null then
          select to_char(min(id))
          into   ispg_
          from   staff$base
          where  bc.extract_mfo(branch)=MFOg_;
        end if;
      end if;
    end if;

    select to_char(min(c.rnk))
    into   mrnk_
    from   customer  c,
           customerw w
    where  c.date_off is null              and
           c.notes='Imported from SKARB6f' and
           w.rnk=c.rnk                     and
           substr(w.value,-6)=p_filial;

    if mrnk_ is null then
      select min(rnk)
      into   mrnk_
      from   customer
      where  date_off is null and
             notes='Imported from SKARB6';
    end if;

    select to_char(data_length)
    into   len_S080_
    from   user_tab_columns
    where  table_name='SPECPARAM' and
           column_name='S080';

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 disable';
    exception when OTHERS then
      null;
    end;

    begin
      execute immediate 'alter trigger TIU_ACCOUNT67 disable';
    exception when OTHERS then
      null;
    end;

    sql_:='select distinct  /* to ACCOUNTS */
                  nvl(cw.rnk,'||mrnk_||')                                                                                                                rnk    ,
     /* s.DAPP */ null                                                                                                                                   DAPP   ,
                  s.DCL                                                                                                                                  dazs   ,
                  to_date('''||p_datx||''')-4                                                                                                            daos   ,
                  s.I_VA                                                                                                                                 kv     ,
                  nvl(u.UBARS,'||ispg_||')                                                                                                               isp    ,
--                substr(''0000''||to_char(s.BALS),-4)                                                                                                   nbs    ,
                  substr(s.NLS,1,4)                                                                                                                      nbs    ,
--                decode(s.ps,98,2,1)                                                                                                                    pos    ,
                  decode(s.ps,98,decode(substr(s.NLS,1,4),''9809'',2,''9910'',2,1),1)                                                                    pos    ,
                  0                                                                                                                                      ostc   ,
                  0                                                                                                                                      ostq   ,
                  s.LIMIT*100                                                                                                                            lim    ,
--                s.type                                                                                                                                 vid    ,
                  (case when substr(s.NLS,1,2)=''25''                       then
                     11
                        when substr(s.NLS,1,4) in (''2610'',
                                                   ''2612'',
                                                   ''2615'',
                                                   ''2630'',
                                                   ''2635'',
                                                   ''2651'',
                                                   ''2652'',
                                                   ''1510'',
                                                   ''1511'',
                                                   ''1512'',
                                                   ''1515'',
                                                   ''1610'',
                                                   ''1611'',
                                                   ''1612'',
                                                   ''1615'')                then
                     4
                        when substr(s.NLS,1,4) in (''1500'',
                                                   ''1600'')                then
                     10
                        when substr(s.NLS,1,2) in (''20'',
                                                   ''21'',
                                                   ''22'',
                                                   ''27'')                  then
                     6
                        when substr(s.NLS,1,4)=''9802''                     then
                     5
                        when substr(s.NLS,1,4) in (''4310'',
                                                   ''4430'',
                                                   ''4432'',
                                                   ''4438'',
                                                   ''4439'',
                                                   ''4530'',
                                                   ''4532'',
                                                   ''4538'',
                                                   ''4539'')                then
                     8
                        when substr(s.NLS,1,4) in (select nbs from dpa_nbs) then
                     3
                   else
                     0
                   end)                                                                                                                                  vid    ,
                  migraSK.valid_symbol(trim(substr(nvl(s.NK,''не определено''),1,70)))                                                                   nms    ,
                  trim(substr(s.NLS,1,4)||s.KSS||substr(s.NLS,5))                                                                                        nls    ,
                  trim(substr(s.NLS_ALT,1,14))                                                                                                           nlsalt ,
                  nvl(f.branch,''/''||'''||MFOg_||'''||''/000000/'')                                                                                     branch ,
                  /* to SPECPARAM_INT */
                  substr(''00''||trim(p.OB22),-2)                                                                                                        OB22   ,
                  p.OB88                                                                                                                                 OB88   ,
                  p.P080                                                                                                                                 P080   ,
                  to_char(p.MFO)                                                                                                                         MFO    ,
                  p.R020_FA                                                                                                                              R020_FA,
                  /* to SPECPARAM */
                  trim(p.R011)                                                                                                                           R011   ,
                  trim(p.R013)                                                                                                                           R013   ,
                  decode(upper(trim(p.S240)),''С'',''C'',''А'',''A'',''В'',''B'',''Е'',''E'',''Н'',''H'',''8'',''B'',''9'',''C'',upper(trim(p.S240)))    S240   ,
                  substr(to_char(nullif(co.C_RISK,0)),1,'||len_S080_||')                                                                                 S080   ,
                  decode(upper(trim(co.TERM)),''С'',''C'',''А'',''A'',''В'',''B'',''Е'',''E'',''Н'',''H'',''8'',''B'',''9'',''C'',upper(trim(co.TERM)))  S180   ,
                  trim(co.PROLONG)                                                                                                                       S190   ,
                  to_char(t.S181)                                                                                                                        S181   ,
                  t.S260                                                                                                                                 S260   ,
                  to_char(co.SOURCE)                                                                                                                     S200   ,
                  to_char(co.EMIS)                                                                                                                       S120   ,
                  to_char(co.V_MAIN)                                                                                                                     S031   ,
                  to_char(mod(co.T_KR,10))                                                                                                               S182   ,
                  migraSK.valid_symbol(trim(substr(co.IDCONTRACT,1,40)))                                                                                 NKD    ,
                  /* to INT_RATN */
                  ps.pap                                                                                                                                 pap    ,
                  trim(s.NLS)                                                                                                                            NLSs6  ,
                  s.I_VA                                                                                                                                 I_VAs6 ,
                  nvl(s.depart,0)                                                                                                                        kfil   ,
                  to_char(nvl(c.GROUP_C,0))                                                                                                              group_c
           from   customerw         cw,
                  S6_S6_SALDO       s ,
                  S6_S6_CLIENTS     c ,
                  S6_S6_CONTRACT    co,
                  S6_S6_TARGET      t ,
                  S6_S6_P__SALDO    p ,
                  S6_S6_FILIAL      f ,
                --branch_parameters bp,
                  USERS_SKARB6_BARS u ,
                  ps
           where  cw.tag(+)=''RS6S6''                                                                                                    and
                  to_number(cw.value(+))=c.IDCLIENT*10000000+c.GROUP_C*1000000+'||p_filial||'                                            and
                  c.IDCLIENT=co.IDCLIENT                                                                                                 and
                  c.GROUP_C=co.GROUP_C                                                                                                   and
                  s.BIC='||p_filial||'                                                                                                   and
                  c.BIC=co.BIC                                                                                                           and
                  s.BIC=co.BIC                                                                                                           and
                  upper(rtrim(co.IDCONTRACT))=upper(rtrim(s.IDCONTRACT))                                                                 and
                  s.DCL is null                                                                                                          and
                  f.kfil(+)=nvl(s.depart,0)                                                                                              and
                  s.ISP_OWNER=u.USKARB6(+)                                                                                               and
                  u.filial(+)='''||p_filial||'''                                                                                         and
                  co.TARGET=t.ID(+)                                                                                                      and
                  trim(s.NLS)=trim(p.NLS(+))                                                                                             and
                  s.I_VA=p.I_VA(+)                                                                                                       and
--                mod(nvl(s.GROUP_U,0),10)=mod(nvl(p.GROUP_U(+),0),10)       and
--                ((mod(nvl(s.GROUP_U,0),10)=0 and substr(s.NLS,1,1)<>''8'') or
--                 (mod(nvl(s.GROUP_U,0),10) in (1,2) and substr(s.NLS,1,2)=''89''))                                                     and
--                mod(nvl(nullif(s.GROUP_U,'||p_filial||'),0),100)=mod(nvl(nullif(p.GROUP_U(+),'||p_filial||'),0),100) and
--                ((mod(nvl(nullif(s.GROUP_U,'||p_filial||'),0),100) in (0,9,24,30) and substr(s.NLS,1,1)<>''8'')      or
--                 (mod(nvl(nullif(s.GROUP_U,'||p_filial||'),0),100) in (1,2,10,11,25,26,31,32) and substr(s.NLS,1,2)=''89''))           and
                  to_number(replace(to_char(s.GROUP_U),'''||p_filial||'''))=to_number(replace(to_char(p.GROUP_U(+)),'''||p_filial||''')) and
                  ((to_number(replace(to_char(s.GROUP_U),'''||p_filial||''')) in (0,9,24,30) and substr(s.NLS,1,1)<>''8'')            or
                   (to_number(replace(to_char(s.GROUP_U),'''||p_filial||''')) in (1,2,10,11,25,26,31,32) and substr(s.NLS,1,2)=''89''))  and
--                substr(''0000''||to_char(s.BALS),-4)=ps.nbs(+)
                  substr(s.NLS,1,4)=ps.nbs(+)
--         order  by substr(''0000''||to_char(s.BALS),-4) desc
           order  by substr(s.NLS,1,4) desc';

    open cur_ for sql_;

    LOOP

      fetch cur_ into rnk_    ,
                      DAPP_   ,
                      dazs_   ,
                      daos_   ,
                      kv_     ,
                      isp_    ,
                      nbs_    ,
                      pos_    ,
                      ostc_   ,
                      ostq_   ,
                      lim_    ,
                      vid_    ,
                      nms_    ,
                      nls_    ,
                      nlsalt_ ,
                      branch_ ,
                      OB22_   ,
                      OB88_   ,
                      P080_   ,
                      MFO_    ,
                      R020_FA_,
                      R011_   ,
                      R013_   ,
                      S240_   ,
                      S080_   ,
                      S180_   ,
                      S190_   ,
                      S181_   ,
                      S260_   ,
                      S200_   ,
                      S120_   ,
                      S031_   ,
                      S182_   ,
                      NKD_    ,
                      pap_    ,
                      NLSs6_  ,
                      I_VAs6_ ,
                      kfil_   ,
                      group_c_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        execute immediate '
                select bdat      ,
                       PERCENRATE,
                       serv_db_s ,
                       serv_kr_s ,
                       serv_i_va
                from   (select nvl(DA,bankdate)             bdat     ,
                               PERCENRATE                            ,
                               trim(substr(serv_db_s,1,14)) serv_db_s,
                               trim(substr(serv_kr_s,1,14)) serv_kr_s,
                               serv_i_va                    serv_i_va
                        from   S6_S6_CHARGEPR
                        where  trim(NLS)='''||NLSs6_||''' and
                               I_VA='||I_VAs6_||'
                        order  by 1 desc, 2 desc)
                where  rownum<2'
                into   bdat_      ,
                       PercenRate_,
                       serv_db_s_ ,
                       serv_kr_s_ ,
                       serv_i_va_;

        execute immediate '
                select p.PERCEN
                from   S6_S6_HIST_PR p
                where  p.PERCENRATE='||PercenRate_||' and
                       p.DA=(select max(DA)
                             from   S6_S6_HIST_PR
                             where  PERCENRATE=p.PERCENRATE)'
                into   IR_;
      exception when no_data_found then
        bdat_      := null;
        serv_db_s_ := null;
        serv_kr_s_ := null;
        serv_i_va_ := null;
        IR_        := null;
      end;

      fl_ := 0;
      op_ := 0;

--    определяем импортили уже счёт или нет (выборка из S6_MIGRNLS)

      begin
        select nls_bars
        into   newnls_
        from   S6_MIGRNLS
        where  nls_fil=nls_ and
               kv=kv_       and
               filial=p_filial;
      exception when no_data_found then
        newnls_ := null;
      end;

--    здесь вычисляем newnls_ на основании информации из таблиц
--    S6_S6_SHABLON, S6_S6_DEPO, S6_S6_GROUP_C, S6_S6_FILIAL

      if newnls_ is null then -- будем открывать новый счёт по маске

--      1. определяем FF_, FFF_, BRANCH_ из S6_S6_FILIAL

        begin
          execute immediate 'select trim(branch)                 ,
                                    substr(''0000''||trim(ff),-4),
                                    substr(''000''||trim(ff),-3) ,
                                    substr(''00''||trim(ff),-2)
                             from   S6_S6_FILIAL
                             where  kfil='||to_char(kfil_)||' and
                                    rownum<2'
                             into   branch_,
                                    ffff_  ,
                                    fff_   ,
                                    ff_;
        exception when no_data_found then
          ffff_ := substr(branch_,-5,4);
          fff_  := substr(branch_,-4,3);
          ff_   := substr(branch_,-3,2);
        end;

--      2. ищем счёт в S6_S6_DEPO, если нашли - newnls_ := vkrzn(KOTL) и ВСЁ

        begin
          execute immediate 'select kotl,
                                    ob22,
                                    branch
                             from   S6_S6_DEPO
                             where  trim(nls)='''||nls_||'''           and
                                    i_va='||to_char(kv_)||'            and
                                    substr(kotl,1,4) not in (''1001'',
                                                             ''1101'',
                                                             ''1011'') and
                                    rownum<2'
                             into   newnls_,
                                    OB22_  ,
                                    branch_;
          nms_    := 'Котловой счёт '||substr(nls_,1,4)||' ОБ22 '||OB22_;
          newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);
          goto BCE;
        exception when OTHERS then
          null;
        end;

--      3. иначе - ищем балансовый в S6_S6_SHABLON, если есть, то берём
--         соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_S6_SHABLON
                             where  ((length(trim(BALS))=4 and
                                      substr('''||nls_||''',1,4)=trim(BALS)) or
                                     (length(trim(BALS))=3 and
                                      substr('''||nls_||''',1,3)=trim(BALS)) or
                                     (length(trim(BALS))=2 and
                                      substr('''||nls_||''',1,2)=trim(BALS)) or
                                     (length(trim(BALS))=1 and
                                      substr('''||nls_||''',1,1)=trim(BALS))) and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      4. иначе - ищем характеристку контрагента счёта в S6_S6_GROUP_C,
--         если есть, то берём соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_S6_GROUP_C
                             where  group_c='''||group_c_||''' and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      5. иначе - добавляем к счёту арифметически 1 (цикл) и определяем
--         первый попавший счёт, его и присваиваем в newnls_

        newnls_ := nls_;

        if substr(newnls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(newnls_,1,3)||'2'||substr(newnls_,5);
        end if;

        newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);

        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

        goto BCE; -- cчёт определен!

<<MASKA>> null; -- здесь разобрать маску СБЕРБАНКА, выродить шаблон в
                -- модификаторах БАРСа и определить счёт по ентому шаблону!

--      BBBB          - балансовый счёт    (В могут быть случайно русские)
--      GGGGGG, GGGGG - RNK
--      HHH, HH или H - номер по порядку   (Н могут быть случайно русские)
--      K             - контрольный разряд (К могут быть случайно русские)
--      FFF или FF    - код отделения
--      AAA или AA    - код ОБ22           (А могут быть случайно русские)

        newnls_ := replace(upper(newnls_),'В','B');
        newnls_ := replace(upper(newnls_),'К','K');
        newnls_ := replace(upper(newnls_),'А','A');
        newnls_ := replace(upper(newnls_),'Н','H');
        newnls_ := replace(upper(newnls_),'R',substr(group_c_,-1));

        OB22_   := replace(OB22_,'А','A');
        OB22_   := replace(OB22_,'В','B');
        OB22_   := replace(OB22_,'С','C');
        OB22_   := replace(OB22_,'Н','H');
        OB22_   := replace(OB22_,'К','K');
        OB22_   := replace(OB22_,'М','M');
        OB22_   := replace(OB22_,'Е','E');
        OB22_   := replace(OB22_,'Х','X');
        OB22_   := replace(OB22_,'Р','P');
        OB22_   := replace(OB22_,'О','O');
        OB22_   := replace(OB22_,'Т','T');

        OB22aaa_:= (case when upper(substr(nvl(OB22_,'00'),1,1))>'9' then
                      to_char(ascii(upper(substr(nvl(OB22_,'00'),1,1)))-55)||
                      substr(nvl(OB22_,'00'),-1)
                    else
                      '0'||to_char(substr(nvl(OB22_,'00'),1,1))||
                      substr(nvl(OB22_,'00'),-1)
                    end);
        OB22aa_ := substr(OB22aaa_,-2);

        newnls_ := replace(upper(newnls_),'BBBB',substr(nls_,1,4));
        newnls_ := replace(upper(newnls_),'G','R');
        newnls_ := replace(upper(newnls_),'H','№');
        newnls_ := replace(upper(newnls_),'K','_');
        newnls_ := replace(upper(newnls_),'FFFF',ffff_);
        newnls_ := replace(upper(newnls_),'FFF',fff_);
        newnls_ := replace(upper(newnls_),'FF',ff_);
        newnls_ := replace(upper(newnls_),'AAA',OB22aaa_);
        newnls_ := replace(upper(newnls_),'AA',OB22aa_);

        if substr(nls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(nls_,1,3)||'2'||substr(newnls_,5);
        end if;

        toroot;
        delete
        from   nlsmask
        where  maskid='TemporaryM';

        insert
        into   nlsmask (MASKID,
                        DESCR ,
                        MASK)
                values ('TemporaryM'                           ,
                        'Временная маска для шаблона счетов СБ',
                        newnls_);
        commit;

--      получаем РЕАЛЬНЫЙ номер счёта

        tokf;
        newnls_ := f_newnls2(null               ,
                             'TemporaryM'       ,
                             substr(newnls_,1,4),
                             rnk_               ,
                             null               ,
                             kv_);
        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

<<BCE>> null;

      end if;

      bc.subst_branch(branch_);

      begin
        select acc
        into   accR_
        from   accounts
        where  nls=newnls_ and
               kv=kv_;
      exception when no_data_found then
        accR_ := null;
                when OTHERS then
        bars_audit.error('open_accountS6f - err(1): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                          'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                          'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                          'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                          'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end;

      if accR_ is null then
        ndz_ := null;
        --1. OP_REG_ex
        begin

          tip_ := 'ODB';

          if    substr(newnls_,1,3)='100' and substr(newnls_,1,4)<>'1007' then
            if kv_=980 then
              tip_ := 'KAS';
            else
              tip_ := 'KAV';
            end if;
          elsif substr(newnls_,1,3) in ('604','603','602') and kv_=980 then
              tip_ := 'SD ';
          elsif substr(newnls_,1,4) in ('2625','2605','2655') then
              tip_ := 'PK1';
          end if;

          if trunc(sysdate)<p_datx then
            begin
              insert
              into   fdat (fdat)
                   values (p_datx-4);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx-4);
          else
            begin
              insert
              into   fdat (fdat)
                   values (p_datx);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx);
          end if;

          gl.amfo := MFOg_;

          OP_REG_exfl(99     ,  -- mod_       INTEGER,   -- Opening mode :
                      null   ,  -- p1_        INTEGER,   -- 1st Par      :
                      0      ,  -- p2_        INTEGER,   -- 2nd Par      :
                      0      ,  -- p3_        INTEGER,   -- 3rd Par (Grp):
                      ndz_   ,  -- p4_ IN OUT INTEGER,   -- 4th Par      :
                      rnk_   ,  -- rnk_       INTEGER,   -- Customer number
                      newnls_,  -- p_nls_     VARCHAR2,  -- Account  number
                      kv_    ,  -- kv_        SMALLINT,  -- Currency code
                      nms_   ,  -- nms_       VARCHAR2,  -- Account name
                      tip_   ,  -- tip_       CHAR,      -- Account type
                      isp_   ,  -- isp_       SMALLINT,
                      accR_  ,  -- accR_  OUT INTEGER,
                      '1'    ,  -- nbsnull_   VARCHAR2 DEFAULT '1',
                      null   ,  -- pap_       NUMBER   DEFAULT NULL,
          /* null */  vid_   ,  -- vid_       NUMBER   DEFAULT NULL,
          /* null */  pos_   ,  -- pos_       NUMBER   DEFAULT NULL,
                      null   ,  -- sec_       NUMBER   DEFAULT NULL,
                      null   ,  -- seci_      NUMBER   DEFAULT NULL,
                      null   ,  -- seco_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkd_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkk_      NUMBER   DEFAULT NULL,
          /* lim_ */  null   ,  -- lim_       NUMBER   DEFAULT NULL,
                      null   ,  -- ostx_      VARCHAR2 DEFAULT NULL,
                      nlsalt_,  -- nlsalt_    VARCHAR2 DEFAULT NULL,
                      branch_,  -- tobo_      VARCHAR2 DEFAULT NULL,
                      null   ,  -- accc_      NUMBER   DEFAULT NULL,
                      0);
          begin
            update accounts
            set    dapp=dapp_
            where  acc=accR_;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountS6f - err(2): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
--          fl_ := 1;
          end;
          begin
            update ree_tmp
            set    ot=6
            where  nls=newnls_ and
                   kv=kv_      and
                   ot=1        and
                   fn_o is null;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountS6f - err(9): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
          end;
--        l_ins_ := l_ins_+1;
        exception when others then
          bars_audit.error('open_accountS6f - err(3): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                            'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                            'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                            'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                            'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
          p_code := p_code+1;
          fl_ := 1;
        end;
      end if;

      if fl_=0 then
        select count(*)
        into   flS6_
        from   accountsw
        where  acc=accR_ and
               tag='IS6S6';
        if flS6_=0 then
          select count(1)
          into   ugosaldoa_
          from   saldoa
          where  acc=accr_;

          if ugosaldoa_=0 then
            begin
              insert
              into   saldoa (acc ,
                             fdat,
                             pdat,
                             ostf,
                             dos ,
                             kos)
                     values (accR_   ,
                             gl.bdate,
                             dapp_   ,
                             0       ,
                             0       ,
                             0);
            EXCEPTION WHEN others THEN
              null;
            end;
          end if;
        else
          update accounts
          set    daos=daos_
          where  acc=accR_;
        end if;

        if op_=1 then
          begin
            insert
            into   accountsw (acc,
                              tag,
                              value)
                      values (accR_  ,
                              'IS6S6',
                              'Imported from SKARB6f');
          exception when others then
            bars_audit.error('open_accountS6f - err(4): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --2. INSERT or UPDATE SPECPARAM_INT

        if fl_=0 then
          begin
            insert
            into   SPECPARAM_INT (acc    ,
                                  p080   ,
                                  ob22   ,
                                  mfo    ,
                                  r020_fa,
                                  ob88)
                          values (accR_   ,
                                  P080_   ,
                                  OB22_   ,
                                  MFO_    ,
                                  R020_FA_,
                                  OB88_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM_INT
              set    p080=P080_      ,
                     ob22=OB22_      ,
                     mfo=MFO_        ,
                     r020_fa=R020_FA_,
                     ob88=OB88_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountS6f - err(5u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountS6f - err(5): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --3. INSERT or UPDATE SPECPARAM

        if fl_=0 then
          begin
            insert
            into   SPECPARAM (acc ,
                              R011,
                              R013,
                              S240,
                              S080,
                              S180,
                              S190,
                              S181,
                              S260,
                              S200,
                              S120,
                              S031,
                              S182,
--                            S130,
                              NKD)
                      values (accR_,
                              R011_,
                              R013_,
                              S240_,
                              S080_,
                              S180_,
                              S190_,
                              S181_,
                              S260_,
                              S200_,
                              S120_,
                              S031_,
                              S182_,
--                            S130_,
                              NKD_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM
              set    R011=R011_,
                     R013=R013_,
                     S240=S240_,
                     S080=S080_,
                     S180=S180_,
                     S190=S190_,
                     S181=S181_,
                     S260=S260_,
                     S200=S200_,
                     S120=S120_,
                     S031=S031_,
                     S182=S182_,
--                   S130=S130_,
                     NKD=NKD_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountS6f - err(6u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountS6f - err(6): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        if substr(nls_,1,1) in ('2','3','9') then

--        Процентные карточки

          if fl_=0 then
            begin
              select acc
              into   ACRdb_
              from   accounts
              where  nls=serv_db_s_ and
                     kv=serv_i_va_;
            exception when no_data_found then
              ACRdb_ := null;
            end;
            begin
              select acc
              into   ACRkr_
              from   accounts
              where  nls=serv_kr_s_ and
                     kv=serv_i_va_;
            exception when no_data_found then
              ACRkr_ := null;
            end;

            if ACRdb_ is not null and ACRkr_ is not null then
              if substr(serv_db_s_,1,4)>substr(serv_kr_s_,1,4) then
                if substr(serv_kr_s_,1,4)='3800' then
                  ACRA_ := null;
                else
                  ACRA_ := ACRkr_;
                end if;
                if substr(serv_db_s_,1,4)='3800' then
                  ACRB_ := null;
                else
                  ACRB_ := ACRdb_;
                end if;
              else
                if substr(serv_db_s_,1,4)='3800' then
                  ACRA_ := null;
                else
                  ACRA_ := ACRdb_;
                end if;
                if substr(serv_kr_s_,1,4)='3800' then
                  ACRB_ := null;
                else
                  ACRB_ := ACRkr_;
                end if;
              end if;
            else
              if ACRdb_ is null and ACRkr_ is not null then
                if substr(serv_kr_s_,1,1)>'2' then
                  if substr(serv_kr_s_,1,4)='3800' then
                    ACRB_ := null;
                  else
                    ACRB_ := ACRkr_;
                  end if;
                  ACRA_ := null;
                else
                  if substr(serv_kr_s_,1,4)='3800' then
                    ACRA_ := null;
                  else
                    ACRA_ := ACRkr_;
                  end if;
                  ACRB_ := null;
                end if;
              end if;
              if ACRdb_ is not null and ACRkr_ is null then
                if substr(serv_db_s_,1,1)>'2' then
                  if substr(serv_db_s_,1,1)='3800' then
                    ACRB_ := null;
                  else
                    ACRB_ := ACRdb_;
                  end if;
                  ACRA_ := null;
                else
                  if substr(serv_db_s_,1,1)='3800' then
                    ACRA_ := null;
                  else
                    ACRA_ := ACRdb_;
                  end if;
                  ACRB_ := null;
                end if;
              end if;
              if ACRdb_ is null and ACRkr_ is null then
                ACRA_ := null;
                ACRB_ := null;
              end if;
            end if;

            if IR_ is not null /* and ACRA_ is not null */ then

              if pap_=1 then
                 id_ := 0;
              else
                 id_ := 1;
              end if;

--            4. INSERT or UPDATE INT_ACCN

              begin
                insert
