  procedure fin_deblocking6f (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type        cur is ref cursor;
    cur_        cur;
    sql_        varchar2(4000);

    NLS_        accounts.nls%type;
    I_VA_       accounts.kv%type;
    MOTIVE_     number(11);
    SUMLOCK_    number(18,2);
    DOCLOCK_    varchar2(100);
    DALOCK_     date;
    DADOCLOCK_  date;
    NLS_BARS_   accounts.nls%type;

    acc_        accounts.acc%type;
    l_rnk       number;
    branch2_    varchar2(15);
    Job_        number;
    MFOg_       varchar2(12);

  begin

    bars_alerter(0);

    p_open   := 0;
    p_code   := 0;
    branch2_ := null;

    MFOg_ := f_ourmfo_g;

--  корректировка accounts по saldoa, saldob (через задание)

    BEGIN
      DBMS_JOB.SUBMIT (job       => Job_               ,
                       what      => 'correct_accounts;',
                       next_date => sysdate            ,
                       interval  => null               ,
                       no_parse  => FALSE);
    exception when others then
      bars_audit.error('fin_deblocking6f - err(J): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
    END;

    tokf;

--  разблокировка счетов: blkk и blkd с 99 на 0

    for k in (select nls_bars,
                     nls_fil ,
                     kv
              from   S6_MIGRNLS
              where  filial=p_filial)
    loop
      update accounts
      set    blkk=0,
             blkd=0
      where  nls=k.nls_bars and
             kv=k.kv;
      p_open := p_open+1;
      if branch2_ is null then
        begin
          select substr(branch,1,15)
          into   branch2_
          from   accounts
          where  nls=k.nls_bars and
                 kv=k.kv;
        exception when no_data_found then
          null;
        end;
      end if;
    end loop;
    commit;

--  удаление МФО филиала из таблиц LKL_RRP и BANK_ACC

    tokf;
    begin
      delete
      from   lkl_rrp
      where  mfo=p_filial;
      commit;
    exception when OTHERS then
      rollback;
      bars_audit.error('fin_deblocking6f - err(1) for delete from lkl_rrp: '||sqlerrm);
    end;
    begin
      delete
      from   bank_acc
      where  mfo=p_filial;
      commit;
    exception when OTHERS then
      rollback;
      bars_audit.error('fin_deblocking6f - err(2) for delete from bank_acc: '||sqlerrm);
    end;
    toroot;

--  блокировка арестованных счетов по информации СКАРБа6(ф)

    tokf;

    sql_:='select trim(substr(trim(l.NLS),1,14)),
                  l.I_VA                        ,
                  l.MOTIVE                      ,
                  l.SUMLOCK*100                 ,
                  l.DOCLOCK                     ,
                  l.DALOCK                      ,
                  l.DADOCLOCK                   ,
                  m.NLS_BARS
           from   S6_S6_LOCKSALDO l,
                  S6_MIGRNLS      m
           where  l.DAUNLOCK is null                                     and
                  substr(m.NLS_FIL(+),1,4)||substr(m.NLS_FIL(+),6)=l.NLS and
                  m.filial(+)='''||p_filial||'''';

    open cur_ for sql_;

    loop

      fetch cur_ into NLS_      ,
                      I_VA_     ,
                      MOTIVE_   ,
                      SUMLOCK_  ,
                      DOCLOCK_  ,
                      DALOCK_   ,
                      DADOCLOCK_,
                      NLS_BARS_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      if NLS_BARS_ is null then
        goto nextrec;
      end if;
      begin
        select acc
        into   acc_
        from   accounts
        where  substr(nls,1,4)||substr(nls,6,9)=NLS_BARS_ and
               kv=I_VA_;
      exception when no_data_found then
        goto nextrec;
      end;

      if    MOTIVE_=0 or
            MOTIVE_=1 then
        update accounts
        set    blkd=1,
               blkk=1
        where  acc=acc_;
      elsif MOTIVE_=2 then
        update accounts
        set    blkd=1,
               blkk=0
        where  acc=acc_;
      elsif MOTIVE_=3 then
        update accounts
        set    blkd=1,
               blkk=0,
               lim=decode(pap,1,1,-1)*nvl(SUMLOCK_,0)
        where  acc=acc_;
      end if;

      begin
        insert
        into   acc_sob (acc ,
                        isp ,
                        fdat,
                        txt)
                values (acc_          ,
                        20094         ,
                        trunc(DALOCK_),
                        DOCLOCK_);
      exception when OTHERS then
        null;
      end;

--    добавка в accountsw
--    'LIE_DOC' , 'Арешт рахунка.Назва документа (наприклад Постанова)'
--    'LIE_DOCN', 'Арешт рахунка. Номер документа'
--    'LIE_DATE', 'Арешт рахунка. Дата документа'
--    'LIE_ATRT', 'Арешт рахунка. Орган, який видав документ'
--    'LIE_SUM' , 'Арешт рахунка. Сума обтяження(в копiйках)'
--    'LIE_VAL' , 'Арешт рахунка. Валюта обтяження(число)'

      begin
        insert
        into   accountsw (acc,
                          tag,
                          value)
                  values (acc_     ,
                          'LIE_DOC',
                          DOCLOCK_);
      exception when OTHERS then
        null;
      end;

      begin
        insert
        into   accountsw (acc,
                          tag,
                          value)
                  values (acc_      ,
                          'LIE_DATE',
                          to_char(DADOCLOCK_,'DD/MM/YYYY'));
      exception when OTHERS then
        null;
      end;

      if SUMLOCK_<>0 then
        begin
          insert
          into   accountsw (acc,
                            tag,
                            value)
                    values (acc_     ,
                            'LIE_SUM',
                          to_char(SUMLOCK_));
        exception when OTHERS then
          null;
        end;

        begin
          insert
          into   accountsw (acc,
                            tag,
                            value)
                    values (acc_     ,
                            'LIE_VAL',
                            to_char(I_VA_));
        exception when OTHERS then
          null;
        end;
      end if;

<<nextrec>> null;
    end loop;
    close cur_;

    dbms_application_info.set_client_info(' ');

    commit;

    toroot;

--  добавка в ALIEN

    execute immediate '
    delete
    from   alien
    where  (MFO,NLS,KV) in (select distinct
                                   to_char(bic),
                                   trim(nls)   ,
                                   i_va
                            from   S6_S6_EXT_SALDO
                            where  to_char(bic) in (select mfo
                                                    from   banks$base))';
    execute immediate 'insert
                       into   alien (MFO ,
                                     NLS ,
                                     KV  ,
                                     OKPO,
                                     NAME)
                              select distinct
                                     to_char(e.bic),
                                     trim(e.nls)   ,
                                     e.i_va        ,
                                     trim(e.id)    ,
                                     trim(substr(e.nk,1,70))
                              from   S6_S6_EXT_SALDO e,
                                     tabval          t
                              where  e.i_va=t.kv and
                                     to_char(e.bic) in (select mfo
                                                        from   banks$base)';
    commit;

--  обновление материализованного представления

    NERUXOMI;

--  добавка RNK и параметра с RNK для бранчей

    begin
      for c in (select branch,
                       name
                from   branch
                where  branch like '/'||MFOg_||'/%'
                order  by branch)
      loop
        begin
          bc.subst_branch(c.branch);
          begin
            select rnk
            into   l_rnk
            from   customer
            where  branch=c.branch and
                   notes='BRANCH<->RNK';
          exception when no_data_found then

            execute immediate 'select s_customer.nextval from dual' into l_rnk;

            insert
            into   customer (rnk      ,
                             tgr      ,
                             custtype ,
                             codcagent,
                             okpo     ,
                             country  ,
                             nmk      ,
                             nmkk     ,
                             notes    ,
                             ise      ,
                             fs       ,
                             oe       ,
                             ved      ,
                             sed      ,
                             k050     ,
                             branch)
                     values (l_rnk                    ,
                             1                        ,
                             1                        ,
                             1                        ,
                             f_ourokpo                ,
                             804                      ,
                             trim(substr(c.name,1,70)),
                             trim(substr(c.name,1,38)),
                             'BRANCH<->RNK'           ,
                             '12201'                  ,
                             '31'                     ,
                             '96130'                  ,
                             '65120'                  ,
                             '80  '                   ,
                             '610'                    ,
                             c.branch);
          end;

          begin
            update branch_parameters
            set    val=to_char(l_rnk)
            where  tag='RNK' and
                   branch=c.branch;

            if SQL%ROWCOUNT=0 then
             insert
             into   branch_parameters (branch,
                                       tag   ,
                                       val)
                               values (c.branch,
                                       'RNK'   ,
                                       to_char(l_rnk));
            end if;
          end;
        exception when others then
          bc.set_context;
        end;
        bc.set_context;
      end loop;
    end;

    commit;

--  гривна по всем бранчам в cur_rates$base

    migraSK.grn11tocurrates;

--  открытие счетов %% расходов

    migraSK.open_proc_drm('fin_deblocking6f - ',p_open,p_code,0,branch2_);

    bars_alerter(1);

  end fin_deblocking6f;

