      -- Перебросить остатки и закрыть старяе лиц.счета 3800 и 3801
      begin
        xZ_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_5f - err(3): Ошибка xZ_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

    end loop;

--  toroot;

    bars_alerter(1);

  end open_3800_5f;

