  begin

    bars_alerter(0);

--  l_ins_ := 0;
    p_open := 0;
    p_code := 0;

    MFOg_ := f_ourmfo_g;

    execute immediate 'select substr(min(branch),-4,3)
                       from   s6_hi_filial'
                       into   fi3_;

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=MFOg_                              and
           logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=MFOg_ and
             logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%';
      if ispg_ is null then
        select to_char(min(id))
        into   ispg_
        from   staff$base
        where  bc.extract_mfo(branch)=MFOg_ and
               tabn is not null;
        if ispg_ is null then
          select to_char(min(id))
          into   ispg_
          from   staff$base
          where  bc.extract_mfo(branch)=MFOg_;
        end if;
      end if;
    end if;

    MFO_ := null;

    sql_:='select distinct
                  TAXF     ,
                  K050     ,
                  DATEON   ,
                  DATEA    ,
                  DATET    ,
                  BDAY     ,
                  FS       ,
                  SED      ,
                  NMK      ,
                  IDCLIENT ,
                  GROUP_C  ,
                  OKPO     ,
                  COUNTRY  ,
                  CDST     ,
                  CREG     ,
                  PRINSIDER,
                  ISP      ,
                  NMKK     ,
                  OE       ,
                  CODCAGENT,
                  ISE      ,
                  SEX      ,
                  TGR      ,
                  CUSTTYPE ,
                  VED      ,
                  ND       ,
                  CRISK    ,
                  PHONE    ,
                  POSTIND  ,
                  OBLAST   ,
                  RAYON    ,
                  CITY     ,
                  STREET   ,
                  HOUSE    ,
                  KVARTIRA ,
                  TEL      ,
                  EMAIL    ,
                  SAB      ,
                  STMT     ,
                  RGADM    ,
                  BIC      ,
                  SERIA    ,
                  NOMBER   ,
                  ORGAN    ,
                  DOTE     ,
                  Adr
           from   (select distinct
                          c.K050                                                         TAXF     ,  -- Код платника податкiв                   varchar(5)
                          substr(''00''||to_char(nvl(c.K050,0)),-3)                      K050     ,  -- Показатель k050                         CHAR(3)
                          nvl(c.D_REG_SA,c.LASTDATE)                                     DATEON   ,  -- Дата реєстрацiї                         smalldatetime
                          c.D_REG_SA                                                     DATEA    ,  -- Дата реєстрацiї в адмiнiстрацiї         smalldatetime
                          c.D_REG_STI                                                    DATET    ,  -- Дата реєстрацiї в податковiй iнспекцiї  smalldatetime
                          null                                                           BDAY     ,  -- Дата народження                         smalldatetime
                          decode(substr(''00''||to_char(nvl(c.K080,0)),-2),
                                 ''99'',''ZZ'',
                                 ''00'',decode(decode(c.KIND,7,1,9,1,c.KIND),5,''10'',
                                                                             1,''31'',
                                                                             3,''31'',
                                                                               ''ZZ''),
                                 substr(''00''||to_char(nvl(c.K080,0)),-2))              FS       ,  -- Форма власностi                         tinyint
                          substr(''0''||to_char(nvl(c.K051,0)),-2)||''  ''               SED      ,  -- Форма господарювання                    char(4)
                          trim(substr(trim(c.NAME),1,70))                                NMK      ,  -- Повне найменування                      varchar(150)
                          c.CODE                                                         IDCLIENT ,  -- Код                                     int
                          0                                                              GROUP_C  ,  -- Код+                                    int
                          trim(substr(migraSK.valid_symbol(c.OKPO),1,10))                OKPO     ,  -- Iдентифiкацiйний код                    varchar(10)
                          to_number(nvl(c.K040,''804''))                                 COUNTRY  ,  -- Країна                                  smallint
                          nullif(c.C_DST,0)                                              CDST     ,  -- Податкова iнспекцiя                     tinyint
                          nullif(c.C_REG,0)                                              CREG     ,  -- Регiон                                  tinyint
                          decode(nvl(c.K060,99),21,2,22,3,23,5,30,4,c.K060)              PRINSIDER,  -- Iнсайдер                                tinyint
                          nvl(u.UBARS,'||ispg_||')                                       ISP      ,  -- Код користувача                         smallint
                          trim(substr(c.SHORTNAME,1,38))                                 NMKK     ,  -- Коротке найменування                    varchar(40)
                          substr(''00000''||to_char(c.K090),-5)                          OE       ,  -- Галузь                                  int
                          decode(c.KIND,7,1,9,1,c.KIND)                                  CODCAGENT,  -- Характеристика
                          decode(substr(''00000''||nvl(to_char(c.K070),''''),-5),
                                 ''00000'',
                                 decode(decode(c.KIND,7,1,9,1,c.KIND),5,''14410'',
                                                                      1,''12201'',
                                                                      2,''20000'',
                                                                      4,''20000'',
                                                                      6,''20000'',
                                                                        ''ZZZZZ''),
                                 substr(''00000''||nvl(to_char(c.K070),''''),-5))        ISE      ,  -- Iнстит.сектор економiки                 int
                          null                                                           SEX      ,  -- Стать                                   tinyint
                          decode(nvl(c.TIN_KEY,0),0,3,4,1,c.TIN_KEY)                     TGR      ,  -- Тип держреєстру                         tinyint
                          decode(substr(''00''||to_char(nvl(c.K051,0)),-2),''91'',3,
                                 decode(c.KIND,3,2,5,3,1))                               CUSTTYPE ,  -- Тип клiєнта                             tinyint
                          substr(''00000''||nvl(c.K110,''''),-5)                         VED      ,  -- Вид економ.дiяльностi                   int
                          to_char(c.CODE)                                                ND       ,
                          to_number(c.K015)                                              CRISK    ,
                          trim(migraSK.valid_symbol(substr(trim(c.PHONE),1,20)))         PHONE    ,  -- Телефон                                 varchar(20)
                          null                                                           POSTIND  ,  -- Поштовий iндекс                         varchar(10)
                          null                                                           OBLAST   ,  -- Область                                 varchar(40)
                          null                                                           RAYON    ,  -- Район                                   varchar(25)
                          null                                                           CITY     ,  -- Мiсто, насел.пункт                      varchar(40)
                          null                                                           STREET   ,  -- Вулиця                                  varchar(40)
                          null                                                           HOUSE    ,  -- Дiм                                     varchar(20)
                          null                                                           KVARTIRA ,  -- Квартира                                varchar(20)
                          trim(migraSK.valid_symbol(substr(trim(c.PHONE),1,20)))         TEL      ,  -- Телефон                                 varchar(20)
                          null                                                           EMAIL    ,  -- Електронна адреса                       varchar(20)
                          null                                                           SAB      ,  -- SAB
                          null                                                           STMT     ,
                          null                                                           RGADM    ,
                          null                                                           BIC      ,
                          upper(trim(substr(c.PASSPORT,1,2)))                            SERIA    ,  -- Серия паспорта
                          trim(substr(c.PASSPORT,3,7))                                   NOMBER   ,  -- Номер паспорта
                          trim(substr(c.PASSPORT,10,50))                                 ORGAN    ,  -- Кем выдан
                          null                                                           DOTE     ,  -- Когда выдан
                          trim(substr(c.ADDRESS,1,70))                                   Adr
                   from   S6_HI_BCL                              c,
--                        (select K051,
--                                substr(K050,1,3) K050
--                         from   KL_K050
--                         where  k051 is not null
--                        union all
--                         select k051,
--                                substr(K050,1,3) K050
--                         from   KL_K051
--                         where  k050 in (select k050
--                                         from   kl_k050
--                                         where  k051 is null)) k,
                          USERS_SKARB6_BARS                      u
                   where  c.KIND=u.USKARB6(+)                                             and
--                        substr(k.K050(+),1,3)=substr(''00''||to_char(nvl(c.K050,0)),-3) and
                          u.FILIAL(+)='''||p_filial||''')
           order  by IDCLIENT,GROUP_C';

    open cur_ for sql_;

    loop
      fetch cur_ into TAXF_     ,
                      K050_     ,
                      DATEON_   ,
                      DATEA_    ,
                      DATET_    ,
                      BDAY_     ,
                      FS_       ,
                      SED_      ,
                      NMK_      ,
                      IDCLIENT_ ,
                      GROUP_C_  ,
                      OKPO_     ,
                      COUNTRY_  ,
                      CDST_     ,
                      CREG_     ,
                      PRINSIDER_,
                      ISP_      ,
                      NMKK_     ,
                      OE_       ,
                      CODCAGENT_,
                      ISE_      ,
                      SEX_      ,
                      TGR_      ,
                      CUSTTYPE_ ,
                      VED_      ,
                      ND_       ,
                      CRISK_    ,
                      PHONE_    ,
                      POSTIND_  ,
                      OBLAST_   ,
                      RAYON_    ,
                      CITY_     ,
                      STREET_   ,
                      HOUSE_    ,
                      KVARTIRA_ ,
                      TEL_      ,
                      EMAIL_    ,
                      SAB_      ,
                      STMT_     ,
                      RGADM_    ,
                      BIC_      ,
                      SERIA_    ,
                      NOMBER_   ,
                      ORGAN_    ,
                      DOTE_     ,
                      Adr_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        select c.rnk
        into   rnk_
        from   customerw w,
               customer  c
        where  w.tag='RS6HI'                                                    and
               w.value=to_char(IDCLIENT_)||to_char(GROUP_C_)||to_char(p_filial) and
               w.rnk=c.rnk                                                      and
               bc.extract_mfo(c.branch)=MFOg_                                   and
               rownum<2;
      exception when no_data_found then
        RNK_ := null;
      end;

      bc.subst_mfo(MFOg_);

      begin

        kl.setCustomerAttr(RNK_                 ,  -- Rnk_  IN OUT customer.rnk%type        -- Customer number
                           CUSTTYPE_            ,  -- Custtype_    customer.custtype%type   -- Тип клиента: 1-банк, 2-юр.лицо, 3-физ.лицо
                           ND_                  ,  -- Nd_          customer.nd%type         -- № договора
                           NMK_                 ,  -- Nmk_         customer.nmk%type        -- Наименование клиента
                           null                 ,  -- Nmkv_        customer.nmkv%type       -- Наименование клиента международное
                           NMKK_                ,  -- Nmkk_        customer.nmkk%type       -- Наименование клиента краткое
                           Adr_                 ,  -- Adr_         customer.adr%type        -- Адрес клиента
                           CODCAGENT_           ,  -- Codcagent_   customer.codcagent%type  -- Характеристика
                           COUNTRY_             ,  -- Country_     customer.country%type    -- Страна
                           PRINSIDER_           ,  -- Prinsider_   customer.prinsider%type  -- Признак инсайдера
                           TGR_                 ,  -- Tgr_         customer.tgr%type        -- Тип гос.реестра
                           OKPO_                ,  -- Okpo_        customer.okpo%type       -- ОКПО
                           STMT_                ,  -- Stmt_        customer.stmt%type       -- Формат выписки
                           SAB_                 ,  -- Sab_         customer.sab%type        -- Эл.код
                           DATEON_              ,  -- DateOn_      customer.date_on%type    -- Дата регистрации
                           TAXF_                ,  -- Taxf_        customer.taxf%type       -- Налоговый код
                           CREG_                ,  -- CReg_        customer.c_reg%type      -- Код обл.НИ
                           CDST_                ,  -- CDst_        customer.c_dst%type      -- Код район.НИ
                           null                 ,  -- Adm_         customer.adm%type        -- Админ.орган
                           null                 ,  -- RgTax_       customer.rgtax%type      -- Рег номер в НИ
                           RGADM_               ,  -- RgAdm_       customer.rgadm%type      -- Рег номер в Адм.
                           DATET_               ,  -- DateT_       customer.datet%type      -- Дата рег в НИ
                           DATEA_               ,  -- DateA_       customer.datea%type      -- Дата рег. в администрации
                           ISE_                 ,  -- Ise_         customer.ise%type        -- Инст. сек. экономики
                           FS_                  ,  -- Fs_          customer.fs%type         -- Форма собственности
                           OE_                  ,  -- Oe_          customer.oe%type         -- Отрасль экономики
                           VED_                 ,  -- Ved_         customer.ved%type        -- Вид эк. деятельности
                           SED_                 ,  -- Sed_         customer.sed%type        -- Форма хозяйствования
                           'Imported from HITCf',  -- Notes_       customer.notes%type      -- Примечание
                           null                 ,  -- Notesec_     customer.notesec%type    -- Примечание для службы безопасности
                           CRISK_               ,  -- CRisk_       customer.crisk%type      -- Категория риска
                           null                 ,  -- Pincode_     customer.pincode%type    --
                           null                 ,  -- RnkP_        customer.rnkp%type       -- Рег. номер холдинга
                           null                 ,  -- Lim_         customer.lim%type        -- Лимит кассы
                           null                 ,  -- NomPDV_      customer.nompdv%type     -- № в реестре плат. ПДВ
                           '9'                  ,  -- MB_          customer.mb%type         -- Принадл. малому бизнесу
                           null                 ,  -- BC_          customer.bc%type         -- Признак НЕклиента банка
                           '/'||MFOg_||'/'      ,  -- Tobo_        customer.tobo%type       -- Код безбалансового отделения
                           ISP_                    -- Isp_         customer.isp%type        -- Менеджер клиента (ответ. исполнитель)
                          );

        begin
          kl.setCustomerEN(RNK_ ,  -- customer.rnk%type
                           ISE_ ,  -- customer.ise%type
                           FS_  ,  -- customer.fs%type
                           VED_ ,  -- customer.ved%type
                           OE_  ,  -- customer.oe%type
                           K050_,  -- customer.k050%type
                           SED_);  -- customer.sed%type
        exception when others then
          bars_audit.error('create_customerShi - err(1): '||sqlerrm      ||' - '  ||Chr(13)||Chr(10)||
                           'RNK       = '||RNK_           ||', '||Chr(13)||Chr(10)||
                           'CUSTTYPE  = '||CUSTTYPE_      ||', '||Chr(13)||Chr(10)||
                           'ND        = '||ND_            ||', '||Chr(13)||Chr(10)||
                           'NMK       = '||NMK_           ||', '||Chr(13)||Chr(10)||
                           'NMKK      = '||NMKK_          ||', '||Chr(13)||Chr(10)||
                           'CODCAGENT = '||CODCAGENT_     ||', '||Chr(13)||Chr(10)||
                           'COUNTRY   = '||COUNTRY_       ||', '||Chr(13)||Chr(10)||
                           'PRINSIDER = '||PRINSIDER_     ||', '||Chr(13)||Chr(10)||
                           'TGR       = '||TGR_           ||', '||Chr(13)||Chr(10)||
                           'OKPO      = '||OKPO_          ||', '||Chr(13)||Chr(10)||
                           'STMT      = '||STMT_          ||', '||Chr(13)||Chr(10)||
                           'SAB       = '||SAB_           ||', '||Chr(13)||Chr(10)||
                           'DATEON    = '||DATEON_        ||', '||Chr(13)||Chr(10)||
                           'TAXF      = '||TAXF_          ||', '||Chr(13)||Chr(10)||
                           'CDST      = '||CDST_          ||', '||Chr(13)||Chr(10)||
                           'CREG      = '||CREG_          ||', '||Chr(13)||Chr(10)||
                           'RGADM     = '||RGADM_         ||', '||Chr(13)||Chr(10)||
                           'DATET     = '||DATET_         ||', '||Chr(13)||Chr(10)||
                           'DATEA     = '||DATEA_         ||', '||Chr(13)||Chr(10)||
                           'ISE       = '||ISE_           ||', '||Chr(13)||Chr(10)||
                           'FS        = '||FS_            ||', '||Chr(13)||Chr(10)||
                           'OE        = '||OE_            ||', '||Chr(13)||Chr(10)||
                           'VED       = '||VED_           ||', '||Chr(13)||Chr(10)||
                           'SED       = '||SED_           ||', '||Chr(13)||Chr(10)||
                           'CRISK     = '||CRISK_         ||', '||Chr(13)||Chr(10)||
                           'ISP       = '||ISP_           ||', '||Chr(13)||Chr(10)||
                           'CLI_KOD   = '||IDCLIENT_      ||', '||Chr(13)||Chr(10)||
                           'GROUP_C   = '||GROUP_C_       ||', '||Chr(13)||Chr(10)||
                           'p_filial  = '||p_filial||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
          fl_ := 1;
          p_code := p_code+1;
          bc.set_context;
          goto custplus;
        end;

        fl_ := 0;
        p_open := p_open+1;
--      l_ins_ := l_ins_+1;
      exception when others then
        bars_audit.error('create_customerShi - err(0): '||sqlerrm      ||' - '  ||Chr(13)||Chr(10)||
                         'RNK       = '||RNK_           ||', '||Chr(13)||Chr(10)||
                         'CUSTTYPE  = '||CUSTTYPE_      ||', '||Chr(13)||Chr(10)||
                         'ND        = '||ND_            ||', '||Chr(13)||Chr(10)||
                         'NMK       = '||NMK_           ||', '||Chr(13)||Chr(10)||
                         'NMKK      = '||NMKK_          ||', '||Chr(13)||Chr(10)||
                         'CODCAGENT = '||CODCAGENT_     ||', '||Chr(13)||Chr(10)||
                         'COUNTRY   = '||COUNTRY_       ||', '||Chr(13)||Chr(10)||
                         'PRINSIDER = '||PRINSIDER_     ||', '||Chr(13)||Chr(10)||
                         'TGR       = '||TGR_           ||', '||Chr(13)||Chr(10)||
                         'OKPO      = '||OKPO_          ||', '||Chr(13)||Chr(10)||
                         'STMT      = '||STMT_          ||', '||Chr(13)||Chr(10)||
                         'SAB       = '||SAB_           ||', '||Chr(13)||Chr(10)||
                         'DATEON    = '||DATEON_        ||', '||Chr(13)||Chr(10)||
                         'TAXF      = '||TAXF_          ||', '||Chr(13)||Chr(10)||
                         'CDST      = '||CDST_          ||', '||Chr(13)||Chr(10)||
                         'CREG      = '||CREG_          ||', '||Chr(13)||Chr(10)||
                         'RGADM     = '||RGADM_         ||', '||Chr(13)||Chr(10)||
                         'DATET     = '||DATET_         ||', '||Chr(13)||Chr(10)||
                         'DATEA     = '||DATEA_         ||', '||Chr(13)||Chr(10)||
                         'ISE       = '||ISE_           ||', '||Chr(13)||Chr(10)||
                         'FS        = '||FS_            ||', '||Chr(13)||Chr(10)||
                         'OE        = '||OE_            ||', '||Chr(13)||Chr(10)||
                         'VED       = '||VED_           ||', '||Chr(13)||Chr(10)||
                         'SED       = '||SED_           ||', '||Chr(13)||Chr(10)||
                         'CRISK     = '||CRISK_         ||', '||Chr(13)||Chr(10)||
                         'ISP       = '||ISP_           ||', '||Chr(13)||Chr(10)||
                         'CLI_KOD   = '||IDCLIENT_      ||', '||Chr(13)||Chr(10)||
                         'GROUP_C   = '||GROUP_C_       ||', '||Chr(13)||Chr(10)||
                         'p_filial  = '||p_filial||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        fl_ := 1;
        p_code := p_code+1;
        bc.set_context;
      end;

<<custplus>> null;

      if fl_=0 then
        if CUSTTYPE_=1 then
          begin
            kl.setBankAttr(RNK_,  -- Rnk_    NUMBER,
                           MFO_,  -- Mfo_    VARCHAR2,
                           BIC_,  -- Bic_    VARCHAR2,
                           null,  -- BicAlt_ VARCHAR2,
                           null,  -- Rating_ VARCHAR2,
                           null,  -- Kod_b_  NUMBER,
                           null,  -- Ruk_    VARCHAR2,
                           TEL_,  -- TelR_   VARCHAR2,
                           null,  -- Buh_    VARCHAR2,
                           null   -- TelB_   VARCHAR2
                           ,null   -- kl190   VARCHAR2
                          );
            fl_ := 0;
          exception when others then
            bars_audit.error('create_customerShi - err(2): '||sqlerrm      ||' - '  ||Chr(13)||Chr(10)||
                             'RNK       = '||RNK_           ||', '||Chr(13)||Chr(10)||
                             'CUSTTYPE  = '||CUSTTYPE_      ||', '||Chr(13)||Chr(10)||
                             'ND        = '||ND_            ||', '||Chr(13)||Chr(10)||
                             'NMK       = '||NMK_           ||', '||Chr(13)||Chr(10)||
                             'NMKK      = '||NMKK_          ||', '||Chr(13)||Chr(10)||
                             'CODCAGENT = '||CODCAGENT_     ||', '||Chr(13)||Chr(10)||
                             'COUNTRY   = '||COUNTRY_       ||', '||Chr(13)||Chr(10)||
                             'PRINSIDER = '||PRINSIDER_     ||', '||Chr(13)||Chr(10)||
                             'TGR       = '||TGR_           ||', '||Chr(13)||Chr(10)||
                             'OKPO      = '||OKPO_          ||', '||Chr(13)||Chr(10)||
                             'STMT      = '||STMT_          ||', '||Chr(13)||Chr(10)||
                             'SAB       = '||SAB_           ||', '||Chr(13)||Chr(10)||
                             'DATEON    = '||DATEON_        ||', '||Chr(13)||Chr(10)||
                             'TAXF      = '||TAXF_          ||', '||Chr(13)||Chr(10)||
                             'CDST      = '||CDST_          ||', '||Chr(13)||Chr(10)||
                             'CREG      = '||CREG_          ||', '||Chr(13)||Chr(10)||
                             'RGADM     = '||RGADM_         ||', '||Chr(13)||Chr(10)||
                             'DATET     = '||DATET_         ||', '||Chr(13)||Chr(10)||
                             'DATEA     = '||DATEA_         ||', '||Chr(13)||Chr(10)||
                             'ISE       = '||ISE_           ||', '||Chr(13)||Chr(10)||
                             'FS        = '||FS_            ||', '||Chr(13)||Chr(10)||
                             'OE        = '||OE_            ||', '||Chr(13)||Chr(10)||
                             'VED       = '||VED_           ||', '||Chr(13)||Chr(10)||
                             'SED       = '||SED_           ||', '||Chr(13)||Chr(10)||
                             'CRISK     = '||CRISK_         ||', '||Chr(13)||Chr(10)||
                             'ISP       = '||ISP_           ||', '||Chr(13)||Chr(10)||
                             'CLI_KOD   = '||IDCLIENT_      ||', '||Chr(13)||Chr(10)||
                             'GROUP_C   = '||GROUP_C_       ||', '||Chr(13)||Chr(10)||
                             'p_filial  = '||p_filial||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
