--

  procedure open_accountShi (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    fl_          number;
    type         cur is ref cursor;
    cur_         cur;
    sql_         varchar2(32000);
    accR_        number;
    ACRA_        number;
    ACRB_        number;
    ACRdb_       number;
    ACRkr_       number;
    ndz_         number;
    id_          number;
    acc_3800_    number;
    acc_3801_    number;
    acc_6204_    number;
    ostc_3800_   number;
    ostc_3801_   number;
    ispg_        varchar(32);
    kvv_         accounts.kv%type;
    NLS_3800_    accounts.nls%type;
    NLS_3801_    accounts.nls%type;

    rnk_         customer.rnk%type;
    DAPP_        date;
    daos_        date;
    dazs_        date;
    kv_          accounts.kv%type;
    isp_         number;
    nbs_         char(4);
    tip_         char(3);
    vid_         number;
    ostc_        number;
    ostq_        number;
    lim_         number;
    nms_         varchar2(70);
    nls_         accounts.nls%type;
    nlsalt_      accounts.nlsalt%type;
    branch_      accounts.branch%type;
    OB22_        SPECPARAM_INT.ob22%type;
    OB88_        SPECPARAM_INT.ob88%type;
    P080_        SPECPARAM_INT.P080%type;
    MFO_         SPECPARAM_INT.MFO%type;
    R020_FA_     SPECPARAM_INT.R020_FA%type;
    R011_        SPECPARAM.R011%type;
    R013_        SPECPARAM.R013%type;
    S080_        SPECPARAM.S080%type;
    S180_        SPECPARAM.S180%type;
    S190_        SPECPARAM.S190%type;
    S181_        SPECPARAM.S181%type;
    S260_        SPECPARAM.S260%type;
    S200_        SPECPARAM.S200%type;
    S120_        SPECPARAM.S120%type;
    S031_        SPECPARAM.S031%type;
    S182_        SPECPARAM.S182%type;
    S240_        SPECPARAM.S240%type;
--  S130_        SPECPARAM.S130%type;
    NKD_         SPECPARAM.NKD%type;
    IR_          int_ratn.ir%type;
    bdat_        int_ratn.bdat%type;
    pap_         ps.pap%type;
    serv_db_s_   accounts.nls%type;
    serv_kr_s_   accounts.nls%type;
    serv_i_va_   accounts.kv%type;
    NLSs6_       varchar2(15);
    I_VAs6_      number;
    PercenRate_  number;
    flS6_        number;
    open_        number;
    s7_          number;
    s8_          number;
    s9_          number;
--  l_ins_       number;
--  len_S080_    varchar2(4);
    mrnk_        varchar2(32);

    pos_         number;
    kfil_        number;
    newnls_      accounts.nls%type;
    nn_          int;
    ffff_        char(4);
    fff_         char(3);
    ff_          char(2);
    i_           int;
    group_c_     varchar2(2);
    OB22aaa_     varchar2(3);
    OB22aa_      varchar2(3);

    fi3_         char(3);
    ugosaldoa_   int;
    op_          int;

    Job_         number;
    MFOg_        varchar2(12);

 begin

    bars_alerter(0);

    gl.frcvr := 1;

--  l_ins_ := 0;
    p_open := 0;
    p_code := 0;

    MFOg_ := f_ourmfo_g;

    execute immediate 'select substr(min(branch),-4,3)
                       from   s6_hi_filial'
                       into   fi3_;

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=MFOg_                              and
           logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=MFOg_ and
             logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%';
      if ispg_ is null then
        select to_char(min(id))
        into   ispg_
        from   staff$base
        where  bc.extract_mfo(branch)=MFOg_ and
               tabn is not null;
        if ispg_ is null then
          select to_char(min(id))
          into   ispg_
          from   staff$base
          where  bc.extract_mfo(branch)=MFOg_;
        end if;
      end if;
    end if;

    mrnk_ := -1;

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 disable';
    exception when OTHERS then
      null;
    end;

    begin
      execute immediate 'alter trigger TIU_ACCOUNT67 disable';
    exception when OTHERS then
      null;
    end;

    sql_:='select distinct  /* to ACCOUNTS */
                  nvl(cw.rnk,'||mrnk_||')                                                                                                  rnk    ,
                  null                                                                                                                     DAPP   ,
                  null                                                                                                                     dazs   ,
                  to_date('''||p_datx||''')-4                                                                                              daos   ,
                  to_number(s.CURR)                                                                                                        kv     ,
                  nvl(u.UBARS,'||ispg_||')                                                                                                 isp    ,
                  substr(s.CODE,1,4)                                                                                                       nbs    ,
                  1                                                                                                                        pos    ,
                  0                                                                                                                        ostc   ,
                  0                                                                                                                        ostq   ,
                  0                                                                                                                        lim    ,
--                nvl(n.taxotype,0)                                                                                                        vid    ,
                  (case when substr(s.CODE,1,2)=''25''                       then
                     11
                        when substr(s.CODE,1,4) in (''2610'',
                                                    ''2612'',
                                                    ''2615'',
                                                    ''2630'',
                                                    ''2635'',
                                                    ''2651'',
                                                    ''2652'',
                                                    ''1510'',
                                                    ''1511'',
                                                    ''1512'',
                                                    ''1515'',
                                                    ''1610'',
                                                    ''1611'',
                                                    ''1612'',
                                                    ''1615'')                then
                     4
                        when substr(s.CODE,1,4) in (''1500'',
                                                    ''1600'')                then
                     10
                        when substr(s.CODE,1,2) in (''20'',
                                                    ''21'',
                                                    ''22'',
                                                    ''27'')                  then
                     6
                        when substr(s.CODE,1,4)=''9802''                     then
                     5
                        when substr(s.CODE,1,4) in (''4310'',
                                                    ''4430'',
                                                    ''4432'',
                                                    ''4438'',
                                                    ''4439'',
                                                    ''4530'',
                                                    ''4532'',
                                                    ''4538'',
                                                    ''4539'')                then
                     8
                        when substr(s.CODE,1,4) in (select nbs from dpa_nbs) then
                     3
                   else
                     0
                   end)                                                                                                                    vid    ,
                  migraSK.valid_symbol(trim(substr(nvl(s.NAME,''не определено''),1,70)))                                                   nms    ,
                  s.CODE                                                                                                                   nls    ,
                  null                                                                                                                     nlsalt ,
                  nvl(f.branch,''/''||'''||MFOg_||'''||''/000000/'')                                                                       branch ,
                  /* to SPECPARAM_INT */
                  substr(''00''||trim(s.OB22),-2)                                                                                          OB22   ,
                  s.OB88                                                                                                                   OB88   ,
                  s.P080                                                                                                                   P080   ,
                  null                                                                                                                     MFO    ,
                  s.R020_FA                                                                                                                R020_FA,
                  /* to SPECPARAM */
                  s.R011                                                                                                                   R011   ,
                  s.R013                                                                                                                   R013   ,
                  nullif(s.S080,''0'')                                                                                                     S080   ,
                  decode(upper(nullif(nullif(nullif(nullif(s.S180,''I''),''K''),''L''),''М'')),
                         ''С'',''C'',''А'',''A'',''В'',''B'',''Е'',''E'',''Н'',''H'',''8'',''B'',''9'',''C'',
                         upper(nullif(nullif(nullif(nullif(s.S180,''I''),''K''),''L''),''М'')))                                            S180   ,
                  s.S190                                                                                                                   S190   ,
                  s.S181                                                                                                                   S181   ,
                  s.S260                                                                                                                   S260   ,
                  s.S200                                                                                                                   S200   ,
                  s.S120                                                                                                                   S120   ,
                  null                                                                                                                     S031   ,
                  null                                                                                                                     S182   ,
                  decode(upper(s.S240),''С'',''C'',''А'',''A'',''В'',''B'',''Е'',''E'',''Н'',''H'',''8'',''B'',''9'',''C'',upper(s.S240))  S240   ,
                  migraSK.valid_symbol(trim(s.RESTNO))                                                                                     NKD    ,
                  /* to INT_RATN */
                  decode(substr(s.CODE,1,2),''2'',2,ps.pap)                                                                                pap    ,
                  s.CODE                                                                                                                   NLSs6  ,
                  to_number(s.CURR)                                                                                                        I_VAs6 ,
                  s.PARTCODE                                                                                                               kfil   ,
                  ''0''                                                                                                                    group_c,
--                decode(s.ISESCROW,1,99,0)                                                                                                blk    ,
                  s.S012                                                                                                                   IR     ,
                  (case when nvl(s.DOPEN,s.LASTDATE)<to_date(''01/01/1920'',''DD/MM/YYYY'') then
                     add_months(nvl(s.DOPEN,s.LASTDATE),1200)
                   else
                     nvl(s.DOPEN,s.LASTDATE)
                   end)                                                                                                                    bdat
           from   customerw         cw,
                  S6_HI_BAC         s ,
                  S6_HI_FILIAL      f ,
                  (select nbs,
                          min(taxotype) taxotype
                   from   DPA_nbs
                   where  type=''DPA''
                   group  by nbs)   n ,
                  USERS_SKARB6_BARS u ,
                  ps
           where  cw.tag(+)=''RS6HI''                       and
                  to_number(cw.value(+))=
                  nvl(s.CLIENT,0)*10000000+'||p_filial||'   and
                  n.nbs(+)=substr(s.CODE,1,4)               and
                  s.DCLOSE is null                          and
                  f.kfil(+)=s.PARTCODE                      and
                  s."USER"=u.USKARB6(+)                     and
                  u.filial(+)='''||p_filial||'''            and
                  (substr(s.CODE,1,1)<>''8'' or
                   substr(s.CODE,1,3) in (''890'',''891'')) and
                  ps.nbs=substr(s.CODE,1,4)
           order  by substr(s.CODE,1,4) desc
          ';

--  bars_audit.info('open_accountShi - info(0a): sql_a = '||substr(sql_,    1,3000));
--  bars_audit.info('open_accountShi - info(0b): sql_b = '||substr(sql_, 3001,3000));
--  bars_audit.info('open_accountShi - info(0c): sql_c = '||substr(sql_, 6001,3000));
--  bars_audit.info('open_accountShi - info(0d): sql_d = '||substr(sql_, 9001,3000));
--  bars_audit.info('open_accountShi - info(0e): sql_e = '||substr(sql_,12001,3000));
--  bars_audit.info('open_accountShi - info(0f): sql_f = '||substr(sql_,15001,3000));
--  bars_audit.info('open_accountShi - info(0g): sql_g = '||substr(sql_,18001,3000));
--  bars_audit.info('open_accountShi - info(0h): sql_h = '||substr(sql_,21001,3000));

    open cur_ for sql_;

    LOOP

      fetch cur_ into rnk_    ,
                      DAPP_   ,
                      dazs_   ,
                      daos_   ,
                      kv_     ,
                      isp_    ,
                      nbs_    ,
                      pos_    ,
                      ostc_   ,
                      ostq_   ,
                      lim_    ,
                      vid_    ,
                      nms_    ,
                      nls_    ,
                      nlsalt_ ,
                      branch_ ,
                      OB22_   ,
                      OB88_   ,
                      P080_   ,
                      MFO_    ,
                      R020_FA_,
                      R011_   ,
                      R013_   ,
                      S080_   ,
                      S180_   ,
                      S190_   ,
                      S181_   ,
                      S260_   ,
                      S200_   ,
                      S120_   ,
                      S031_   ,
                      S182_   ,
                      S240_   ,
                      NKD_    ,
                      pap_    ,
                      NLSs6_  ,
                      I_VAs6_ ,
                      kfil_   ,
                      group_c_,
                      IR_     ,
                      bdat_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      fl_ := 0;
      op_ := 0;

--    определяем импортили уже счёт или нет (выборка из S6_MIGRNLS)

      begin
        select nls_bars
        into   newnls_
        from   S6_MIGRNLS
        where  nls_fil=nls_ and
               kv=kv_       and
               filial=p_filial;
      exception when no_data_found then
        newnls_ := null;
      end;

--    здесь вычисляем newnls_ на основании информации из таблиц
--    S6_HI_SHABLON, S6_HI_DEPO, S6_HI_GROUP_C, S6_HI_FILIAL

      if newnls_ is null then -- будем открывать новый счёт по маске

--      1. определяем FF_, FFF_, BRANCH_ из S6_HI_FILIAL

        begin
          execute immediate 'select trim(branch)                 ,
                                    substr(''0000''||trim(ff),-4),
                                    substr(''000''||trim(ff),-3) ,
                                    substr(''00''||trim(ff),-2)
                             from   S6_HI_FILIAL
                             where  kfil='||to_char(kfil_)||' and
                                    rownum<2'
                             into   branch_,
                                    ffff_  ,
                                    fff_   ,
                                    ff_;
        exception when no_data_found then
          ffff_ := substr(branch_,-5,4);
          fff_  := substr(branch_,-4,3);
          ff_   := substr(branch_,-3,2);
        end;

--      2. ищем счёт в S6_HI_DEPO, если нашли - newnls_ := vkrzn(KOTL) и ВСЁ

        begin
          execute immediate 'select kotl,
                                    ob22,
                                    branch
                             from   S6_HI_DEPO
                             where  trim(nls)='''||nls_||'''           and
                                    i_va='||to_char(kv_)||'            and
                                    substr(kotl,1,4) not in (''1001'',
                                                             ''1101'',
                                                             ''1011'') and
                                    rownum<2'
                             into   newnls_,
                                    OB22_  ,
                                    branch_;
          nms_    := 'Котловой счёт '||substr(nls_,1,4)||' ОБ22 '||OB22_;
          newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);
          goto BCE;
        exception when OTHERS then
          null;
        end;

--      3. иначе - ищем балансовый в S6_HI_SHABLON, если есть, то берём
--         соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_HI_SHABLON
                             where  ((length(trim(BALS))=4 and
                                      substr('''||nls_||''',1,4)=trim(BALS)) or
                                     (length(trim(BALS))=3 and
                                      substr('''||nls_||''',1,3)=trim(BALS)) or
                                     (length(trim(BALS))=2 and
                                      substr('''||nls_||''',1,2)=trim(BALS)) or
                                     (length(trim(BALS))=1 and
                                      substr('''||nls_||''',1,1)=trim(BALS))) and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      4. иначе - ищем характеристку контрагента счёта в S6_HI_GROUP_C,
--         если есть, то берём соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_HI_GROUP_C
                             where  group_c='''||group_c_||''' and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      5. иначе - добавляем к счёту арифметически 1 (цикл) и определяем
--         первый попавший счёт, его и присваиваем в newnls_

        newnls_ := nls_;

        if substr(newnls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(newnls_,1,3)||'2'||substr(newnls_,5);
        end if;

        newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);

        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

        goto BCE; -- cчёт определен!

<<MASKA>> null; -- здесь разобрать маску СБЕРБАНКА, выродить шаблон в
                -- модификаторах БАРСа и определить счёт по ентому шаблону!

--      BBBB          - балансовый счёт    (В могут быть случайно русские)
--      GGGGGG, GGGGG - RNK
--      HHH, HH или H - номер по порядку   (Н могут быть случайно русские)
--      K             - контрольный разряд (К могут быть случайно русские)
--      FFF или FF    - код отделения
--      AAA или AA    - код ОБ22           (А могут быть случайно русские)

        newnls_ := replace(upper(newnls_),'В','B');
        newnls_ := replace(upper(newnls_),'К','K');
        newnls_ := replace(upper(newnls_),'А','A');
        newnls_ := replace(upper(newnls_),'Н','H');

        OB22_   := replace(OB22_,'А','A');
        OB22_   := replace(OB22_,'В','B');
        OB22_   := replace(OB22_,'С','C');
        OB22_   := replace(OB22_,'Н','H');
        OB22_   := replace(OB22_,'К','K');
        OB22_   := replace(OB22_,'М','M');
        OB22_   := replace(OB22_,'Е','E');
        OB22_   := replace(OB22_,'Х','X');
        OB22_   := replace(OB22_,'Р','P');
        OB22_   := replace(OB22_,'О','O');
        OB22_   := replace(OB22_,'Т','T');

        OB22aaa_:= (case when upper(substr(nvl(OB22_,'00'),1,1))>'9' then
                      to_char(ascii(upper(substr(nvl(OB22_,'00'),1,1)))-55)||
                      substr(nvl(OB22_,'00'),-1)
                    else
                      '0'||to_char(substr(nvl(OB22_,'00'),1,1))||
                      substr(nvl(OB22_,'00'),-1)
                    end);
        OB22aa_ := substr(OB22aaa_,-2);

        newnls_ := replace(upper(newnls_),'BBBB',substr(nls_,1,4));
        newnls_ := replace(upper(newnls_),'G','R');
        newnls_ := replace(upper(newnls_),'H','№');
        newnls_ := replace(upper(newnls_),'K','_');
        newnls_ := replace(upper(newnls_),'FFFF',ffff_);
        newnls_ := replace(upper(newnls_),'FFF',fff_);
        newnls_ := replace(upper(newnls_),'FF',ff_);
        newnls_ := replace(upper(newnls_),'AAA',OB22aaa_);
        newnls_ := replace(upper(newnls_),'AA',OB22aa_);

        if substr(nls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(nls_,1,3)||'2'||substr(newnls_,5);
        end if;

        toroot;
        delete
        from   nlsmask
        where  maskid='TemporaryM';

        insert
        into   nlsmask (MASKID,
                        DESCR ,
                        MASK)
                values ('TemporaryM'                           ,
                        'Временная маска для шаблона счетов СБ',
                        newnls_);
        commit;

--      получаем РЕАЛЬНЫЙ номер счёта

        tokf;
        newnls_ := f_newnls2(null               ,
                             'TemporaryM'       ,
                             substr(newnls_,1,4),
                             rnk_               ,
                             null               ,
                             kv_);
        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

<<BCE>> null;

      end if;

      bc.subst_branch(branch_); -- устанавливаем бранч для открываемого счёта

--    здесь имеем newnls_ и kv_ для открытия НОВОГО счёта или изменения старого

      if rnk_=-1 then
        bars_audit.error('open_accountShi - err(10): счёт не привязан ни к одному клиенту '||Chr(13)||Chr(10)||
                                                               'NLS = '    ||nls_   ||', ' ||Chr(13)||Chr(10)||
                                                               'NEWNLS = ' ||newnls_||', ' ||Chr(13)||Chr(10)||
                                                               'KV = '     ||kv_    ||' '  ||Chr(13)||Chr(10)||
                                                               'branch = ' ||branch_||', ' ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end if;

      begin
        select acc
        into   accR_
        from   accounts
        where  nls=newnls_ and
               kv=kv_;
      exception when no_data_found then
        accR_ := null;
                when OTHERS then
        bars_audit.error('open_accountShi - err(1): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                          'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                          'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                          'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                          'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end;

      if accR_ is null then
        ndz_ := null;
        --1. OP_REG_ex
        begin

          tip_ := 'ODB';

          if    substr(newnls_,1,3)='100' and substr(newnls_,1,4)<>'1007' then
            if kv_=980 then
              tip_ := 'KAS';
            else
              tip_ := 'KAV';
            end if;
          elsif substr(newnls_,1,3) in ('604','603','602') and kv_=980 then
              tip_ := 'SD ';
          elsif substr(newnls_,1,4) in ('2625','2605','2655') then
              tip_ := 'PK1';
          end if;

          if trunc(sysdate)<p_datx then
            begin
              insert
              into   fdat (fdat)
                   values (p_datx-4);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx-4);
          else
            begin
              insert
              into   fdat (fdat)
                   values (p_datx);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx);
          end if;

          gl.amfo := MFOg_;

          OP_REG_exfl(99     ,  -- mod_       INTEGER,   -- Opening mode :
                      null   ,  -- p1_        INTEGER,   -- 1st Par      :
                      0      ,  -- p2_        INTEGER,   -- 2nd Par      :
                      0      ,  -- p3_        INTEGER,   -- 3rd Par (Grp):
                      ndz_   ,  -- p4_ IN OUT INTEGER,   -- 4th Par      :
                      rnk_   ,  -- rnk_       INTEGER,   -- Customer number
                      newnls_,  -- p_nls_     VARCHAR2,  -- Account  number
                      kv_    ,  -- kv_        SMALLINT,  -- Currency code
                      nms_   ,  -- nms_       VARCHAR2,  -- Account name
                      tip_   ,  -- tip_       CHAR,      -- Account type
                      isp_   ,  -- isp_       SMALLINT,
                      accR_  ,  -- accR_  OUT INTEGER,
                      '1'    ,  -- nbsnull_   VARCHAR2 DEFAULT '1',
                      null   ,  -- pap_       NUMBER   DEFAULT NULL,
                      vid_   ,  -- vid_       NUMBER   DEFAULT NULL,
                      pos_   ,  -- pos_       NUMBER   DEFAULT NULL,
                      null   ,  -- sec_       NUMBER   DEFAULT NULL,
                      null   ,  -- seci_      NUMBER   DEFAULT NULL,
                      null   ,  -- seco_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkd_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkk_      NUMBER   DEFAULT NULL,
          /* lim_? */ null   ,  -- lim_       NUMBER   DEFAULT NULL,
                      null   ,  -- ostx_      VARCHAR2 DEFAULT NULL,
                      nlsalt_,  -- nlsalt_    VARCHAR2 DEFAULT NULL,
                      branch_,  -- tobo_      VARCHAR2 DEFAULT NULL,
                      null   ,  -- accc_      NUMBER   DEFAULT NULL,
                      0);
          op_ := 1;
          begin
            update accounts
            set    dapp=dapp_
            where  acc=accR_;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountShi - err(2): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
--          fl_ := 1;
          end;
          begin
            update ree_tmp
            set    ot=6
            where  nls=newnls_ and
                   kv=kv_      and
                   ot=1        and
                   fn_o is null;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountShi - err(9): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
          end;
--        l_ins_ := l_ins_+1;
        exception when others then
          bars_audit.error('open_accountShi - err(3): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                            'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                            'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                            'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                            'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
          p_code := p_code+1;
          fl_ := 1;
        end;
      end if;

      if fl_=0 then
        select count(*)
        into   flS6_
        from   accountsw
        where  acc=accR_ and
               tag='IS6HI';
        if flS6_=0 then
          select count(1)
          into   ugosaldoa_
          from   saldoa
          where  acc=accr_;

          if ugosaldoa_=0 then
            begin
              insert
              into   saldoa (acc ,
                             fdat,
                             pdat,
                             ostf,
                             dos ,
                             kos)
                     values (accR_   ,
                             gl.bdate,
                             dapp_   ,
                             0       ,
                             0       ,
                             0);
            EXCEPTION WHEN OTHERS THEN
              null;
            end;
          end if;
        else
          update accounts
          set    daos=daos_
          where  acc=accR_;
        end if;

        if op_=1 then
          begin
            insert
            into   accountsw (acc,
                              tag,
                              value)
                      values (accR_  ,
                              'IS6HI',
                              'Imported from HITCf');
          exception when others then
            bars_audit.error('open_accountShi - err(4): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --2. INSERT or UPDATE SPECPARAM_INT

        if fl_=0 then
          begin
            insert
            into   SPECPARAM_INT (acc    ,
                                  p080   ,
                                  ob22   ,
                                  mfo    ,
                                  r020_fa,
                                  ob88)
                          values (accR_   ,
                                  P080_   ,
                                  OB22_   ,
                                  MFO_    ,
                                  R020_FA_,
                                  OB88_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM_INT
              set    p080=P080_      ,
                     ob22=OB22_      ,
                     mfo=MFO_        ,
                     r020_fa=R020_FA_,
                     ob88=OB88_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountShi - err(7u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountShi - err(7): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --3. INSERT or UPDATE SPECPARAM

        if fl_=0 then
          begin
            insert
            into   SPECPARAM (acc ,
                              R011,
                              R013,
                              S240,
                              S080,
                              S180,
                              S190,
                              S181,
                              S260,
                              S200,
                              S120,
                              S031,
                              S182,
--                            S130,
                              NKD)
                      values (accR_,
                              R011_,
                              R013_,
                              S240_,
                              S080_,
                              S180_,
                              S190_,
                              S181_,
                              S260_,
                              S200_,
                              S120_,
                              S031_,
                              S182_,
--                            S130_,
                              NKD_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM
              set    R011=R011_,
                     R013=R013_,
                     S240=S240_,
                     S080=S080_,
                     S180=S180_,
                     S190=S190_,
                     S181=S181_,
                     S260=S260_,
                     S200=S200_,
                     S120=S120_,
                     S031=S031_,
                     S182=S182_,
--                   S130=S130_,
                     NKD=NKD_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountShi - err(8u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                 'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                 'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                 'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                 'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountShi - err(8): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                              'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                              'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                              'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                              'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        if substr(nls_,1,1) in ('2','3','9') then

--        Процентные карточки

          if fl_=0 then

            if IR_ is not null /* and ACRA_ is not null */ then

              if pap_=1 then
                 id_ := 0;
              else
                 id_ := 1;
              end if;

--            4. INSERT or UPDATE INT_ACCN

              begin
                insert
                into    INT_ACCN (ACC    ,  -- accR_
                                  ID     ,  -- id_
                                  METR   ,  -- 0
--                                BASEM  ,
                                  BASEY  ,  -- 0
                                  FREQ   ,  -- 1
--                                STP_DAT,
                                  ACR_DAT,  -- bankdate
--                                APL_DAT,  -- bankdate-1
--                                TT     ,
                                  ACRA   ,  -- ???
                                  ACRB
--                                S      ,
--                                TTB    ,
--                                KVB    ,
--                                NLSB   ,
--                                MFOB   ,
--                                NAMB   ,
--                                NAZN   ,
--                                IO     ,
--                                BRANCH    -- branch_
                                 )
                          values (accR_     ,
                                  id_       ,
                                  0         ,
--                                null      ,
                                  0         ,
                                  1         ,
--                                null      ,
                                  bankdate  ,
--                                bankdate-1,
--                                null      ,
                                  accR_     ,  -- ACRA_ ???
                                  null         -- ACRB_ ???
--                                null      ,
--                                null      ,
--                                null      ,
--                                null      ,
--                                null      ,
--                                null      ,
--                                null      ,
--                                null      ,
--                                branch_
                                 );
              EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
                update INT_ACCN
                set    METR   =0         ,
--                     BASEM  =null      ,
                       BASEY  =0         ,
                       FREQ   =1         ,
--                     STP_DAT=null      ,
                       ACR_DAT=bankdate  ,
--                     APL_DAT=bankdate-1,
--                     TT     =null      ,
                       ACRA   =accr_     ,  -- ACRA_ ???
                       ACRB   =null         -- ACRB_ ???
--                     S      =null      ,
--                     TTB    =null      ,
--                     KVB    =null      ,
--                     NLSB   =null      ,
--                     MFOB   =null      ,
--                     NAMB   =null      ,
--                     NAZN   =null      ,
--                     IO     =null      ,
--                     BRANCH =branch_
                where  acc=accR_ and
                       id=id_;
                        WHEN OTHERS THEN
