  procedure load_hitc_ostc (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type     cur is ref cursor;
    cur_     cur;
    sql_     varchar2(4000);

    tt_      oper.TT%type      := '024';
    sk_      oper.SK%type      ;
    TIP_     accounts.TIP%type ;
    VOB_     oper.vob%type     := 6;
    REF_     oper.REF%type     ;
    DK_      oper.DK%type      ;
    NMST_    oper.NAM_A%type   ;
    NLST_    oper.NLSA%type    ;
    NMSS_    oper.NAM_A%type   ;
    S_       oper.S%type       ;
    SQ_      oper.SQ%type      ;
    NAZN_    oper.NAZN%type    := 'Переведення залишку з ';
    Isp_     oper.userid%type  := 20094;

    NLSS_    accounts.nls%type ;
    NLS_OLD_ accounts.nls%type ;
    KV_      accounts.kv%type  ;
    OST_     accounts.ostc%type;
    OSTQ_    accounts.ostq%type;

    c024_    number;
    pos_     accounts.pos%type ;
    accs_    accounts.acc%type ;
    daot_    date;
    f38_     char(1);

  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

    toroot;

    update tts
    set    rang=100
    where  tt=tt_;
    commit;

    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    select count(1)
    into   c024_
    from   oper
    where  tt=tt_        and
           sos=5         and
           vdat=gl.bdate and
           userid=20094  and
           nazn like 'Переведення залишку з '||p_filial||'/%';

    if c024_>0 then
      bars_audit.error('load_hitc_ostc - err(0): повторная загрузка остатков невозможна, необходим откат ...');
      p_code := p_code+1;
    goto nozagruzka;
    end if;

    sql_:='select m.nls_bars                                        NLSS   ,
                  decode(substr(o.acca,1,4),''3739'',o.accb,
                                            ''3906'',o.accb,
                                            ''9900'',o.accb,
                                            ''9910'',o.accb,o.acca) NLS_OLD,
                  to_number(o.curr)                                 KV     ,
                  decode(o.curr,''980'',o.nsum,o.esum)*100          OST    ,
                  o.nsum*100                                        OSTQ   ,
                  decode(substr(o.acca,1,4),''3739'',0,
                                            ''3906'',0,
                                            ''9900'',0,
                                            ''9910'',0,1)           DK
           from   S6_HI_ODM  o,
                  S6_MIGRNLS m
           where  decode(substr(o.acca,1,4),''3739'',o.accb,
                                            ''3906'',o.accb,
                                            ''9900'',o.accb,
                                            ''9910'',o.accb,o.acca)=m.nls_fil and
                  to_number(o.curr)=m.kv                                      and
                  m.filial='''||p_filial||'''                                 and
                  decode(o.curr,''980'',o.nsum,o.esum)<>0';

    open cur_ for sql_;

    loop

      fetch cur_ into NLSS_   ,
                      NLS_OLD_,
                      KV_     ,
                      OST_    ,
                      OSTQ_   ,
                      DK_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        execute immediate 'select NLS_TRA
                           from   (select trim(bals)||''%'' LIK,
                                          KV                   ,
                                          to_char(transit)  NLS_TRA
                                   from   S6_HI_TRANSIT
                                   order  by length(trim(bals)) desc,
                                             trim(bals)             ,
                                             nvl(KV,0) desc)
                           where  '''||nls_old_||''' like LIK            and
                                  (nullif(KV,0) is null or '||kv_||'=KV) and
                                  rownum=1'
                           into   NLST_;
      EXCEPTION WHEN NO_DATA_FOUND THEN
        bars_audit.error('load_hitc_ostc - err(1): Не описан в TRANSIT.DBF счёт-транзит для '||p_filial||'/'||nls_old_||'('||KV_||') '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
                WHEN OTHERS THEN
        if sqlcode=-904 then
          bars_audit.error('load_hitc_ostc - err(5): старая структура TRANSIT.DBF '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        end if;
        bars_audit.error('load_hitc_ostc - err(6): '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
      end;

      begin
        select trim(substr(nms,1,38)),
               daos
        into   NMST_,
               daot_
        from   accounts
        where  kv=KV_    and
               nls=NLST_ and
               dazs is null;
      EXCEPTION WHEN NO_DATA_FOUND THEN
        bars_audit.error('load_hitc_ostc - err(2): Не найден или закрыт счёт-транзит '||nlsT_||'('||KV_||') '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
      end;

      begin
        select trim(substr(nms,1,38)),
               tip                   ,
               pos                   ,
               acc
        into   NMSS_,
               TIP_ ,
               pos_ ,
               accs_
        from   accounts
        where  kv=KV_    and
               nls=NLSS_ and
               dazs is null;
      EXCEPTION WHEN NO_DATA_FOUND THEN
        bars_audit.error('load_hitc_ostc - err(3): Не найден или закрыт НОВЫЙ счёт '||NLSS_||' для '||p_filial||'/'||nls_old_||'('||kv_||') '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
      end;

      if TIP_='KAS' then
        sk_ := 37; -- после уточнения Житомира
      else
        sk_ := null;
      end if;

      S_  := ost_;
      sq_ := ostq_;

      if daot_>p_datx then
        update accounts
        set    daos=p_datx
        where  kv=KV_ and
               nls=NLST_;
      end if;

      if substr(NLSS_,1,4)='2625' then

        toroot;

        select substr(flags,39,1)
        into   f38_
        from   tts
        where  tt=tt_;

        update tts
        set    flags=substr(flags,1,38)||'1'||substr(flags,40)
        where  tt=tt_;

        commit;

        tokf;

        gl.pl_dat(p_datx);
        gl.amfo := f_ourmfo_g;

      end if;

      begin
        GL.REF(REF_);
        GL.IN_DOC2(REF_,  TT_,   VOB_, REF_, SYSDATE, GL.BDATE, DK_, KV_,
                   S_,    KV_,   S_,   sq_,  sk_,     GL.BDATE, GL.BDATE,
                   NMsT_, NLST_, gl.AMFO,
                   NMSS_, NLSS_, gl.AMFO,
                   NAZN_||p_filial||'/'||NLS_OLD_,
                   NULL, null, null, null, null, 1, null, ISP_);
        GL.PAYV(0, REF_, GL.BDATE, TT_, DK_, KV_, NLST_, S_, KV_, NLSS_, S_);
        GL.PAY (2, REF_, GL.BDATE);
        commit;
        p_open := p_open+1;
      EXCEPTION WHEN others THEN
        bars_audit.error('load_hitc_ostc - err(4): Ошибка оплаты '||nlsT_||'('||KV_||') --> '||NLSS_||'('||KV_||') на сумму='||S_||' - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        rollback;
        p_code := p_code+1;
      end;

      if substr(NLSS_,1,4)='2625' then

        toroot;

        update tts
        set    flags=substr(flags,1,38)||f38_||substr(flags,40)
        where  tt=tt_;

        commit;

        tokf;

        gl.pl_dat(p_datx);
        gl.amfo := f_ourmfo_g;

      end if;

<<dalee>> null;

    end loop;
    close cur_;

    dbms_application_info.set_client_info('pereocenka');

    migraSK.pereocenka(p_code,'load_hitc_ostc - err',p_datx);

    dbms_application_info.set_client_info(' ');

<<nozagruzka>> null;

    toroot;

    update tts
    set    rang=null
    where  tt=tt_;
    commit;

    bars_alerter(1);

  end load_hitc_ostc;

--

  procedure drop_hitc_ostc (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

--  откат документов по развороту остатков

    toroot;

    update tts
    set    flags=substr(flags,1,38)||'1'||substr(flags,40)
    where  tt='BAK';
    commit;

    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    for k in (
              select ref
              from   oper
              where  tt='024'      and
                     sos=5         and
                     vdat=gl.bdate and
                     userid=20094  and
                     nazn like 'Переведення залишку з '||p_filial||'/%')
    loop
      FOR x IN (SELECT a.acc ,
                       o.fdat,
                       o.sq
                FROM   accounts a,
                       opldok   o
                WHERE  o.ref=k.ref AND
                       a.pos=2     AND
                       o.acc=a.acc)
      LOOP
        UPDATE saldob
        SET    dos=dos-x.sq,
               kos=kos-x.sq
        WHERE  acc=x.acc AND
               fdat=x.fdat;
      END LOOP;
      ful_bak(k.ref);
      bars_audit.info('drop_hitc_ostc: ful_bak '||k.ref);
      commit;
      p_open := p_open+1;
    end loop;

    toroot;

    update tts
    set    flags=substr(flags,1,38)||'0'||substr(flags,40)
    where  tt='BAK';
    commit;

    bars_alerter(1);

  end drop_hitc_ostc;

