  procedure fin_deblockinghi (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    l_rnk     number;
    blkk_     number;
    branch2_  varchar2(15);
    Job_      number;
    MFOg_     varchar2(12);
  begin

    bars_alerter(0);

    p_open   := 0;
    p_code   := 0;
    branch2_ := null;

    MFOg_ := f_ourmfo_g;

--  корректировка accounts по saldoa, saldob (через задание)

    BEGIN
      DBMS_JOB.SUBMIT (job       => Job_               ,
                       what      => 'correct_accounts;',
                       next_date => sysdate            ,
                       interval  => null               ,
                       no_parse  => FALSE);
    exception when others then
      bars_audit.error('fin_deblockinghi - err(J): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
    END;

    tokf;

--  разблокировка счетов: blkk и blkd с 99 на 0

    for k in (select nls_bars,
                     nls_fil ,
                     kv
              from   S6_MIGRNLS
              where  filial=p_filial)
    loop
      begin
        execute immediate 'select isescrow
                           from   s6_hi_'||p_filial||'_bac
                           where  code='''||k.nls_fil||''' and
                                  to_number(curr)='||to_char(k.kv)
                           into   blkk_;
        exception when others then
          blkk_ := 0;
        end;
      update accounts
      set    blkk=blkk_*99,
             blkd=0
      where  nls=k.nls_bars and
             kv=k.kv;
      p_open := p_open+1;
      if branch2_ is null then
        begin
          select substr(branch,1,15)
          into   branch2_
          from   accounts
          where  nls=k.nls_bars and
                 kv=k.kv;
        exception when no_data_found then
          null;
        end;
      end if;
    end loop;
    commit;

    toroot;

--  удаление МФО филиала из таблиц LKL_RRP и BANK_ACC

    tokf;
    begin
      delete
      from   lkl_rrp
      where  mfo=p_filial;
      commit;
    exception when OTHERS then
      rollback;
      bars_audit.error('fin_deblockinghi - err(1) for delete from lkl_rrp: '||sqlerrm);
    end;
    begin
      delete
      from   bank_acc
      where  mfo=p_filial;
      commit;
    exception when OTHERS then
      rollback;
      bars_audit.error('fin_deblockinghi - err(2) for delete from bank_acc: '||sqlerrm);
    end;
    toroot;

--  обновление материализованного представления

    NERUXOMI;

--  добавка RNK и параметра с RNK для бранчей

    begin
      for c in (select branch,
                       name
                from   branch
                where  branch like '/'||MFOg_||'/%'
                order  by branch)
      loop
        begin
          bc.subst_branch(c.branch);
          begin
            select rnk
            into   l_rnk
            from   customer
            where  branch=c.branch and
                   notes='BRANCH<->RNK';
          exception when no_data_found then

            execute immediate 'select s_customer.nextval from dual' into l_rnk;

            insert
            into   customer (rnk      ,
                             tgr      ,
                             custtype ,
                             codcagent,
                             okpo     ,
                             country  ,
                             nmk      ,
                             nmkk     ,
                             notes    ,
                             ise      ,
                             fs       ,
                             oe       ,
                             ved      ,
                             sed      ,
                             k050     ,
                             branch)
                     values (l_rnk                    ,
                             1                        ,
                             1                        ,
                             1                        ,
                             f_ourokpo                ,
                             804                      ,
                             trim(substr(c.name,1,70)),
                             trim(substr(c.name,1,38)),
                             'BRANCH<->RNK'           ,
                             '12201'                  ,
                             '31'                     ,
                             '96130'                  ,
                             '65120'                  ,
                             '80  '                   ,
                             '610'                    ,
                             c.branch);
          end;

          begin
            update branch_parameters
            set    val=to_char(l_rnk)
            where  tag='RNK' and
                   branch=c.branch;

            if SQL%ROWCOUNT=0 then
             insert
             into   branch_parameters (branch,
                                       tag   ,
                                       val)
                               values (c.branch,
                                       'RNK'   ,
                                       to_char(l_rnk));
            end if;
          end;
        exception when others then
          bc.set_context;
        end;
        bc.set_context;
      end loop;
    end;

    commit;

--  гривна по всем бранчам в cur_rates$base

    migraSK.grn11tocurrates;

--  открытие счетов %% расходов

    migraSK.open_proc_drm('fin_deblockinghi - ',p_open,p_code,0,branch2_);

    bars_alerter(1);

  end fin_deblockinghi;

