  procedure open_3800_hi (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type      cur is ref cursor;
    cur_      cur;
    sql_      varchar2(4000);
    BRANCH2_  varchar2(15);
  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

/*  Открыть новые 3800/3801 бранч2, заполняя одновременно VP_LIST.
    Предлагаю маску 3800_ББББОО,  3801_ББББООВВВ,
    ББББ - код бранча
    ОО   - код ОБ22
    ВВВ  - код вал
*/
    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    sql_:='select distinct
                  substr(branch,1,15) BRANCH2
           from   S6_HI_FILIAL';

    open cur_ for sql_;

    loop

      fetch cur_ into BRANCH2_;
      exit when cur_%notfound;

      -- kv (959,961,962) + ОБ22('09')
      begin
        xM_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_hi - err(1): Ошибка xM_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

      -- kv not (959,961,962) + ОБ22('03','10','16','20')
      begin
        xV_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_hi - err(2): Ошибка xV_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

      -- Перебросить остатки и закрыть старяе лиц.счета 3800 и 3801
      begin
        xZ_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_hi - err(3): Ошибка xZ_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

    end loop;

--  toroot;

    bars_alerter(1);

  end open_3800_hi;

