                  s.BDAT                                                                                                                   BDAT    ,
                  s.PR                                                                                                                     IR      ,
                  ps.pap                                                                                                                   PAP     ,
                  n.NLSNPROC                                                                                                               NLSNPROC,
                  n.NLSDR                                                                                                                  NLSDR   ,
                  s.DADN                                                                                                                   DADN    ,
                  s.DADK                                                                                                                   DADK
           from   customerw                                             cw,
                  S6_UF_ALCOUT                                          s ,
--                (select *
--                 from   S6_UF_ALCOUT s
--                 where  (s.nls,s.kv) in (select nls,kv
--                                         from   (select nls,kv,count(*)
--                                                 from   S6_UF_ALCOUT
--                                                 group  by nls,kv
--                                                 having count(*)>1)) and
--                        nvl(s.dazs,trunc(sysdate)+36524)=
--                        (select max(nvl(dazs,trunc(sysdate)+36524))
--                         from   S6_UF_ALCOUT where nls=s.nls and kv=s.kv)
--                union all
--                 select *
--                 from   S6_UF_ALCOUT
--                 where  (nls,kv) not in (select nls,kv
--                                         from   (select nls,kv,count(*)
--                                                 from   S6_UF_ALCOUT
--                                                 group  by nls,kv
--                                                 having count(*)>1))) s ,
                  S6_UF_CLIOUT                                          c ,
                  S6_UF_ALNLSN                                          n ,
                  USERS_SKARB6_BARS                                     u ,
                  ps
           where  cw.tag(+)=''RNKUF''                                                       and
                  to_number(cw.value(+))=nvl(c.RNK,0)*10000000+'||p_filial||'               and
--                (s.DAZS is null                                                           or
--                 (s.DAZS is not null and s.DAZS>=add_months(trunc(sysdate,''YEAR''),-12)) or
--                 (s.DAZS is not null and substr(s.NLS,1,4) in (select NBS
--                                                               from   DPA_NBS)))          and
                  s.DAZS is null                                                            and
                  s.ISP=u.USKARB6(+)                                                        and
                  u.filial(+)='''||p_filial||'''                                            and
                  substr(s.NLS,1,4)=ps.nbs(+)                                               and
                  n.nls(+)=s.nls                                                            and
                  n.kv(+)=s.kv                                                              and
                  c.rnk=s.rnk                                                               and
                  (substr(s.NLS,1,1)<>''8'' or substr(s.NLS,1,2)=''89'')
           order  by substr(s.NLS,1,4) desc';
    open cur_ for sql_;

    NKD_ := null;

    LOOP

      fetch cur_ into RNK_     ,
                      DAPP_    ,
                      DAZS_    ,
                      DAOS_    ,
                      KV_      ,
                      ISP_     ,
                      NBS_     ,
                      POS_     ,
                      OSTC_    ,
                      OSTQ_    ,
                      LIM_     ,
                      OSTX_    ,
                      VID_     ,
                      NMS_     ,
                      NLS_     ,
                      NLSALT_  ,
                      BRANCH_  ,
                      OB22_    ,
                      OB88_    ,
                      P080_    ,
                      MFO_     ,
                      R020_FA_ ,
                      R011_    ,
                      R013_    ,
                      S080_    ,
                      S180_    ,
                      S181_    ,
                      S190_    ,
                      S200_    ,
                      S230_    ,
                      S240_    ,
                      S120_    ,
                      S130_    ,
                      S250_    ,
                      S031_    ,
                      S182_    ,
                      R014_    ,
                      K072_    ,
                      S090_    ,
--                    S260_    ,
                      BDAT_    ,
                      IR_      ,
                      PAP_     ,
                      NLSNPROC_,
                      NLSDR_   ,
                      DADN_    ,
                      DADK_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      fl_ := 0;
      op_ := 0;

--    определяем импортили уже счёт или нет (выборка из S6_MIGRNLS)

      begin
        select nls_bars
        into   newnls_
        from   S6_MIGRNLS
        where  nls_fil=nls_ and
               kv=kv_       and
               filial=p_filial;
      exception when no_data_found then
        newnls_ := null;
      end;

--    здесь вычисляем newnls_ на основании информации из таблиц
--    S6_UF_SHABLON, S6_UF_DEPO, S6_UF_FILIAL

      if newnls_ is null then -- будем открывать новый счёт по маске

--      1. определяем FF_, FFF_, BRANCH_ из S6_UF_FILIAL

        begin
          execute immediate 'select substr(''0000''||trim(ff),-4),
                                    substr(''000''||trim(ff),-3) ,
                                    substr(''00''||trim(ff),-2)
                             from   S6_UF_FILIAL
                             where  branch='''||branch_||''' and
                                    rownum<2'
                             into   ffff_,
                                    fff_ ,
                                    ff_;
        exception when OTHERS then
          ffff_ := substr(branch_,-5,4);
          fff_  := substr(branch_,-4,3);
          ff_   := substr(branch_,-3,2);
        end;

--      2. ищем счёт в S6_HI_DEPO, если нашли - newnls_ := vkrzn(KOTL) и ВСЁ

        begin
          execute immediate 'select kotl,
                                    ob22,
                                    branch
                             from   S6_UF_DEPO
                             where  trim(nls)='''||nls_||'''           and
                                    i_va='||to_char(kv_)||'            and
                                    substr(kotl,1,4) not in (''1001'',
                                                             ''1101'',
                                                             ''1011'') and
                                    rownum<2'
                             into   newnls_,
                                    OB22_  ,
                                    branch_;
          nms_    := 'Котловой счёт '||substr(nls_,1,4)||' ОБ22 '||OB22_;
          newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);
          goto BCE;
        exception when OTHERS then
          null;
        end;

--      3. иначе - ищем балансовый в S6_UF_SHABLON, если есть, то берём
--         соответствующую маску счёта оттуда

        begin
          execute immediate 'select maska
                             from   S6_UF_SHABLON
                             where  ((length(trim(BALS))=4 and
                                      substr('''||nls_||''',1,4)=trim(BALS)) or
                                     (length(trim(BALS))=3 and
                                      substr('''||nls_||''',1,3)=trim(BALS)) or
                                     (length(trim(BALS))=2 and
                                      substr('''||nls_||''',1,2)=trim(BALS)) or
                                     (length(trim(BALS))=1 and
                                      substr('''||nls_||''',1,1)=trim(BALS))) and
                                    rownum<2'
                             into   newnls_;
          goto MASKA;
        exception when OTHERS then
          null;
        end;

--      5. иначе - добавляем к счёту арифметически 1 (цикл) и определяем
--         первый попавший счёт, его и присваиваем в newnls_

        newnls_ := nls_;

        if substr(newnls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(newnls_,1,3)||'2'||substr(newnls_,5);
        end if;

        newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);

        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

        goto BCE; -- cчёт определен!

<<MASKA>> null; -- здесь разобрать маску СБЕРБАНКА, выродить шаблон в
                -- модификаторах БАРСа и определить счёт по ентому шаблону!

--      BBBB          - балансовый счёт    (В могут быть случайно русские)
--      GGGGGG, GGGGG - RNK
--      HHH, HH или H - номер по порядку   (Н могут быть случайно русские)
--      K             - контрольный разряд (К могут быть случайно русские)
--      FFF или FF    - код отделения
--      AAA или AA    - код ОБ22           (А могут быть случайно русские)

        newnls_ := replace(upper(newnls_),'В','B');
        newnls_ := replace(upper(newnls_),'К','K');
        newnls_ := replace(upper(newnls_),'А','A');
        newnls_ := replace(upper(newnls_),'Н','H');

        OB22_   := replace(OB22_,'А','A');
        OB22_   := replace(OB22_,'В','B');
        OB22_   := replace(OB22_,'С','C');
        OB22_   := replace(OB22_,'Н','H');
        OB22_   := replace(OB22_,'К','K');
        OB22_   := replace(OB22_,'М','M');
        OB22_   := replace(OB22_,'Е','E');
        OB22_   := replace(OB22_,'Х','X');
        OB22_   := replace(OB22_,'Р','P');
        OB22_   := replace(OB22_,'О','O');
        OB22_   := replace(OB22_,'Т','T');

        OB22aaa_:= (case when upper(substr(nvl(OB22_,'00'),1,1))>'9' then
                      to_char(ascii(upper(substr(nvl(OB22_,'00'),1,1)))-55)||
                      substr(nvl(OB22_,'00'),-1)
                    else
                      '0'||to_char(substr(nvl(OB22_,'00'),1,1))||
                      substr(nvl(OB22_,'00'),-1)
                    end);
        OB22aa_ := substr(OB22aaa_,-2);

        newnls_ := replace(upper(newnls_),'BBBB',substr(nls_,1,4));
        newnls_ := replace(upper(newnls_),'G','R');
        newnls_ := replace(upper(newnls_),'H','№');
        newnls_ := replace(upper(newnls_),'K','_');
        newnls_ := replace(upper(newnls_),'FFFF',ffff_);
        newnls_ := replace(upper(newnls_),'FFF',fff_);
        newnls_ := replace(upper(newnls_),'FF',ff_);
        newnls_ := replace(upper(newnls_),'AAA',OB22aaa_);
        newnls_ := replace(upper(newnls_),'AA',OB22aa_);

        if substr(nls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(nls_,1,3)||'2'||substr(newnls_,5);
        end if;

        toroot;
        delete
        from   nlsmask
        where  maskid='TemporaryM';

        insert
        into   nlsmask (MASKID,
                        DESCR ,
                        MASK)
                values ('TemporaryM'                           ,
                        'Временная маска для шаблона счетов СБ',
                        newnls_);
        commit;

--      получаем РЕАЛЬНЫЙ номер счёта

        tokf;
        newnls_ := f_newnls2(null               ,
                             'TemporaryM'       ,
                             substr(newnls_,1,4),
                             rnk_               ,
                             null               ,
                             kv_);
        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

<<BCE>> null;

      end if;

      bc.subst_branch(branch_); -- устанавливаем бранч для открываемого счёта

--    здесь имеем newnls_ и kv_ для открытия НОВОГО счёта или изменения старого

      if rnk_=-1 then
        bars_audit.error('open_accountUF - err(10): счёт не привязан ни к одному клиенту '||Chr(13)||Chr(10)||
                                                              'NLS = '    ||nls_   ||', ' ||Chr(13)||Chr(10)||
                                                              'NEWNLS = ' ||newnls_||', ' ||Chr(13)||Chr(10)||
                                                              'KV = '     ||kv_    ||' '  ||Chr(13)||Chr(10)||
                                                              'branch = ' ||branch_||', ' ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end if;

      begin
        select acc
        into   accR_
        from   accounts
        where  nls=newnls_ and
               kv=kv_;
      exception when no_data_found then
        accR_ := null;
                when OTHERS then
        bars_audit.error('open_accountUF - err(1): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                         'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                         'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                         'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                         'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end;

      if accR_ is null then
        ndz_ := null;
        --1. OP_REG_ex
        begin

          tip_ := 'ODB';

          if    substr(newnls_,1,3)='100' and substr(newnls_,1,4)<>'1007' then
            if kv_=980 then
              tip_ := 'KAS';
            else
              tip_ := 'KAV';
            end if;
          elsif substr(newnls_,1,3) in ('604','603','602') and kv_=980 then
              tip_ := 'SD ';
          elsif substr(newnls_,1,4) in ('2625','2605','2655') then
              tip_ := 'PK1';
          end if;

          if trunc(sysdate)<p_datx then
            begin
              insert
              into   fdat (fdat)
                   values (p_datx-4);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx-4);
          else
            begin
              insert
              into   fdat (fdat)
                   values (p_datx);
              commit;
            exception when OTHERS then
              null;
            end;
            gl.pl_dat(p_datx);
          end if;

          gl.amfo := MFOg_;

          OP_REG_exfl(99     ,  -- mod_       INTEGER,   -- Opening mode :
                      null   ,  -- p1_        INTEGER,   -- 1st Par      :
                      0      ,  -- p2_        INTEGER,   -- 2nd Par      :
                      0      ,  -- p3_        INTEGER,   -- 3rd Par (Grp):
                      ndz_   ,  -- p4_ IN OUT INTEGER,   -- 4th Par      :
                      rnk_   ,  -- rnk_       INTEGER,   -- Customer number
                      newnls_,  -- p_nls_     VARCHAR2,  -- Account  number
                      kv_    ,  -- kv_        SMALLINT,  -- Currency code
                      nms_   ,  -- nms_       VARCHAR2,  -- Account name
                      tip_   ,  -- tip_       CHAR,      -- Account type
                      isp_   ,  -- isp_       SMALLINT,
                      accR_  ,  -- accR_  OUT INTEGER,
                      '1'    ,  -- nbsnull_   VARCHAR2 DEFAULT '1',
                      null   ,  -- pap_       NUMBER   DEFAULT NULL,
                      vid_   ,  -- vid_       NUMBER   DEFAULT NULL,
                      pos_   ,  -- pos_       NUMBER   DEFAULT NULL,
                      null   ,  -- sec_       NUMBER   DEFAULT NULL,
                      null   ,  -- seci_      NUMBER   DEFAULT NULL,
                      null   ,  -- seco_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkd_      NUMBER   DEFAULT NULL,
                      99     ,  -- blkk_      NUMBER   DEFAULT NULL,
          /* lim_? */ null   ,  -- lim_       NUMBER   DEFAULT NULL,
                      null   ,  -- ostx_      VARCHAR2 DEFAULT NULL,
                      nlsalt_,  -- nlsalt_    VARCHAR2 DEFAULT NULL,
                      branch_,  -- tobo_      VARCHAR2 DEFAULT NULL,
                      null   ,  -- accc_      NUMBER   DEFAULT NULL,
                      0);
          op_ := 1;
          begin
            update accounts
            set    dapp=dapp_
            where  acc=accR_;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountUF - err(2): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                             'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                             'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                             'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                             'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                             'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
--          fl_ := 1;
          end;
          begin
            update ree_tmp
            set    ot=6
            where  nls=newnls_ and
                   kv=kv_      and
                   ot=1        and
                   fn_o is null;
          EXCEPTION WHEN OTHERS THEN
            bars_audit.error('open_accountUF - err(9): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                             'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                             'NBS = '   ||nbs_   ||', '||Chr(13)||Chr(10)||
                                             'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                             'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                             'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
          end;
--        l_ins_ := l_ins_+1;
        exception when others then
          bars_audit.error('open_accountUF - err(3): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                           'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                           'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                           'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                           'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
          p_code := p_code+1;
          fl_ := 1;
        end;
      end if;

      if fl_=0 then
        select count(*)
        into   flS6_
        from   accountsw
        where  acc=accR_ and
               tag='IMPUF';
        if flS6_=0 then
          select count(1)
          into   ugosaldoa_
          from   saldoa
          where  acc=accr_;

          if ugosaldoa_=0 then
            begin
              insert
              into   saldoa (acc ,
                             fdat,
                             pdat,
                             ostf,
                             dos ,
                             kos)
                     values (accR_   ,
                             gl.bdate,
                             dapp_   ,
                             0       ,
                             0       ,
                             0);
            EXCEPTION WHEN OTHERS THEN
              null;
            end;
          end if;
        else
          update accounts
          set    daos=daos_
          where  acc=accR_;
        end if;

        if op_=1 then
          begin
            insert
            into   accountsw (acc,
                              tag,
                              value)
                      values (accR_  ,
                              'IMPUF',
                              'Imported from UnicornF');
          exception when others then
            bars_audit.error('open_accountUF - err(4): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                             'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                             'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                             'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                             'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --2. INSERT or UPDATE SPECPARAM_INT

        if fl_=0 then
          begin
            insert
            into   SPECPARAM_INT (acc    ,
                                  p080   ,
                                  ob22   ,
                                  mfo    ,
                                  r020_fa,
                                  ob88)
                          values (accR_   ,
                                  P080_   ,
                                  OB22_   ,
                                  MFO_    ,
                                  R020_FA_,
                                  OB88_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM_INT
              set    p080=P080_      ,
                     ob22=OB22_      ,
                     mfo=MFO_        ,
                     r020_fa=R020_FA_,
                     ob88=OB88_
              where  acc=accR_;
            EXCEPTION WHEN OTHERS THEN
              bars_audit.error('open_accountUF - err(7u): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                                'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                                'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                                'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
            end;
                    WHEN OTHERS THEN
            bars_audit.error('open_accountUF - err(7): '||sqlerrm||', '||Chr(13)||Chr(10)||
                                             'branch = '||branch_||', '||Chr(13)||Chr(10)||
                                             'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                             'NEWNLS = '||newnls_||', '||Chr(13)||Chr(10)||
                                             'KV = '    ||kv_          ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
            p_code := p_code+1;
            fl_ := 1;
          end;
        end if;

        --3. INSERT or UPDATE SPECPARAM

        if DADK_ is not null then
          update accounts
          set    mdate=DADK_
          where  acc=accR_;
          if DADN_ is not null then
            S180_ := fs180(accR_,null,null,DADN_);
            begin
              select s181
              into   S181_
              from   kl_s180
              where  s180=S180_;
            exception when no_data_found then
              null;
            end;
            begin
              insert
              into   accountsw (acc,
                                tag,
                                value)
                        values (accR_ ,
                                'DADN',
                                to_char(DADN_,'DD/MM/YYYY'));
            exception when others then
              null;
            end;
          end if;
        end if;

        if fl_=0 then
          begin
            insert
            into   SPECPARAM (acc ,
                              R011,
                              R013,
                              S240,
                              S080,
                              S180,
                              S190,
                              S181,
--                            S260,
                              S200,
                              S120,
                              S031,
                              S182,
--                            S130,
                              NKD)
                      values (accR_,
                              R011_,
                              R013_,
                              S240_,
                              S080_,
                              S180_,
                              S190_,
                              S181_,
--                            S260_,
                              S200_,
                              S120_,
                              S031_,
                              S182_,
--                            S130_,
                              NKD_);
          EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
            begin
              update SPECPARAM
              set    R011=R011_,
                     R013=R013_,
                     S240=S240_,
                     S080=S080_,
                     S180=S180_,
                     S190=S190_,
                     S181=S181_,
--                   S260=S260_,
                     S200=S200_,
                     S120=S120_,
                     S031=S031_,
                     S182=S182_,
--                   S130=S130_,
                     NKD=NKD_
