      update accounts
      set    blkk=blkk_*99,
             blkd=0
      where  nls=k.nls_bars and
             kv=k.kv;
      p_open := p_open+1;
      if branch2_ is null then
        begin
          select substr(branch,1,15)
          into   branch2_
          from   accounts
          where  nls=k.nls_bars and
