--

  procedure open_3800_UF (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type      cur is ref cursor;
    cur_      cur;
    sql_      varchar2(4000);
    BRANCH2_  varchar2(15);
  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

/*  Открыть новые 3800/3801 бранч2, заполняя одновременно VP_LIST.
    Предлагаю маску 3800_ББББОО,  3801_ББББООВВВ,
    ББББ - код бранча
    ОО   - код ОБ22
    ВВВ  - код вал
*/
    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    sql_:='select distinct
                  substr(branch,1,15) BRANCH2
           from   S6_UF_FILIAL';

    open cur_ for sql_;

    loop

      fetch cur_ into BRANCH2_;
      exit when cur_%notfound;

      -- kv (959,961,962) + ОБ22('09')
      begin
        xM_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_UF - err(1): Ошибка xM_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

      -- kv not (959,961,962) + ОБ22('03','10','16','20')
      begin
        xV_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_UF - err(2): Ошибка xV_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

      -- Перебросить остатки и закрыть старяе лиц.счета 3800 и 3801
      begin
        xZ_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_UF - err(3): Ошибка xZ_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

    end loop;

--  toroot;

    bars_alerter(1);

  end open_3800_UF;

--

  procedure open_proc_drm (p_errmask in varchar2, p_open in out number, p_code in out number, p_bal number, p_branch in varchar2 default null)
  is
    --
    -- типы данных
    --
    type       t_branchrec is record (branch_id   branch.branch%type,
                                      branch_mfo  banks.mfo%type    ,
                                      branch_prnt branch.branch%type,
                                      branch_num  varchar2(3)       ,
                                      branch_rnk  customer.rnk%type);
    type       t_branchdata is table of t_branchrec;
    --
    -- константы
    --
    c_sourid   constant number(1)  := 4;
    c_baseval  constant number(3)  := 980;
    c_accgrp   constant number(38) := 26;
    c_acctype  constant char(3)    := 'ODB';
    c_accuser  constant number(38) := gl.auid;
    --
    -- переменные
    --
    l_branch   t_branchdata;
    l_accid    accounts.acc%type;
    l_accnum   accounts.nls%type;
    l_accname  accounts.nms%type;
    l_accmask  accounts.nls%type;
    l_branch2  varchar2(16);

  begin

--  p_open := 0;
--  p_code := 0;

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 disable';
    exception when OTHERS then
      null;
    end;

    l_branch2 := nvl(p_branch,'/'||f_ourmfo_g||'/______/')||'%';

    bars_context.set_context;

    select branch,
           mfo   ,
           upp   ,
           null  ,
           null
    bulk   collect
    into   l_branch
    from   (select branch                              ,
                   substr(branch,1,15)              upp,
                   bars_context.extract_mfo(branch) mfo
            from   branch
            where  branch like l_branch2)
    order  by 2,1;

    for i in 1..l_branch.count
    loop

      bars_audit.trace(p_errmask||'info(4): '||l_branch(i).branch_id||'...');

--    код ALEGRO родительского подразделения для формирования № счета
      l_branch(i).branch_num := substr(migraSK.get_alegrocode(l_branch(i).branch_prnt),1,3);
      if l_branch(i).branch_num is null then
        bars_audit.error(p_errmask||'err(3): не определён код ALEGRO (поле PODR из ASVO_FFF_PODR) для '||l_branch(i).branch_prnt);
        p_code := p_code+1;
      end if;

--    рег.№ клиента-родительского подразделения для открытия счёта
      begin
        select rnk
        into   l_branch(i).branch_rnk
        from   customer
        where  to_char(rnk)=branch_edit.getbranchparams(l_branch(i).branch_prnt,'RNK') and
               custtype in (1,2)                                                       and
               date_off is null;
      exception when no_data_found then
        bars_audit.error(p_errmask||'err(4): не найден РНК родительского подразделения для открытия счёта');
        p_code := p_code+1;
                when too_many_rows then
        bars_audit.error(p_errmask||'err(5): найдено несколько РНК родительского подразделения для открытия счёта');
        p_code := p_code+1;
      end;

      if l_branch(i).branch_rnk is not null and
         l_branch(i).branch_num is not null then

        bars_audit.trace(p_errmask||'info(5): ALEGRO № '||
                         l_branch(i).branch_num||' '||'клиент № '||
                         to_char(l_branch(i).branch_rnk));

--       bars_context.subst_branch(l_branch(i).branch_id);
         bars_context.subst_branch(l_branch(i).branch_prnt);
         for exp in (select 0                          code,
                            v.vidd                         ,
                            v.kv                           ,
                            v.tip_ost                      ,
                            v.bsd                          ,
                            v.bsn                          ,
                            (case when v.bsd='2620' then
                               '7040'
                                  when v.bsd='3320' then
                               '7052'
                                  when v.bsd='3330' then
                               '7053'
                             else
                               '7041'
                             end)                      nbs ,
                            'Проц.витр. '||v.type_name name,
                            (select substr(p.val,1,2)
                             from   dpt_vidd_params p
                             where  p.vidd=v.vidd and
                                    p.tag='DB7_OB22')  ob22
                     from   dpt_vidd v
                     where  not exists (select 1
                                        from   proc_dr$base p
                                        where  p.branch=l_branch(i).branch_id and
                                               p.nbs=v.bsd                    and
                                               p.rezid=v.vidd                 and
                                               p.sour=c_sourid                and
                                               decode(v.kv,c_baseval,p.g67,p.v67) is not null)
                     union  all
                     select 1                                             code,
                            v.vidd                                            ,
                            v.kv                                              ,
                            v.tip_ost                                         ,
                            v.bsd                                             ,
                            v.bsn                                             ,
                            (case when v.bsd='2620' then
                               '7040'
                                  when v.bsd='3320' then
                               '7052'
                                  when v.bsd='3330' then
                               '7053'
                             else
                               '7041'
                             end)                                         nbs ,
                            'Проц.витр.при дострок.розірв. '||v.type_name name,
                            (select substr(p.val,1,2)
                             from   dpt_vidd_params p
                             where  p.vidd=v.vidd and
                                    p.tag='KR7_OB22')                     ob22
                     from   dpt_vidd v
                     where  not exists (select 1
                                        from   proc_dr$base p
                                        where  p.branch=l_branch(i).branch_id and
                                               p.nbs=v.bsd                    and
                                               p.rezid=v.vidd                 and
                                               p.sour=c_sourid                and
                                               decode(v.kv,c_baseval,p.g67n,p.v67n) is not null)
                     order  by 2,1)
         loop

           if exp.ob22 is not null then

              l_accid   := null;
              l_accnum  := null;
              l_accname := null;

-- (old)      маска счета NBS + 0 + 9 + ALEGRO(-2,2) + OB22(3)
--            l_accmask := exp.nbs||'09'||l_branch(i).branch_num||migraSK.get_numob22(exp.ob22);

-- (new)      маска счета NBS + 0 + OB22(3) + ALEGRO(-3,3)
              l_accmask := exp.nbs||'0'||lpad(migraSK.get_numob22(exp.ob22),3,'0')||lpad(l_branch(i).branch_num,3,'0');

              l_accmask := vkrzn(substr(l_branch(i).branch_mfo,1,5),l_accmask);

--            поиск счета расходов по proc_dr$base для родит.подразделения
              if l_branch(i).branch_id != l_branch(i).branch_prnt then
                begin
                select a.nls
                  into   l_accnum
                  from   proc_dr$base  p,
                         accounts      a,
                         specparam_int s
                  where  p.nbs=exp.bsd                                           and
                         p.sour=c_sourid                                         and
                         p.rezid=exp.vidd                                        and
                         p.branch=l_branch(i).branch_prnt                        and
                         a.acc=s.acc                                             and
                         s.ob22=exp.ob22                                         and
                         a.nbs=exp.nbs                                           and
                         a.kv=c_baseval                                          and
                         a.branch=l_branch(i).branch_prnt                        and
                         a.nls=(case when exp.code=0 then p.g67 else p.g67n end) and
                         a.dazs is null                                          and
                         rownum<2;
                  bars_audit.trace(p_errmask||'info(6): найден счёт по proc_dr$base - '||l_accnum);
                exception when no_data_found then
                  l_accnum := null;
                  end;
              end if;

--            поиск счета расходов по бал.счету и ОБ22 для родит.подразделения
              if l_accnum is null then
                migraSK.get_account(p_errmask => p_errmask              ,
                                    p_ob22    => exp.ob22               ,
                                    p_branch  => l_branch(i).branch_prnt,
                                    p_nbs     => exp.nbs                ,
                                    p_kv      => exp.kv                 ,
                                    p_accmask => l_accmask              ,
                                    p_accnum  => l_accnum);
              end if;

              if l_accnum is not null then
                bars_audit.trace(p_errmask||'info(7): найден счёт - '||l_accnum);
              else
                if l_branch(i).branch_id!=l_branch(i).branch_prnt then
/*                данная ошибка выдается только для отделений 3-го уровня в том
                  случае, когда провалились обе попытки найти счёт расходов для
                  данного вида вклада в родительском подразделении 2-го уровня.
                  Попытка № 1. Справочник proc_dr$base не(корректно) заполнен
                  для данного вида вклада и родительского подразделения. Т.е.
                  если в proc_dr$base задать фильтр
                    nbs=exp.bsd    and
                    sour=4         and
                    rezid=exp.vidd and
                    branch=l_branch(i).branch_prnt, то
                  - не будет отобрано ни одной строки
                  или
                  - поле g67 (exp.code=0) / g67n (exp.code=1) не заполнено
                  или
                  - в поле g67 (exp.code=0) / g67n (exp.code=1) будет указан
                    счёт, но для него НЕ выполняется хотя бы одно из условий:
                        a) счёт не закрыт
                        b) счёт открыт в родительском подразделении
                        c) счёт открыт в нац.валюте
                        d) бал.счёт счёта = exp.nbs
                        e) для счёта заполнен OB22
                        f) OB22 счета = exp.ob22
                  Попытка № 2. В массиве открытых (!) в родительском
                  подразделении(!) счетов в нац.валюте:
                  - нет ни одного счёта с балансовым счётом exp.nbs и
                    OB22 = exp.ob22;
                  - есть несколько счетов с балансовым счётом exp.nbs и
                    OB22 = exp.ob22, но ни один из них не подходит ни по маске
                    счёта, ни по "хвосту" счёта (должен заканчиваться на код
                    валюты вида вклада).

                  Решение: эта ошибка - только следствие ошибки при заполнении
                  справочника для родительского подразделения, которую и нужно              exp.nbs
                  исправить!
*/
                  bars_audit.error(p_errmask||'err(6): не найден счёт расходов'                                                                  ||Chr(13)||Chr(10)||
                                              '(vidd='||exp.vidd||', ob22='||exp.ob22||', nbs='||exp.nbs||', branch='||l_branch(i).branch_id||')'||Chr(13)||Chr(10)||
                                              'для вида вклада vidd в родительском подразделении 2-го уровня'                                    ||Chr(13)||Chr(10)||
                                              '(необходимо исправить ошибку заполнения справочника для родительского подразделения)');
                  p_code := p_code+1;
                else
                  bars_audit.trace(p_errmask||'info(8): не найден счёт');
                end if;
              end if;

--            открытие счета для подразделений 1-го и 2-го уровня
              if l_accnum is null and
                l_branch(i).branch_id=l_branch(i).branch_prnt then

                l_accnum  := l_accmask;
                l_accname := trim(substr(exp.name,1,70));

                migraSK.open_account(p_errmask => p_errmask             ,
                                     p_mfo     => l_branch(i).branch_mfo,
                                     p_branch  => l_branch(i).branch_id ,
                                     p_accnum  => l_accnum              ,
                                     p_acccur  => c_baseval             ,
                                     p_accname => l_accname             ,
                                     p_custid  => l_branch(i).branch_rnk,
                                     p_ob22    => exp.ob22              ,
                                     p_accid   => l_accid               ,
                                     p_opena   => p_open                ,
                                     p_codea   => p_code);

                if l_accid is null then
                  l_accnum := null;
                end if;
              end if; -- открытие счета для подразделений 1-го и 2-го уровня

              if l_accnum is not null then
                migraSK.fill_procdr(p_errmask => p_errmask             ,
                                    p_bsd     => exp.bsd               ,
                                    p_bsn     => exp.bsn               ,
                                    p_rezid   => exp.vidd              ,
                                    p_io      => exp.tip_ost           ,
                                    p_kf      => l_branch(i).branch_mfo,
                                    p_branch  => l_branch(i).branch_id ,
                                    p_code    => exp.code              ,
                                    p_accnum  => l_accnum);
              end if;

           end if; -- ob22 is not null

         end loop; -- exp

      end if;

      commit;

    end loop; -- branch_loop

    bars_context.set_context;

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 disable';
    exception when OTHERS then
      null;
    end;

  exception when others then

    bars_context.set_context;
    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 disable';
    exception when OTHERS then
      null;
    end;
    bars_audit.error('open_proc_dr - err(7): '||sqlerrm||' '||dbms_utility.format_error_backtrace);
    p_code := p_code+1;

  end open_proc_drm;

--

  --
  -- внутр.функция поиска счета по бал.счету и ОБ22 для заданного подразделения
  --
  procedure get_account
    (p_errmask in varchar2            ,
     p_ob22    in char                ,  -- параметр ОБ22
     p_branch  in accounts.branch%type,  -- подразделение
     p_nbs     in accounts.nbs%type   ,  -- бал.счет
     p_kv      in accounts.kv%type    ,  -- валюта
     p_accmask in accounts.nls%type   ,  -- маска счета
     p_accnum out accounts.nls%type)     -- номер счета
  is
    l_attempt  number(1);
    c_baseval  constant number(3) := 980;
  begin

    l_attempt := 1;

    begin
      select a.nls
      into   p_accnum
      from   accounts      a,
             specparam_int s
      where  a.acc=s.acc       and
             s.ob22=p_ob22     and
             a.nbs=p_nbs       and
             a.kv=c_baseval    and
             a.branch=p_branch and
             a.dazs is null;
    exception when no_data_found then
      p_accnum := null;
              when too_many_rows then
      l_attempt := 2;
    end;

    if l_attempt=2 then
--    поиск по маске счета
      begin
        select a.nls
        into   p_accnum
        from   accounts      a,
               specparam_int s
        where  a.acc=s.acc       and
               s.ob22=p_ob22     and
               a.nbs=p_nbs       and
               a.kv=c_baseval    and
               a.branch=p_branch and
               a.dazs is null    and
               a.nls=p_accmask;
      exception when no_data_found or too_many_rows then
        l_attempt := 3;
      end;
    end if;

    if l_attempt=3 then
--    поиск по хвосту счета
      begin
        select a.nls
        into   p_accnum
        from   accounts      a,
               specparam_int s
        where  a.acc=s.acc       and
               s.ob22=p_ob22     and
               a.nbs=p_nbs       and
               a.kv=c_baseval    and
               a.branch=p_branch and
               a.dazs is null    and
               substr(a.nls,-length(to_char(p_kv)))=to_char(p_kv);
      exception
        when no_data_found or too_many_rows then
          l_attempt := 4;
      end;
    end if;

    if l_attempt=4 then
--    поиск самого "свежего" счета
      select max(nls)
      into   p_accnum
      from   (select (case
                      when nvl(a.dapp,a.daos)=max(nvl(a.dapp,a.daos))
                           over (partition by a.branch) then
                        a.nls
                      else
                        null
                      end) nls
              from   accounts      a,
                     specparam_int s
              where  a.acc=s.acc       and
                     s.ob22=p_ob22     and
                     a.branch=p_branch and
                     a.nbs= p_nbs      and
                     a.kv=c_baseval    and
                     a.dazs is null    and
                     substr(a.nls,-length(to_char(p_kv)))!=to_char(p_kv));
    end if;
    bars_audit.trace(p_errmask||'info(1): get - закончен поиск на попытке № '||to_char(l_attempt));

  end get_account;

--

  --
  -- внутр.процедура открытия счета и заполнения параметра OB22
  --
  procedure open_account
   (p_errmask in     varchar2            ,
    p_mfo     in     accounts.kf%type    ,
    p_branch  in     accounts.branch%type,
    p_accnum  in     accounts.nls%type   ,
    p_acccur  in     accounts.kv%type    ,
    p_accname in     accounts.nms%type   ,
    p_custid  in     accounts.rnk%type   ,
    p_ob22    in     varchar2            ,
    p_accid      out accounts.acc%type   ,
    p_opena   in out number              ,
    p_codea   in out number)
  is
    l_accid    accounts.acc%type;
    l_branch   accounts.branch%type;
    l_dazs     accounts.dazs%type;
    l_tmp      number;
    c_accgrp   constant number(38) := 26;
    c_acctype  constant char(3)    := 'ODB';
    c_accuser  constant number(38) := gl.auid;
  begin

    begin

      select acc   ,
             branch,
             dazs
      into   l_accid ,
             l_branch,
             l_dazs
      from   accounts
      where  nls=p_accnum and
             kv=p_acccur  and
             kf=p_mfo;

/*    ошибки №1 и №3 выдаются только при заполнении proc_dr$base для отделений
      2-го уровня в том случае, когда после 2-х неуспешных попыток найти счёт
      расходов для данного вида вклада в отделении 2-го уровня (см. комментарий
      выше), мы нашли счёт, номер которого = рассчитанной ранее маске, но этот
      счёт либо закрыт, либо открыт в другом подразделении
*/
      if    l_dazs is not null then
        bars_audit.error(p_errmask||'err(1): open - счёт '||p_accnum||' закрыт, необходимо "реанимировать" этот счёт или открыть другой, подобный этому');
        p_codea := p_codea+1;
        p_accid := null;
      elsif p_branch=l_branch then
        bars_audit.trace(p_errmask||'info(2): open - счёт '||p_accnum||' найден');
        p_opena := p_opena+1;
        p_accid := l_accid;
      else
        bars_audit.error(p_errmask||'err(2): open - счёт '||p_accnum||' чужой - '||l_branch||', необходимо этому счёту установить правильный бранч');
        p_codea := p_codea+1;
        p_accid := null;
      end if;

    exception when no_data_found then
      op_reg_exfl(99       ,
                  0        ,
                  0        ,
                  c_accgrp ,
                  l_tmp    ,
                  p_custid ,
                  p_accnum ,
                  p_acccur ,
                  p_accname,
                  c_acctype,
                  c_accuser,
                  l_accid  ,
                  '1'      ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  null     ,
                  p_branch ,
                  null     ,
                  0);
      p_opena := p_opena+1;
      p_accid := l_accid;
      bars_audit.trace(p_errmask||'info(3): open - счёт '||p_accnum||' открыт');
    end;

    if p_accid is not null then
      begin
        insert
        into   specparam_int (acc,
                              ob22)
                      values (p_accid,
                              p_ob22);
      exception when dup_val_on_index then
        update specparam_int
        set    ob22=p_ob22
        where  ob22 is null and
               acc=p_accid;
      end;
    end if;

  end open_account;

