  procedure fill_procdr
   (p_errmask in varchar2                ,
    p_bsd     in proc_dr$base.nbs%type   ,
    p_bsn     in proc_dr$base.nbsn%type  ,
    p_rezid   in proc_dr$base.rezid%type ,
