  function valid_string (p_str in varchar2) return varchar2
  is
    ret_  varchar2(4096);
    s_    char(1);
  begin
    ret_ := '';
    for i in 1..length(p_str)
    loop
      s_:=substr(p_str,i,1);
      if (s_>='0' and s_<='9') or
         (s_>='a' and s_<='z') or
         (s_>='A' and s_<='Z') then
        ret_:=ret_||s_;
      end if;
    end loop;
    return ret_;
  end valid_string;

