                               'GROUP_C   = '||GROUP_C_                ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
              p_code := p_code+1;
              fl_ := 1;
              bc.set_context;
            end;
          end if;
        end if;

        bc.set_context;
      end if;

      if fl_=0 then
         commit;
      else
         rollback;
      end if;

    end loop;
    close cur_;

    dbms_application_info.set_client_info(' ');

    bars_alerter(1);

  end create_customerNF;

--

  procedure open_accountNF (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    fl_         number;
    type        cur is ref cursor;
    cur_        cur;
    sql_        varchar2(32000);
    accR_       number;
    ndz_        number;
    MFOg_       varchar2(12);
    id_         number;
    ispg_       varchar(32);
    kvv_        accounts.kv%type;

    rnk_        customer.rnk%type;
    DAPP_       date;
    daos_       date;
    dazs_       date;
    kv_         accounts.kv%type;
    isp_        number;
    nbs_        char(4);
    tip_        char(3);
    vid_        number;
    ostc_       number;
    ostq_       number;
    lim_        number;
    OSTX_       number;
    nms_        varchar2(70);
    nls_        varchar2(64);
    nlsalt_     accounts.nlsalt%type;
    branch_     accounts.branch%type;
    OB22_       SPECPARAM_INT.ob22%type;
    OB88_       SPECPARAM_INT.ob88%type;
    P080_       SPECPARAM_INT.P080%type;
    MFO_        SPECPARAM_INT.MFO%type;
    R020_FA_    SPECPARAM_INT.R020_FA%type;
    R011_       SPECPARAM.R011%type;
    R013_       SPECPARAM.R013%type;
    S240_       SPECPARAM.S240%type;
    S080_       SPECPARAM.S080%type;
    S180_       SPECPARAM.S180%type;
    S190_       SPECPARAM.S190%type;
    S181_       SPECPARAM.S181%type;
    S260_       SPECPARAM.S260%type;
    S200_       SPECPARAM.S200%type;
    S230_       SPECPARAM.S230%type;
    S120_       SPECPARAM.S120%type;
    S031_       SPECPARAM.S031%type;
    S182_       SPECPARAM.S182%type;
    R014_       SPECPARAM.R014%type;
    K072_       SPECPARAM.K072%type;
    S130_       SPECPARAM.S130%type;
    S250_       SPECPARAM.S250%type;
--  S090_       date;
    NKD_        SPECPARAM.NKD%type;
    IR_         int_ratn.ir%type;
    BDAT_       int_ratn.bdat%type;
    PAP_        ps.pap%type;
    flUN_       number;
    open_       number;
    s7_         number;
    s8_         number;
    s9_         number;
    l_ins_      number;
    mrnk_       varchar2(32);

    NLSNPROC_   accounts.nls%type;
    NLSDR_      accounts.nls%type;
    acra_       accounts.acc%type;
    acrb_       accounts.acc%type;
    DADN_       date;
    DADK_       date;

    pos_        number;
    Job_        number;
    fi3_        char(3);
    op_         int;
    newnls_     accounts.nls%type;
    ffff_       char(4);
    fff_        char(3);
    ff_         char(2);
    i_          int;
    OB22aaa_    varchar2(3);
    OB22aa_     varchar2(3);
    flS6_       number;
    ugosaldoa_  int;

    SHTAR_      int;
    idshtar_    varchar2(32);
    branchugo_  accounts.branch%type;

    prc_        int;
    forsoot_    varchar2(64);

  begin

    bars_alerter(0);

    gl.frcvr := 1;

--  l_ins_ := 0;
    p_open := 0;
    p_code := 0;

    MFOg_ := f_ourmfo_g;

    execute immediate 'select substr(min(branch),12,3)
                       from   s6_nf_users'
                       into   fi3_;

    select to_char(min(id))
    into   ispg_
    from   staff$base
    where  bc.extract_mfo(branch)=MFOg_                              and
           logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%' and
           tabn is not null;
    if ispg_ is null then
      select to_char(min(id))
      into   ispg_
      from   staff$base
      where  bc.extract_mfo(branch)=MFOg_ and
             logname like 'U'||migraSK.kodobl(MFOg_)||'_'||fi3_||'_S%';
      if ispg_ is null then
        select to_char(min(id))
        into   ispg_
        from   staff$base
        where  bc.extract_mfo(branch)=MFOg_ and
               tabn is not null;
        if ispg_ is null then
          select to_char(min(id))
          into   ispg_
          from   staff$base
          where  bc.extract_mfo(branch)=MFOg_;
        end if;
      end if;
    end if;

    mrnk_ := -1;

    begin
      execute immediate 'alter trigger tbiu_specparamint_ob22 disable';
    exception when OTHERS then
      null;
    end;

    begin
      execute immediate 'alter trigger TIU_ACCOUNT67 disable';
    exception when OTHERS then
      null;
    end;

    sql_:='select distinct  /* to ACCOUNTS */
                  nvl(cw.rnk,'||mrnk_||')                                                                                                  RNK     ,
                  nullif(s.DAPP,to_date(''30.12.1899'',''DD.MM.YYYY''))                                                                    DAPP    ,
                  nullif(s.DAZS,to_date(''30.12.1899'',''DD.MM.YYYY''))                                                                    DAZS    ,
--                s.DAOS                                                                                                                   DAOS    ,
                  to_date('''||p_datx||''')-4                                                                                              daos    ,
                  s.KV                                                                                                                     KV      ,
                  nvl(u.UBARS,'||ispg_||')                                                                                                 ISP     ,
                  substr(s.NLS,1,4)                                                                                                        NBS     ,
                  1                                                                                                                        POS     ,
                  s.OSTC*100                                                                                                               OSTC    ,
                  s.OSTQ*100                                                                                                               OSTQ    ,
                  0                                                                                                                        LIM     ,
                  null                                                                                                                     OSTX    ,
                  (case when substr(s.NLS,1,2)=''25''                       then
                     11
                        when substr(s.NLS,1,4) in (''2610'',
                                                   ''2612'',
                                                   ''2615'',
                                                   ''2630'',
                                                   ''2635'',
                                                   ''2651'',
                                                   ''2652'',
                                                   ''1510'',
                                                   ''1511'',
                                                   ''1512'',
                                                   ''1515'',
                                                   ''1610'',
                                                   ''1611'',
                                                   ''1612'',
                                                   ''1615'')                then
                     4
                        when substr(s.NLS,1,4) in (''1500'',
                                                   ''1600'')                then
                     10
                        when substr(s.NLS,1,2) in (''20'',
                                                   ''21'',
                                                   ''22'',
                                                   ''27'')                  then
                     6
                        when substr(s.NLS,1,4)=''9802''                     then
                     5
                        when substr(s.NLS,1,4) in (''4310'',
                                                   ''4430'',
                                                   ''4432'',
                                                   ''4438'',
                                                   ''4439'',
                                                   ''4530'',
                                                   ''4532'',
                                                   ''4538'',
                                                   ''4539'')                then
                     8
                        when substr(s.NLS,1,4) in (select nbs from dpa_nbs) then
                     3
                   else
                     0
                   end)                                                                                                                    VID     ,
                  migraSK.valid_symbol(
                  trim(substr(nvl(s.NMS,''не определено''),1,70)))                                                                         NMS     ,
                  s.NLS                                                                                                                    NLS     ,
                  trim(substr(s.NLSALT,1,14))                                                                                              NLSALT  ,
                  s.BRANCH                                                                                                                 BRANCH  ,
                  /* to SPECPARAM_INT */
                  substr(''00''||s.OB22,-2)                                                                                                OB22    ,
                  null                                                                                                                     OB88    ,
                  s.P080                                                                                                                   P080    ,
                  null                                                                                                                     MFO     ,
                  null                                                                                                                     R020_FA ,
                  /* to SPECPARAM */
                  s.R011                                                                                                                   R011    ,
                  s.R013                                                                                                                   R013    ,
                  nullif(s.S080,''0'')                                                                                                     S080    ,
                  decode(upper(s.S180),''С'',''C'',''А'',''A'',''В'',''B'',''Е'',''E'',''Н'',''H'',''8'',''B'',''9'',''C'',upper(s.S180))  S180    ,
                  null                                                                                                                     S181    ,
                  s.S190                                                                                                                   S190    ,
                  s.S200                                                                                                                   S200    ,
                  s.S230                                                                                                                   S230    ,
                  decode(upper(s.S240),''С'',''C'',''А'',''A'',''В'',''B'',''Е'',''E'',''Н'',''H'',''8'',''B'',''9'',''C'',upper(s.S240))  S240    ,
                  s.S120                                                                                                                   S120    ,
                  s.S130                                                                                                                   S130    ,
                  s.S250                                                                                                                   S250    ,
                  s.S031                                                                                                                   S031    ,
                  s.S182                                                                                                                   S182    ,
                  s.R014                                                                                                                   R014    ,
                  s.K072                                                                                                                   K072    ,
--                s.S090                                                                                                                   S090    ,
                  s.S260                                                                                                                   S260    ,
                  nullif(s.BDAT,to_date(''30.12.1899'',''DD.MM.YYYY''))                                                                    BDAT    ,
                  s.PR                                                                                                                     IR      ,
                  ps.pap                                                                                                                   PAP     ,
                  ps.prc                                                                                                                   PRC     ,
                  n.NLSNPROC                                                                                                               NLSNPROC,
                  n.NLSDR                                                                                                                  NLSDR   ,
                  s.SHTAR                                                                                                                  SHTAR   ,
                  nullif(s.DADN,to_date(''30.12.1899'',''DD.MM.YYYY''))                                                                    DADN    ,
                  nullif(s.DADK,to_date(''30.12.1899'',''DD.MM.YYYY''))                                                                    DADK
           from   customerw                                             cw,
--                S6_NF_ALCOUT                                          s ,
                  (select *
                   from   S6_NF_ALCOUT s
                   where  (s.nls,s.kv) in (select nls,kv
                                           from   (select nls,kv,count(*)
                                                   from   S6_NF_ALCOUT
                                                   group  by nls,kv
                                                   having count(*)>1)) and
                          nvl(s.dazs,trunc(sysdate)+36524)=
                          (select max(nvl(dazs,trunc(sysdate)+36524))
                           from   S6_NF_ALCOUT where nls=s.nls and kv=s.kv)
                  union all
                   select *
                   from   S6_NF_ALCOUT
                   where  (nls,kv) not in (select nls,kv
                                           from   (select nls,kv,count(*)
                                                   from   S6_NF_ALCOUT
                                                   group  by nls,kv
                                                   having count(*)>1))) s ,
                  S6_NF_CLIOUT                                          c ,
                  S6_NF_ALNLSN                                          n ,
                  USERS_SKARB6_BARS                                     u ,
                  ps
           where  cw.tag(+)=''RNKNF''                                                      and
                  to_number(cw.value(+))=nvl(mod(c.RNK,1000000),0)*10000000+'||p_filial||' and
                  (nullif(s.DAZS,to_date(''30.12.1899'',''DD.MM.YYYY'')) is null      or
                   (nullif(s.DAZS,to_date(''30.12.1899'',''DD.MM.YYYY'')) is not null and
                    nullif(s.DAZS,to_date(''30.12.1899'',''DD.MM.YYYY''))>=
                    add_months(trunc(sysdate,''YEAR''),-12))                          or
                   (nullif(s.DAZS,to_date(''30.12.1899'',''DD.MM.YYYY'')) is not null and
                    substr(s.NLS,1,4) in (select NBS from DPA_NBS)))                       and
                  s.ISP=u.USKARB6(+)                                                       and
                  u.filial(+)='''||p_filial||'''                                           and
                  substr(s.NLS,1,4)=ps.nbs(+)                                              and
                  n.nls(+)=s.nls                                                           and
                  n.kv(+)=s.kv                                                             and
                  c.rnk=s.rnk
           order  by substr(s.NLS,1,4) desc,decode(s.kv,980,0,1)';
    open cur_ for sql_;

    NKD_ := null;

    LOOP

      fetch cur_ into RNK_     ,
                      DAPP_    ,
                      DAZS_    ,
                      DAOS_    ,
                      KV_      ,
                      ISP_     ,
                      NBS_     ,
                      POS_     ,
                      OSTC_    ,
                      OSTQ_    ,
                      LIM_     ,
                      OSTX_    ,
                      VID_     ,
                      NMS_     ,
                      NLS_     ,
                      NLSALT_  ,
                      BRANCH_  ,
                      OB22_    ,
                      OB88_    ,
                      P080_    ,
                      MFO_     ,
                      R020_FA_ ,
                      R011_    ,
                      R013_    ,
                      S080_    ,
                      S180_    ,
                      S181_    ,
                      S190_    ,
                      S200_    ,
                      S230_    ,
                      S240_    ,
                      S120_    ,
                      S130_    ,
                      S250_    ,
                      S031_    ,
                      S182_    ,
                      R014_    ,
                      K072_    ,
--                    S090_    ,
                      S260_    ,
                      BDAT_    ,
                      IR_      ,
                      PAP_     ,
                      prc_     ,
                      NLSNPROC_,
                      NLSDR_   ,
                      SHTAR_   ,
                      DADN_    ,
                      DADK_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      fl_ := 0;
      op_ := 0;

      if OSTC_<>0 and DAZS_ is not null then
        DAZS_ := null;
      end if;

--    определяем импортили уже счёт или нет (выборка из S6_MIGRNLS)

      begin
        select nls_bars
        into   newnls_
        from   S6_MIGRNLS
        where  nls_fil=nls_ and
               kv=kv_       and
               filial=p_filial;
      exception when no_data_found then
        newnls_ := null;
      end;

--    здесь вычисляем newnls_ на основании информации из таблиц
--    S6_NF_SHABLON, S6_NF_DEPO, S6_NF_FILIAL

      if newnls_ is null then -- будем открывать новый счёт по маске

--      1. определяем FF_, FFF_, BRANCH_ из S6_NF_FILIAL

        begin
          execute immediate 'select substr(''0000''||trim(ff),-4),
                                    substr(''000''||trim(ff),-3) ,
                                    substr(''00''||trim(ff),-2)
                             from   S6_NF_FILIAL
                             where  branch='''||branch_||''' and
                                    rownum<2'
                             into   ffff_,
                                    fff_ ,
                                    ff_;
        exception when OTHERS then
          ffff_ := substr(branch_,-5,4);
          fff_  := substr(branch_,-4,3);
          ff_   := substr(branch_,-3,2);
        end;

--      2. ищем счёт в S6_NF_DEPO, если нашли - newnls_ := vkrzn(KOTL) и ВСЁ

        begin
          execute immediate 'select kotl,
                                    ob22
--                                  branch
                             from   S6_NF_DEPO
                             where  trim(nls)='''||nls_||'''           and
                                    i_va='||to_char(kv_)||'            and
                                    substr(kotl,1,4) not in (''1001'',
                                                             ''1101'',
                                                             ''1011'') and
                                    rownum<2'
                             into   newnls_,
                                    OB22_;
--                                  branch_;
--        nms_    := 'Котловой счёт '||substr(nls_,1,4)||' ОБ22 '||OB22_;
          newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);
          goto BCE;
        exception when OTHERS then
--        null;
          if substr(nls_,1,2) in ('10','11') then
            forsoot_ := 'неверно';
          else
            forsoot_ := 'отсутствует';
          end if;
          bars_audit.error('open_accountNF - err(11): '||forsoot_||' соответствие в DEPO.DBF '||Chr(13)||Chr(10)||
                                                                    'NLS = '   ||nls_   ||', '||Chr(13)||Chr(10)||
                                                                    'KV = '    ||kv_    ||', '||Chr(13)||Chr(10)||
                                                                    'branch = '||branch_      ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
          p_code := p_code+1;
          fl_ := 1;
          goto notdepo;
        end;

--      3. иначе - ищем балансовый в S6_NF_SHABLON, если есть, то берём
--         соответствующую маску счёта оттуда

--      begin
--        execute immediate 'select maska
--                           from   S6_NF_SHABLON
--                           where  ((length(trim(BALS))=4 and
--                                    substr('''||nls_||''',1,4)=trim(BALS)) or
--                                   (length(trim(BALS))=3 and
--                                    substr('''||nls_||''',1,3)=trim(BALS)) or
--                                   (length(trim(BALS))=2 and
--                                    substr('''||nls_||''',1,2)=trim(BALS)) or
--                                   (length(trim(BALS))=1 and
--                                    substr('''||nls_||''',1,1)=trim(BALS))) and
--                                  rownum<2'
--                           into   newnls_;
--        goto MASKA;
--      exception when OTHERS then
--        null;
--      end;

--      5. иначе - добавляем к счёту арифметически 1 (цикл) и определяем
--         первый попавший счёт, его и присваиваем в newnls_

--      newnls_ := nls_;
--
--      if substr(newnls_,1,4) in ('1001','1101','1011') then
--        newnls_ := substr(newnls_,1,3)||'2'||substr(newnls_,5);
--      end if;
--
--      newnls_ := vkrzn(substr(MFOg_,1,5),newnls_);
--
--      i_ := 0;
--      while i_=0
--      loop
--        begin
--          select 0
--          into   i_
--          from   accounts
--          where  nls=newnls_ and
--                 kv=kv_;
--          newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
--        exception when no_data_found then
--          i_ := 1;
--        end;
--      end loop;
--
--      goto BCE; -- cчёт определен!

<<MASKA>> null; -- здесь разобрать маску СБЕРБАНКА, выродить шаблон в
                -- модификаторах БАРСа и определить счёт по ентому шаблону!

--      BBBB          - балансовый счёт    (В могут быть случайно русские)
--      GGGGGG, GGGGG - RNK
--      HHH, HH или H - номер по порядку   (Н могут быть случайно русские)
--      K             - контрольный разряд (К могут быть случайно русские)
--      FFF или FF    - код отделения
--      AAA или AA    - код ОБ22           (А могут быть случайно русские)

        newnls_ := replace(upper(newnls_),'В','B');
        newnls_ := replace(upper(newnls_),'К','K');
        newnls_ := replace(upper(newnls_),'А','A');
        newnls_ := replace(upper(newnls_),'Н','H');

        OB22_   := replace(OB22_,'А','A');
        OB22_   := replace(OB22_,'В','B');
        OB22_   := replace(OB22_,'С','C');
        OB22_   := replace(OB22_,'Н','H');
        OB22_   := replace(OB22_,'К','K');
        OB22_   := replace(OB22_,'М','M');
        OB22_   := replace(OB22_,'Е','E');
        OB22_   := replace(OB22_,'Х','X');
        OB22_   := replace(OB22_,'Р','P');
        OB22_   := replace(OB22_,'О','O');
        OB22_   := replace(OB22_,'Т','T');

        OB22aaa_:= (case when upper(substr(nvl(OB22_,'00'),1,1))>'9' then
                      to_char(ascii(upper(substr(nvl(OB22_,'00'),1,1)))-55)||
                      substr(nvl(OB22_,'00'),-1)
                    else
                      '0'||to_char(substr(nvl(OB22_,'00'),1,1))||
                      substr(nvl(OB22_,'00'),-1)
                    end);
        OB22aa_ := substr(OB22aaa_,-2);

        newnls_ := replace(upper(newnls_),'BBBB',substr(nls_,1,4));
        newnls_ := replace(upper(newnls_),'G','R');
        newnls_ := replace(upper(newnls_),'H','№');
        newnls_ := replace(upper(newnls_),'K','_');
        newnls_ := replace(upper(newnls_),'FFFF',ffff_);
        newnls_ := replace(upper(newnls_),'FFF',fff_);
        newnls_ := replace(upper(newnls_),'FF',ff_);
        newnls_ := replace(upper(newnls_),'AAA',OB22aaa_);
        newnls_ := replace(upper(newnls_),'AA',OB22aa_);

        if substr(nls_,1,4) in ('1001','1101','1011') then
          newnls_ := substr(nls_,1,3)||'2'||substr(newnls_,5);
        end if;

        toroot;
        delete
        from   nlsmask
        where  maskid='TemporaryM';

        insert
        into   nlsmask (MASKID,
                        DESCR ,
                        MASK)
                values ('TemporaryM'                           ,
                        'Временная маска для шаблона счетов СБ',
                        newnls_);
        commit;

--      получаем РЕАЛЬНЫЙ номер счёта

        tokf;
        newnls_ := f_newnls2(null               ,
                             'TemporaryM'       ,
                             substr(newnls_,1,4),
                             rnk_               ,
                             null               ,
                             kv_);
        i_ := 0;
        while i_=0
        loop
          begin
            select 0
            into   i_
            from   accounts
            where  nls=newnls_ and
                   kv=kv_;
            newnls_ := vkrzn(substr(MFOg_,1,5),to_char(to_number(newnls_)+1));
          exception when no_data_found then
            i_ := 1;
          end;
        end loop;

<<BCE>> null;

      end if;

      bc.subst_branch(branch_); -- устанавливаем бранч для открываемого счёта

--    здесь имеем newnls_ и kv_ для открытия НОВОГО счёта или изменения старого

      if rnk_=-1 then
        bars_audit.error('open_accountNF - err(10): счёт не привязан ни к одному клиенту '||Chr(13)||Chr(10)||
                                                              'NLS = '    ||nls_   ||', ' ||Chr(13)||Chr(10)||
                                                              'NEWNLS = ' ||newnls_||', ' ||Chr(13)||Chr(10)||
                                                              'KV = '     ||kv_    ||' '  ||Chr(13)||Chr(10)||
                                                              'branch = ' ||branch_||', ' ||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        fl_ := 1;
        goto kakaxer;
      end if;

      begin
        select acc,
               branch
        into   accR_,
               branchugo_
        from   accounts
        where  nls=newnls_ and
               kv=kv_;
      exception when no_data_found then
