    p_open := 0;
    p_code := 0;

    MFOg_ := f_ourmfo_g;

--  execute immediate 'select substr(min(branch),12,3)
--                     from   s6_nf_users'
--                     into   fi3_;

    for k in (select distinct
                     u2.table_name,
                     u2.constraint_name
              from   user_constraints u1,
                     user_constraints u2
              where  u2.r_constraint_name=u1.constraint_name and
                     u1.table_name='STAFF$BASE')
    loop
      begin
        execute immediate 'ALTER TABLE '||k.table_name||' MODIFY CONSTRAINT '||k.constraint_name||' DISABLE';
      exception when OTHERS then
        bars_audit.error('drop_userNF - err(0): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
      end;
    end loop;

    for k in (SELECT s.id
              from   staff$base        s,
                     USERS_SKARB6_BARS u
              where  bc.extract_mfo(s.branch)=MFOg_ and
                     u.filial=p_filial              and
                     u.ubars=s.id)

    loop
      begin
--      bars_audit.info('drop_userNF - info(1): '||k.id);
        delete
        from   USERS_SKARB6_BARS
        where  kf=MFOg_   and
               ubars=k.id and
               filial=p_filial;
        BARS_USERADM.drop_user(k.id);
--      bars_audit.info('drop_userNF - info(2): '||k.id);
        delete
        from   staff$base
        where  id=k.id;
        p_open := p_open+SQL%rowcount;
        commit;
      exception when OTHERS then
        bars_audit.error('drop_userNF - err(1): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
      end;
    end loop;

--  enable CONSTRAINTs

    for k in (select distinct
                     u2.table_name,
                     u2.constraint_name
              from   user_constraints u1,
                     user_constraints u2
              where  u2.r_constraint_name=u1.constraint_name and
                     u1.table_name='STAFF$BASE')
    loop
      begin
        execute immediate 'ALTER TABLE '||k.table_name||' MODIFY CONSTRAINT '||k.constraint_name||' ENABLE NOVALIDATE';
      exception when OTHERS then
        bars_audit.error('drop_userNF - err(2): constraint '||k.constraint_name||' '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
      end;
    end loop;

    bars_alerter(1);

  end drop_userNF;

--

  procedure load_nadF_ostc (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type     cur is ref cursor;
    cur_     cur;
    sql_     varchar2(4000);

    tt_      oper.TT%type      := '024';
    sk_      oper.SK%type      ;
    TIP_     accounts.TIP%type ;
    VOB_     oper.vob%type     := 6;
    REF_     oper.REF%type     ;
    DK_      oper.DK%type      ;
    NMST_    oper.NAM_A%type   ;
    NLST_    oper.NLSA%type    ;
    NMSS_    oper.NAM_A%type   ;
    S_       oper.S%type       ;
    SQ_      oper.SQ%type      ;
    NAZN_    oper.NAZN%type    := 'Переведення залишку з ';
    Isp_     oper.userid%type  := 20094;

    NLSS_    accounts.nls%type ;
    NLS_OLD_ accounts.nls%type ;
    KV_      accounts.kv%type  ;
    OST_     accounts.ostc%type;
    OSTQ_    accounts.ostq%type;

    c024_    number;
    pos_     accounts.pos%type ;
    accs_    accounts.acc%type ;
    daot_    date;
    f38_     char(1);

  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

    toroot;

    update tts
    set    rang=100
    where  tt=tt_;
    commit;

    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    select count(1)
    into   c024_
    from   oper
    where  tt=tt_        and
           sos=5         and
           vdat=gl.bdate and
           userid=20094  and
           nazn like 'Переведення залишку з '||p_filial||'/%';

    if c024_>0 then
      bars_audit.error('load_nadF_ostc - err(0): повторная загрузка остатков невозможна, необходим откат ...');
      p_code := p_code+1;
    goto nozagruzka;
    end if;

    sql_:='select m.nls_bars         NLSS   ,
                  o.nls              NLS_OLD,
                  o.kv               KV     ,
                  abs(o.ostc)*100    OST    ,
                  abs(o.ostq)*100    OSTQ   ,
                  (sign(o.ostc)+1)/2 DK
           from   S6_NF_ALCOUT o,
                  S6_MIGRNLS   m
           where  o.nls=m.nls_fil             and
                  o.kv=m.kv                   and
                  m.filial='''||p_filial||''' and
                  o.ostc<>0';

    open cur_ for sql_;

    loop

      fetch cur_ into NLSS_   ,
                      NLS_OLD_,
                      KV_     ,
                      OST_    ,
                      OSTQ_   ,
                      DK_;
      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        execute immediate 'select NLS_TRA
                           from   (select trim(bals)||''%'' LIK,
                                          KV                   ,
                                          to_char(transit)  NLS_TRA
                                   from   S6_NF_TRANSIT
                                   order  by length(trim(bals)) desc,
                                             trim(bals)             ,
                                             nvl(KV,0) desc)
                           where  '''||nls_old_||''' like LIK            and
                                  (nullif(KV,0) is null or '||kv_||'=KV) and
                                  rownum=1'
                           into   NLST_;
      EXCEPTION WHEN NO_DATA_FOUND THEN
        bars_audit.error('load_nadF_ostc - err(1): Не описан в TRANSIT.DBF счёт-транзит для '||p_filial||'/'||nls_old_||'('||KV_||') '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
                WHEN OTHERS THEN
        if sqlcode=-904 then
          bars_audit.error('load_nadF_ostc - err(5): старая структура TRANSIT.DBF '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        end if;
        bars_audit.error('load_nadF_ostc - err(6): '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
      end;

      begin
        select trim(substr(nms,1,38)),
               daos
        into   NMST_,
               daot_
        from   accounts
        where  kv=KV_    and
               nls=NLST_ and
               dazs is null;
      EXCEPTION WHEN NO_DATA_FOUND THEN
        bars_audit.error('load_nadF_ostc - err(2): Не найден или закрыт счёт-транзит '||nlsT_||'('||KV_||') '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
      end;

      begin
        select trim(substr(nms,1,38)),
               tip                   ,
               pos                   ,
               acc
        into   NMSS_,
               TIP_ ,
               pos_ ,
               accs_
        from   accounts
        where  kv=KV_    and
               nls=NLSS_ and
               dazs is null;
      EXCEPTION WHEN NO_DATA_FOUND THEN
        bars_audit.error('load_nadF_ostc - err(3): Не найден или закрыт НОВЫЙ счёт '||NLSS_||' для '||p_filial||'/'||nls_old_||'('||kv_||') '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto dalee;
      end;

      if TIP_='KAS' then
        sk_ := 37; -- после уточнения Житомира
      else
        sk_ := null;
      end if;

      S_  := ost_;
      sq_ := ostq_;

      if daot_>p_datx then
        update accounts
        set    daos=p_datx
        where  kv=KV_ and
               nls=NLST_;
      end if;

      if substr(NLSS_,1,4) in ('2625','2605') then

        toroot;

        select substr(flags,39,1)
        into   f38_
        from   tts
        where  tt=tt_;

        update tts
        set    flags=substr(flags,1,38)||'1'||substr(flags,40)
        where  tt=tt_;

        commit;

        tokf;

        gl.pl_dat(p_datx);
        gl.amfo := f_ourmfo_g;

      end if;

      begin
        GL.REF(REF_);
        GL.IN_DOC2(REF_,  TT_,   VOB_, REF_, SYSDATE, GL.BDATE, DK_, KV_,
                   S_,    KV_,   S_,   sq_,  sk_,     GL.BDATE, GL.BDATE,
                   NMsT_, NLST_, gl.AMFO,
                   NMSS_, NLSS_, gl.AMFO,
                   NAZN_||p_filial||'/'||NLS_OLD_,
                   NULL, null, null, null, null, 1, null, ISP_);
        GL.PAYV(0, REF_, GL.BDATE, TT_, DK_, KV_, NLST_, S_, KV_, NLSS_, S_);
        GL.PAY (2, REF_, GL.BDATE);
        commit;
        p_open := p_open+1;
      EXCEPTION WHEN others THEN
        bars_audit.error('load_nadF_ostc - err(4): Ошибка оплаты '||nlsT_||'('||KV_||') --> '||NLSS_||'('||KV_||') на сумму='||S_||' - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        rollback;
        p_code := p_code+1;
      end;

      if substr(NLSS_,1,4) in ('2625','2605') then

        toroot;

        update tts
        set    flags=substr(flags,1,38)||f38_||substr(flags,40)
        where  tt=tt_;

        commit;

        tokf;

        gl.pl_dat(p_datx);
        gl.amfo := f_ourmfo_g;

      end if;

<<dalee>> null;

    end loop;
    close cur_;

    dbms_application_info.set_client_info('pereocenka');

    migraSK.pereocenka(p_code,'load_nadF_ostc - err',p_datx);

    dbms_application_info.set_client_info(' ');

<<nozagruzka>> null;

    toroot;

    update tts
    set    rang=null
    where  tt=tt_;
    commit;

    bars_alerter(1);

  end load_nadF_ostc;

--

  procedure drop_nadF_ostc (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

--  откат документов по развороту остатков

    toroot;

    update tts
    set    flags=substr(flags,1,38)||'1'||substr(flags,40)
    where  tt='BAK';
    commit;

    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    for k in (
              select ref
              from   oper
              where  tt='024'      and
                     sos=5         and
                     vdat=gl.bdate and
                     userid=20094  and
                     nazn like 'Переведення залишку з '||p_filial||'/%')
    loop
      FOR x IN (SELECT a.acc ,
                       o.fdat,
                       o.sq
                FROM   accounts a,
                       opldok   o
                WHERE  o.ref=k.ref AND
                       a.pos=2     AND
                       o.acc=a.acc)
      LOOP
        UPDATE saldob
        SET    dos=dos-x.sq,
               kos=kos-x.sq
        WHERE  acc=x.acc AND
               fdat=x.fdat;
      END LOOP;
      ful_bak(k.ref);
      bars_audit.info('drop_nadF_ostc: ful_bak '||k.ref);
      commit;
      p_open := p_open+1;
    end loop;

    toroot;

    update tts
    set    flags=substr(flags,1,38)||'0'||substr(flags,40)
    where  tt='BAK';
    commit;

    bars_alerter(1);

  end drop_nadF_ostc;

--

  procedure fin_deblockingNF (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    l_rnk     number;
    blkk_     number;
    branch2_  varchar2(15);
    Job_      number;
    MFOg_     varchar2(12);
  begin

    bars_alerter(0);

    p_open   := 0;
    p_code   := 0;
    branch2_ := null;

    MFOg_ := f_ourmfo_g;

--  корректировка accounts по saldoa, saldob (через задание)

    BEGIN
      DBMS_JOB.SUBMIT (job       => Job_               ,
                       what      => 'correct_accounts;',
                       next_date => sysdate            ,
                       interval  => null               ,
                       no_parse  => FALSE);
    exception when others then
      bars_audit.error('fin_deblockingNF - err(J): '||sqlerrm||Chr(13)||Chr(10)||replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
    END;

    tokf;

--  разблокировка счетов: blkk и blkd с 99 на 0

    for k in (select nls_bars,
                     nls_fil ,
                     kv
              from   S6_MIGRNLS
              where  filial=p_filial)
    loop
--    begin
--      execute immediate 'select isescrow
--                         from   s6_hi_'||p_filial||'_bac
--                         where  code='''||k.nls_fil||''' and
--                                to_number(curr)='||to_char(k.kv)
--                         into   blkk_;
--      exception when others then
      blkk_ := 0;
--      end;
      update accounts
      set    blkk=blkk_*99,
             blkd=0
      where  nls=k.nls_bars and
             kv=k.kv;
      p_open := p_open+1;
      if branch2_ is null then
        begin
          select substr(branch,1,15)
          into   branch2_
          from   accounts
          where  nls=k.nls_bars and
                 kv=k.kv;
        exception when no_data_found then
          null;
        end;
      end if;
    end loop;
    commit;

    toroot;

--  удаление МФО филиала из таблиц LKL_RRP и BANK_ACC

    tokf;
    begin
      delete
      from   lkl_rrp
      where  mfo=p_filial;
      commit;
    exception when OTHERS then
      rollback;
      bars_audit.error('fin_deblockingNF - err(1) for delete from lkl_rrp: '||sqlerrm);
    end;
    begin
      delete
      from   bank_acc
      where  mfo=p_filial;
      commit;
    exception when OTHERS then
      rollback;
      bars_audit.error('fin_deblockingNF - err(2) for delete from bank_acc: '||sqlerrm);
    end;
    toroot;

--  обновление материализованного представления

    NERUXOMI;

--  добавка RNK и параметра с RNK для бранчей

    begin
      for c in (select branch,
                       name
                from   branch
                where  branch like '/'||MFOg_||'/%'
                order  by branch)
      loop
        begin
          bc.subst_branch(c.branch);
          begin
            select rnk
            into   l_rnk
            from   customer
            where  branch=c.branch and
                   notes='BRANCH<->RNK';
          exception when no_data_found then

            execute immediate 'select s_customer.nextval from dual' into l_rnk;

            insert
            into   customer (rnk      ,
                             tgr      ,
                             custtype ,
                             codcagent,
                             okpo     ,
                             country  ,
                             nmk      ,
                             nmkk     ,
                             notes    ,
                             ise      ,
                             fs       ,
                             oe       ,
                             ved      ,
                             sed      ,
                             k050     ,
                             branch)
                     values (l_rnk                    ,
                             1                        ,
                             1                        ,
                             1                        ,
                             f_ourokpo                ,
                             804                      ,
                             trim(substr(c.name,1,70)),
                             trim(substr(c.name,1,38)),
                             'BRANCH<->RNK'           ,
                             '12201'                  ,
                             '31'                     ,
                             '96130'                  ,
                             '65120'                  ,
                             '80  '                   ,
                             '610'                    ,
                             c.branch);
          end;

          begin
            update branch_parameters
            set    val=to_char(l_rnk)
            where  tag='RNK' and
                   branch=c.branch;

            if SQL%ROWCOUNT=0 then
             insert
             into   branch_parameters (branch,
                                       tag   ,
                                       val)
                               values (c.branch,
                                       'RNK'   ,
                                       to_char(l_rnk));
            end if;
          end;
        exception when others then
          bc.set_context;
        end;
        bc.set_context;
      end loop;
    end;

    commit;

--  гривна по всем бранчам в cur_rates$base

    migraSK.grn11tocurrates;

--  открытие счетов %% расходов

--  migraSK.open_proc_drm('fin_deblockingNF - ',p_open,p_code,0,branch2_);

    bars_alerter(1);

  end fin_deblockingNF;

--

  procedure open_3800_NF (p_filial in varchar2, p_open out number, p_code out number, p_datx date)
  is
    type      cur is ref cursor;
    cur_      cur;
    sql_      varchar2(4000);
    BRANCH2_  varchar2(15);
  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

/*  Открыть новые 3800/3801 бранч2, заполняя одновременно VP_LIST.
    Предлагаю маску 3800_ББББОО,  3801_ББББООВВВ,
    ББББ - код бранча
    ОО   - код ОБ22
    ВВВ  - код вал
*/
    tokf;

    begin
      insert
      into   fdat (fdat)
           values (p_datx);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_datx);
    gl.amfo := f_ourmfo_g;

    sql_:='select distinct
                  substr(branch,1,15) BRANCH2
           from   S6_NF_FILIAL';

    open cur_ for sql_;

    loop

      fetch cur_ into BRANCH2_;
      exit when cur_%notfound;

      -- kv (959,961,962) + ОБ22('09')
      begin
        xM_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_NF - err(1): Ошибка xM_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

      -- kv not (959,961,962) + ОБ22('03','10','16','20')
      begin
        xV_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_NF - err(2): Ошибка xV_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

      -- Перебросить остатки и закрыть старяе лиц.счета 3800 и 3801
      begin
        xZ_3800(gl.amfo, BRANCH2_, p_datx);
        p_open := p_open+1;
      exception when others then
        bars_audit.error('open_3800_NF - err(3): Ошибка xZ_3800 - '||sqlerrm||' '||dbms_utility.format_error_backtrace);
        p_code := p_code+1;
      end;

    end loop;

--  toroot;

    bars_alerter(1);

  end open_3800_NF;

