          l_clobtail := replace(replace(l_clob,'<motion>'),'</motion>');
          l_clob     := '';
          loop
            i := instr(l_clobtail,'</Row>');
            if nvl(i,0)>0 then
              l_clobrow := substr(l_clobtail,1,i+5);
              if nvl(length(l_clob),0)+length(l_clobrow)<23813 then
                l_clobtail := substr(l_clobtail,i+6);
                l_clob     := l_clob||l_clobrow;
              else
                l_clob := '<motion>'||l_clob||'</motion>';
                l_recbm.extend;
                l_n := l_n + 1;
                l_recbm(l_n).recbenefmotion := b64e(l_clob);
                l_clob := '';
              end if;
            else
              exit;
            end if;
          end loop;
          if nvl(length(l_clob),0)>0 then
            l_clob := '<motion>'||l_clob||'</motion>';
            l_recbm.extend;
            l_n := l_n + 1;
            l_recbm(l_n).recbenefmotion := b64e(l_clob);
          end if;
        end if;
        l_clob := k.recbm;
      end if;
    end loop;
    if nvl(length(l_clob),0)>0 then
      if length(l_clob)<23830 then
        l_recbm.extend;
        l_n := l_n + 1;
        l_recbm(l_n).recbenefmotion := b64e(l_clob);
      else
        l_clobtail := replace(replace(l_clob,'<motion>'),'</motion>');
        l_clob     := '';
        loop
          i := instr(l_clobtail,'</Row>');
          if nvl(i,0)>0 then
            l_clobrow := substr(l_clobtail,1,i+5);
            if nvl(length(l_clob),0)+length(l_clobrow)<23813 then
              l_clobtail := substr(l_clobtail,i+6);
              l_clob     := l_clob||l_clobrow;
            else
              l_clob := '<motion>'||l_clob||'</motion>';
              l_recbm.extend;
              l_n := l_n + 1;
              l_recbm(l_n).recbenefmotion := b64e(l_clob);
              l_clob := '';
            end if;
          else
            exit;
          end if;
        end loop;
        if nvl(length(l_clob),0)>0 then
          l_clob := '<motion>'||l_clob||'</motion>';
          l_recbm.extend;
          l_n := l_n + 1;
          l_recbm(l_n).recbenefmotion := b64e(l_clob);
        end if;
      end if;
    end if;

    for i in 1..l_recbm.count
    loop
--    bars_audit.info(p_errmask||'(D): record = '||substr(l_recbm(i).recbenefmotion,1,3000));
      begin
        bars_audit.info('load_comp2cen: service '||l_action||' start');
        wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                                p_action       => l_action           ,
                                p_http_method  => wsm_mgr.G_HTTP_POST,
                                p_content_type => wsm_mgr.G_CT_JSON  ,
                                p_wallet_path  => l_walett_path      , -- null
                                p_wallet_pwd   => l_walett_pass);      -- null
        wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val);
        wsm_mgr.add_parameter  (p_name => 'record'       , p_value => l_recbm(i).recbenefmotion);
        wsm_mgr.execute_request(g_response);
        l_result := g_response.cdoc; -- если есть ответ - в clob будет
        bars_audit.info('load_comp2cen: service '||l_action||' end - '||substr(l_result,1,3000));
        if (substr(l_result,1,3) in ('400','401','404','500') or
            substr(l_result,1,1)='-') and length(l_result)>3 then
          bars_audit.error(p_errmask||'(1): '||chr(13)||chr(10)||
                           replace(l_result,chr(10),chr(13)||chr(10))||'---'||
                           chr(13)||chr(10)||'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback to status1;
          return;
        else
--        bars_audit.info('load_comp2cen: service '||l_action||' test rivne = '||
--                        to_number(l_result)||', p_open = '||p_open);
          begin
            p_open := p_open+to_number(l_result);
          exception when others then
            p_open := p_open+1;
          end;
          dbms_application_info.set_client_info('open='||to_char(p_open)||
                                              ', error='||to_char(p_code));
--        commit;
        end if;
      exception when others then
        bars_audit.error(p_errmask||'(2):'||chr(13)||chr(10)||
                         replace(sqlerrm,chr(10),chr(13)||chr(10))||
                         ' - '||chr(13)||chr(10)||
                         replace(dbms_utility.format_error_backtrace,chr(10),
                         chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                         'необхідно виконати ВІДКАТ згортання...');
        p_code := p_code+1;
        rollback to status1;
        dbms_application_info.set_client_info(' ');
        return;
      end;
    end loop;

--  запуск JOBа по загрузке движения

    bars_audit.info('load_comp2cen: job load_motions start');
    BEGIN
      DBMS_JOB.SUBMIT (job       => Job_                    ,
                       what      => 'migraAS.load_motions('''||p_tvbv||''','''||f_ourmfo||''','''||l_branch||''','''||p_errmask||''');',
                       next_date => sysdate                 ,
                       interval  => null                    ,
                       no_parse  => FALSE);
    exception when others then
      bars_audit.error(p_errmask||'(Jd):'||chr(13)||chr(10)||
                       replace(sqlerrm,chr(10),chr(13)||chr(10))||
                       ' - '||chr(13)||chr(10)||
                       replace(dbms_utility.format_error_backtrace,chr(10),
                       chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                       'необхідно виконати ВІДКАТ згортання...');
      p_code := p_code+1;
      rollback to status1;
      dbms_application_info.set_client_info(' ');
      return;
    END;
    bars_audit.info('load_comp2cen: job load_motions after start');

--  сформировать проводку (или проводки) и сделать commit;

    bars_audit.info('load_comp2cen: selected accounts for wiring');
    for k in (select distinct n.nls0_depo                nls  ,
                              n.kv                            ,
                              -a.ostc                    limit,
                              trim(substr(a.nms,1,38))   nms  ,
                              a.branch                        ,
                              replace(a.ob22,'23','01')  ob22 ,
                              c.okpo
              from   asvo_nls n,
                     accounts a,
                     customer c
              where  n.tvbv=p_tvbv     and
                     a.nls=n.nls0_depo and
                     a.kv=n.kv         and
                     c.rnk=a.rnk)
    loop
      bars_audit.info('load_comp2cen: run nls = '||k.NLS);
      if k.limit>0 then
        GL.REF
             (REF_);
        dbms_application_info.set_client_info('create ref = '||to_char(REF_));
        bars_audit.info('load_comp2cen: create ref = '||to_char(REF_));
        begin
--        GL.IN_DOC3
--             (REF_ , TT_    , VOB_   , REF_   , SYSDATE, GL.BDATE, 1,
--              k.KV , k.limit, k.KV   , k.limit, NULL   , GL.BDATE, GL.BDATE,
--              nmsd_, R9910_ , gl.AMFO,
--              k.NMS, k.NLS  , gl.AMFO,
--              'Списання сум компенсаційних вкладів на ЦРКР',
--              NULL, okpod_, k.okpo, NULL, NULL, 0, NULL, null);
          GL.IN_DOC3
               (REF_ , TT_    , VOB_   , REF_   , SYSDATE, GL.BDATE, 1,
                k.KV , k.limit, k.KV   , k.limit, NULL   , GL.BDATE, GL.BDATE,
                nmsd_, R9910_ , gl.AMFO,
                k.NMS, k.NLS  , gl.AMFO,
                'Списання сум компенсаційних коштів переданих ТВБВ ('||
                p_tvbv||') '|| k.branch||' до ЦРКР',
                NULL, okpod_, k.okpo, NULL, NULL, 0, NULL, null);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(9):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;
        bars_audit.info('load_comp2cen: GL.IN_DOC3 ok '||REF_);
        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, R9910_, k.limit,
                                         k.KV, k.NLS , k.limit);
          bars_audit.info('load_comp2cen: GL.PAYV ok '||REF_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(3):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;

        begin
          GL.PAY
             (2, REF_, GL.BDATE);
          bars_audit.info('load_comp2cen: GL.PAY2 ok '||REF_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(4):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;

        begin
          insert
          into   operw (ref,
                        tag,
                        value)
                values (REF_   ,
                        'TVBV ',
                        p_tvbv); -- доп.реквизит для отката
          bars_audit.info('load_comp2cen: insert into operw ok '||REF_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(5):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;

--      для проводки в ЦА
--      передать в сервис сумму (limit),
--      nls/kv, branch, p_tvbv, date_import (справочно для записи в доп.реквизиты)
        l_action := 'makewiring';
        begin
--        bars_audit.info('load_comp2cen: nls='        ||k.nls                              );
--        bars_audit.info('load_comp2cen: kv='         ||to_char(k.kv)                      );
--        bars_audit.info('load_comp2cen: branch='     ||k.branch                           );
--        bars_audit.info('load_comp2cen: tvbv='       ||p_tvbv                             );
--        bars_audit.info('load_comp2cen: date_import='||to_char(l_date_import,'YYYY-MM-DD'));
          bars_audit.info('load_comp2cen: service '    ||l_action||' start'                 );
          dbms_application_info.set_client_info('service '||l_action||' for ob22='||k.ob22||' start');
          wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                                  p_action       => l_action           ,
                                  p_http_method  => wsm_mgr.G_HTTP_POST,
                                  p_content_type => wsm_mgr.g_ct_json  ,
                                  p_wallet_path  => l_walett_path      ,
                                  p_wallet_pwd   => l_walett_pass);
          wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val                );
          wsm_mgr.add_parameter  (p_name => 'summa'        , p_value => to_char(k.limit)                   );
          wsm_mgr.add_parameter  (p_name => 'nls'          , p_value => k.nls                              );
          wsm_mgr.add_parameter  (p_name => 'ob22'         , p_value => k.ob22                             );
          wsm_mgr.add_parameter  (p_name => 'kv'           , p_value => to_char(k.kv)                      );
          wsm_mgr.add_parameter  (p_name => 'branch'       , p_value => k.branch                           );
          wsm_mgr.add_parameter  (p_name => 'tvbv'         , p_value => p_tvbv                             );
          wsm_mgr.add_parameter  (p_name => 'date_import'  , p_value => to_char(l_date_import,'YYYY-MM-DD'));
          wsm_mgr.execute_request(g_response);
          l_result := g_response.cdoc; -- если есть ответ - в clob будет
          bars_audit.info('load_comp2cen: service '||l_action||' end - '||substr(l_result,1,3000));
          if substr(l_result,1,3) in ('400','401','404','500') and length(l_result)>3 then
            bars_audit.error(p_errmask||'(7): '||chr(13)||chr(10)||
                             replace(substr(l_result,1,3000),chr(10),chr(13)||chr(10))||
                             '---'||chr(13)||chr(10)||
                             'необхідно виконати ВІДКАТ згортання...');
            p_code := p_code+1;
            rollback;
            dbms_application_info.set_client_info(' ');
            return;
          end if;
        exception when others then
          bars_audit.error(p_errmask||'(8):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||
                           case when instr(sqlerrm,'код [')>0 then
                             '---'||chr(13)||chr(10)||
                             'необхідно виконати ВІДКАТ згортання...'
                           else
                             ''
                           end);
          p_code := p_code+1;
          dbms_application_info.set_client_info(' ');
--        rollback;
--        return;
        end;
      end if;
    end loop;
    dbms_application_info.set_client_info(' ');

--  здесь вроде-бы всё нормально, а если так, то commit;
    commit;
    bars_audit.info('load_comp2cen: end');

  end load_comp2cen;

--

  procedure load_motions (p_tvbv   in  char,
                          p_mfo    in  varchar2,
                          p_branch in  varchar2,
                          p_errmask    varchar2)
  is
    c_                   int;
    l_date_import        date;
    l_url_wapp           varchar2(256);
    l_Authorization_val  varchar2(256);
    l_walett_path        varchar2(256);
    l_walett_pass        varchar2(256);
    l_action             varchar2(32);
    g_response           wsm_mgr.t_response;
    l_result             varchar2(32767);
    l_clob               clob;
    l_n                  int;
    TYPE                 RBM is record (recbenefmotion clob);
    type                 t_RBM is table of RBM;
    l_recbm              t_RBM := t_RBM();
    l_login              varchar2(30);
    l_pass               varchar2(30);
    l_clobtail           clob;
    l_clobrow            clob;
    i                    int;
  begin

    bc.go(p_branch);

--  select count(1)
--  into   c_
--  from   V$OPEN_CURSOR c,
--         V$SESSION     s
--  where  s.status='ACTIVE' and
--         s.sid=c.sid       and
--         c.sql_text like 'SELECT 3711'||p_mfo||p_tvbv||' FROM DUAL%';

--  if c_=0 then
      execute immediate 'SELECT 3711'||p_mfo||p_tvbv||' FROM DUAL' INTO c_;

--    здесь блок из load_comp2cen (только по движению)

      begin
        execute immediate 'select branch_attribute_utl.get_attribute_value(
                                  p_branch_code    => '''||p_branch||''',
                                  p_attribute_code => ''CRKR_URL'',
                                  p_raise_expt     => 0,
                                  p_parent_lookup  => 1,
                                  p_check_exist    => 0,
                                  p_def_value      => '''')
                           from   dual'
                           into   l_url_wapp;
      exception when others then
        l_url_wapp := GetGlobalOption('CRKR_URL');
      end;
      if l_url_wapp is null then
--      l_url_wapp := 'http://url_undefined';
        l_url_wapp := 'http://10.10.10.101:9081/barsroot/api/crkr/';
      end if;
      if substr(l_url_wapp,-1)<>'/' then
        l_url_wapp := l_url_wapp||'/';
      end if;

      begin
        execute immediate 'select branch_attribute_utl.get_attribute_value(
                                  p_branch_code    => '''||p_branch||''',
                                  p_attribute_code => ''CRKR_LOGIN'',
                                  p_raise_expt     => 0,
                                  p_parent_lookup  => 1,
                                  p_check_exist    => 0,
                                  p_def_value      => '''')
                           from   dual'
                           into   l_login;
      exception when others then
        l_login := GetGlobalOption('CRKR_LOGIN');
      end;

      begin
        execute immediate 'select branch_attribute_utl.get_attribute_value(
                                  p_branch_code    => '''||p_branch||''',
                                  p_attribute_code => ''CRKR_PASS'',
                                  p_raise_expt     => 0,
                                  p_parent_lookup  => 1,
                                  p_check_exist    => 0,
                                  p_def_value      => '''')
                           from   dual'
                           into   l_pass;
      exception when others then
        l_pass := GetGlobalOption('CRKR_PASS');
      end;

      l_Authorization_val := 'Basic '||utl_raw.cast_to_varchar2(utl_encode.base64_encode(
                             utl_raw.cast_to_raw(l_login||':'||l_pass)));
--    l_Authorization_val := 'Basic YWJzYWRtOnF3ZXJ0eQ==';
--    l_Authorization_val := utl_raw.cast_to_varchar2(utl_encode.base64_encode(
--                           utl_raw.cast_to_raw(GetGlobalOption('CRKR_LOGIN')||':'||
--                           GetGlobalOption('CRKR_PASS'))));

      begin
        execute immediate 'select branch_attribute_utl.get_attribute_value(
                                  p_branch_code    => '''||p_branch||''',
                                  p_attribute_code => ''CRKR_WALLET_PATH'',
                                  p_raise_expt     => 0,
                                  p_parent_lookup  => 1,
                                  p_check_exist    => 0,
                                  p_def_value      => '''')
                           from   dual'
                           into   l_walett_path;
      exception when others then
        l_walett_path := GetGlobalOption('CRKR_WALLET_PATH');
      end;

      begin
        execute immediate 'select branch_attribute_utl.get_attribute_value(
                                  p_branch_code    => '''||p_branch||''',
                                  p_attribute_code => ''CRKR_WALLET_PASS'',
                                  p_raise_expt     => 0,
                                  p_parent_lookup  => 1,
                                  p_check_exist    => 0,
                                  p_def_value      => '''')
                           from   dual'
                           into   l_walett_pass;
      exception when others then
        l_walett_pass := GetGlobalOption('CRKR_WALLET_PASS');
      end;

--    l_action := 'add_compen';
      l_action := 'datamigration';

      l_clob   := '';
      l_n      := 0;

      for k in (select a.id         ,
                       a.date_import,
                       (select XmlElement("motion",
                               XmlAgg(
                               XmlElement("Row",
                               XMLFOREST(m.ID_COMPEN                  ,
                                         m.IDM                        ,
                                         m.SUMOP                      ,
                                         m.DK                         ,
                                         m.DATL                       ,
                                         m.TYPO                       ,
                                         m.MARK                       ,
                                         m.VER                        ,
                                         clean_string(m.STAT)     STAT,
                                         m.OST                        ,
                                         m.ZPR                        ,
                                         m.DATP                       ,
                                         clean_string(m.PREA)     PREA,
                                         lpad(int2hex(ascii(m.OI)*
                                              decode(substr(m.OI,2,1),null,1,256)+
                                              nvl(ascii(substr(m.OI,2,1)),0)),
                                              length(m.oi)*2,'0') OI  ,
                                         lpad(int2hex(ascii(m.OL)*
                                              decode(substr(m.OL,2,1),null,1,256)+
                                              nvl(ascii(substr(m.OL,2,1)),0)),
                                              length(m.ol)*2,'0') OL)))).getClobVal()
                        from   asvo_compen_motion m
                        where  m.id_compen=a.id) recbm --motion
                FROM   asvo_compen a
                WHERE  a.tvbv=p_tvbv and
                       a.status>=1)
      loop
        l_date_import := k.date_import;
        if nvl(length(l_clob),0)+length(k.recbm)<23830 then
          l_clob := l_clob||k.recbm;
        else
          if length(l_clob)<23830 then
            l_recbm.extend;
            l_n := l_n + 1;
            l_recbm(l_n).recbenefmotion := b64e(l_clob);
          else
            l_clobtail := replace(replace(l_clob,'<motion>'),'</motion>');
            l_clob     := '';
            loop
              i := instr(l_clobtail,'</Row>');
              if nvl(i,0)>0 then
                l_clobrow := substr(l_clobtail,1,i+5);
                if nvl(length(l_clob),0)+length(l_clobrow)<23813 then
                  l_clobtail := substr(l_clobtail,i+6);
                  l_clob     := l_clob||l_clobrow;
                else
                  l_clob := '<motion>'||l_clob||'</motion>';
                  l_recbm.extend;
                  l_n := l_n + 1;
                  l_recbm(l_n).recbenefmotion := b64e(l_clob);
                  l_clob := '';
                end if;
              else
                exit;
              end if;
            end loop;
            if nvl(length(l_clob),0)>0 then
              l_clob := '<motion>'||l_clob||'</motion>';
              l_recbm.extend;
              l_n := l_n + 1;
              l_recbm(l_n).recbenefmotion := b64e(l_clob);
            end if;
          end if;
          l_clob := k.recbm;
        end if;
      end loop;
      if nvl(length(l_clob),0)>0 then
        if length(l_clob)<23830 then
          l_recbm.extend;
          l_n := l_n + 1;
          l_recbm(l_n).recbenefmotion := b64e(l_clob);
        else
          l_clobtail := replace(replace(l_clob,'<motion>'),'</motion>');
          l_clob     := '';
          loop
            i := instr(l_clobtail,'</Row>');
            if nvl(i,0)>0 then
              l_clobrow := substr(l_clobtail,1,i+5);
              if nvl(length(l_clob),0)+length(l_clobrow)<23813 then
                l_clobtail := substr(l_clobtail,i+6);
                l_clob     := l_clob||l_clobrow;
              else
                l_clob := '<motion>'||l_clob||'</motion>';
                l_recbm.extend;
                l_n := l_n + 1;
                l_recbm(l_n).recbenefmotion := b64e(l_clob);
                l_clob := '';
              end if;
            else
              exit;
            end if;
          end loop;
          if nvl(length(l_clob),0)>0 then
            l_clob := '<motion>'||l_clob||'</motion>';
            l_recbm.extend;
            l_n := l_n + 1;
            l_recbm(l_n).recbenefmotion := b64e(l_clob);
          end if;
        end if;
      end if;
      for i in 1..l_recbm.count
      loop
        begin
          bars_audit.info('load motions: jservice '||l_action||' start');
          wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                                  p_action       => l_action           ,
                                  p_http_method  => wsm_mgr.G_HTTP_POST,
                                  p_content_type => wsm_mgr.G_CT_JSON  ,
                                  p_wallet_path  => l_walett_path      , -- null
                                  p_wallet_pwd   => l_walett_pass);      -- null
          wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val);
          wsm_mgr.add_parameter  (p_name => 'record'       , p_value => l_recbm(i).recbenefmotion);
          wsm_mgr.execute_request(g_response);
          l_result := g_response.cdoc; -- если есть ответ - в clob будет
          bars_audit.info('load motions: jservice '||l_action||' end - '||substr(l_result,1,3000));
          if (substr(l_result,1,3) in ('400','401','404','500') or
              substr(l_result,1,1)='-') and length(l_result)>3 then
            bars_audit.error(p_errmask||'(j1):'||chr(13)||chr(10)||
                             replace(l_result,chr(10),chr(13)||chr(10)));
            rollback;
            return;
          end if;
        exception when others then
          bars_audit.error(p_errmask||'(j2): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          rollback;
          return;
        end;
      end loop;
--  end if;

  end load_motions;

