  procedure drop_comp2cen (p_tvbv in  char    ,
                           p_open out number  ,
                           p_code out number  ,
                           p_errmask  varchar2)
  is
    TT_                  char(3);
    l_url_wapp           varchar2(256);
    l_Authorization_val  varchar2(256);
    l_walett_path        varchar2(256);
    l_walett_pass        varchar2(256);
    l_action             varchar2(32);
    g_response           wsm_mgr.t_response;
    l_result             varchar2(32767);
    l_date_import        date;
    job_                 number;
    sid_                 number;
    serial_              number;
    l_timeout            number;
    l_branch             varchar2(30);
    l_login              varchar2(30);
    l_pass               varchar2(30);
    l_timeouts           varchar2(30);
    l_num                number;
    l_str                varchar2(1024);
    l_rudel              number;
  begin

--  tokf;
    bars_audit.info('drop_comp2cen: begin');
    p_code := 0;
    p_open := 0;

    update asvo_compen
    set    status=0
    where  tvbv=p_tvbv and
           status=1;

    l_rudel := sql%rowcount;

    if l_rudel=0 then
      rollback;
      return;
    end if;

    l_branch := sys_context('bars_context','user_branch');

--  TT_ := '024';
    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_TT'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   TT_;
    exception when others then
      TT_ := GetGlobalOption('CRKR_TT');
    end;
    if trim(TT_) is null then
      TT_ := 'АСВ';
    end if;

--  убиение СВОЕГО JOBа

    bars_audit.info('drop_comp2cen: drop job');
    begin
      select job
      into   job_
      from   user_jobs
      where  what like 'migraAS.load_motions%' and
             rownum<2;
      begin
        DBMS_JOB.REMOVE(job_);
        commit;
        bars_audit.info('drop_comp2cen: job dropped');
      exception when OTHERS then
        null;
      end;
    exception when no_data_found then
      null;
    end;

    bars_audit.info('drop_comp2cen: kill session');
    begin
      select s.sid,
             s.serial#
      into   sid_,
             serial_
      from   V$OPEN_CURSOR c,
             V$SESSION     s
      where  rownum<2          and
             s.status='ACTIVE' and
             c.sid=s.sid       and
             c.sql_text like 'SELECT 3711'||f_ourmfo||p_tvbv||' FROM DUAL%';
      begin
        execute immediate 'ALTER SYSTEM KILL SESSION '''||to_char(sid_)||','||to_char(serial_)||''' IMMEDIATE';
        bars_audit.info('drop_comp2cen: kill session ok');
      exception when OTHERS then
        null;
      end;
    exception when no_data_found then
      null;
      bars_audit.info('drop_comp2cen: session for kill missing');
    end;

--  погнали откатывать

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_URL'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_url_wapp;
    exception when others then
      l_url_wapp := GetGlobalOption('CRKR_URL');
    end;
    if l_url_wapp is null then
--    l_url_wapp := 'http://url_undefined';
      l_url_wapp := 'http://10.10.10.101:9081/barsroot/api/crkr/';
    end if;
    if substr(l_url_wapp,-1)<>'/' then
      l_url_wapp := l_url_wapp||'/';
    end if;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_LOGIN'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_login;
    exception when others then
      l_login := GetGlobalOption('CRKR_LOGIN');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_PASS'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_pass;
    exception when others then
      l_pass := GetGlobalOption('CRKR_PASS');
    end;

    bars_audit.info('drop_comp2cen: l_url_wapp = '||l_url_wapp);
    l_Authorization_val := 'Basic '||utl_raw.cast_to_varchar2(utl_encode.base64_encode(
                           utl_raw.cast_to_raw(l_login||':'||l_pass)));

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_WALLET_PATH'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_walett_path;
    exception when others then
      l_walett_path := GetGlobalOption('CRKR_WALLET_PATH');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_WALLET_PASS'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_walett_pass;
    exception when others then
      l_walett_pass := GetGlobalOption('CRKR_WALLET_PASS');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_TIMEOUT'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_timeouts;
    exception when others then
      l_timeouts := GetGlobalOption('CRKR_TIMEOUT');
    end;

    if l_timeouts is not null then
      begin
        l_timeout := greatest(to_number(l_timeouts),60);
      exception when others then
        l_timeout := 300;
      end;
    else
      l_timeout := 300;
    end if;
    l_timeout := greatest(l_timeout,1200); --збільшити побільше, не встигає.

    l_action := 'dropcompen';

    begin
      bars_audit.info('service '||l_action||' start');
      utl_http.set_transfer_timeout(l_timeout);
      wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                              p_action       => l_action           ,
                              p_http_method  => wsm_mgr.G_HTTP_POST,
                              p_content_type => wsm_mgr.g_ct_json  ,
                              p_wallet_path  => l_walett_path      ,
                              p_wallet_pwd   => l_walett_pass);
      wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val);
      wsm_mgr.add_parameter  (p_name => 'tvbv'         , p_value => p_TVBV             );
--    wsm_mgr.add_parameter  (p_name => 'kf'           , p_value => f_ourmfo_g         );
      wsm_mgr.add_parameter  (p_name => 'kf'           , p_value => f_ourmfo           );
      wsm_mgr.execute_request(g_response);
      l_result := g_response.cdoc; -- если есть ответ - в clob будет
      bars_audit.info('service '||l_action||' end - '||substr(l_result,1,3000));
      if substr(l_result,1,4) in ('400 ','401 ','404 ','500 ') and length(l_result)>4 then
        bars_audit.error(p_errmask||'(1):'||chr(13)||chr(10)||
                         replace(substr(l_result,1,3000),chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        rollback;
        return;
      else
        begin
          p_open := p_open+to_number(l_result);
        exception when others then
          p_open := p_open+1;
        end;
      end if;
    exception when others then
      bars_audit.error(p_errmask||'(2):'||chr(13)||chr(10)||
                       replace(sqlerrm,chr(10),chr(13)||chr(10))||
                       ' - '||chr(13)||chr(10)||
                       replace(dbms_utility.format_error_backtrace,chr(10),chr(13)||chr(10)));
      p_code := p_code+1;
      rollback;
      return;
    end;

--  if l_rudel<>p_open or l_rudel=0 or p_open=0 then
--    rollback;
--    return;
--  end if;
--  commit;

    if p_open=0 then
      p_open := l_rudel;
    end if;

/*
    begin
      select max(date_import)
      into   l_date_import
      from   asvo_compen
      where  tvbv=p_tvbv;
    exception when others then
      l_date_import := trunc(sysdate)-14;
    end;
*/
    select nvl(min(o.pdat),trunc(sysdate)-14)
              into   l_date_import
              from   oper  o,
                     operw w
              where  o.tt=TT_                                    and
                     o.pdat>trunc(sysdate)-180                   and
                     o.sos>=1                                    and
                     substr(o.branch,2,6)=f_ourmfo               and
                     w.ref=o.ref                                 and
                     w.tag='TVBV '                               and
                     w.value=p_tvbv;

--  tokf;
    bars_audit.info('drop_comp2cen: making p_back_dok');
    for k in (select o.ref
              from   oper  o,
                     operw w
              where  w.tag='TVBV '  and
                     w.value=p_TVBV and
                     o.ref=w.ref    and
                     o.sos>0        and
                     o.tt=TT_       and
                     o.pdat>=l_date_import)
    loop
      bars_audit.info('drop_comp2cen: p_back_dok for ref = '||k.ref||' start');
--    ful_bak(k.ref);
      p_back_dok(k.ref,5,null,l_num,l_str,0);
      insert
      into   oper_visa (ref   ,
                        dat   ,
                        userid,
                        status)
                values (k.ref  ,
                        SYSDATE,
                        user_id,
                        3);
      bars_audit.info('drop_comp2cen: p_back_dok for ref = '||k.ref||' ok');
--    p_open := p_open+1;
    end loop;
    commit;
    bars_audit.info('drop_comp2cen: ending p_back_dok');

    l_action := 'dropwiring';

    begin
      bars_audit.info('service '||l_action||' start');
      wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                              p_action       => l_action           ,
                              p_http_method  => wsm_mgr.G_HTTP_POST,
                              p_content_type => wsm_mgr.g_ct_json  ,
                              p_wallet_path  => l_walett_path      ,
                              p_wallet_pwd   => l_walett_pass);
      wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val                           );
      wsm_mgr.add_parameter  (p_name => 'tvbv'         , p_value => p_TVBV                                        );
--    wsm_mgr.add_parameter  (p_name => 'kf'           , p_value => f_ourmfo_g                                    );
      wsm_mgr.add_parameter  (p_name => 'kf'           , p_value => f_ourmfo                                      );
--    wsm_mgr.add_parameter  (p_name => 'date_import'  , p_value => to_char(l_date_import,'DD/MM/YYYY HH24:MI:SS'));
      wsm_mgr.add_parameter  (p_name => 'date_import'  , p_value => to_char(l_date_import,'YYYY-MM-DD')           );
      wsm_mgr.execute_request(g_response);
      l_result := g_response.cdoc; -- если есть ответ - в clob будет
      bars_audit.info('service '||l_action||' end - '||substr(l_result,1,3000));
      if substr(l_result,1,3) in ('400','401','404','500') and length(l_result)>3 then
        bars_audit.error(p_errmask||'(3):'||chr(13)||chr(10)||
                         replace(l_result,chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
--      p_open := 0;
        rollback;
        return;
      end if;
    exception when others then
      bars_audit.error(p_errmask||'(4):'||chr(13)||chr(10)||
                       replace(sqlerrm,chr(10),chr(13)||chr(10))||' - '||
                       chr(13)||chr(10)||
                       replace(dbms_utility.format_error_backtrace,chr(10),
                       chr(13)||chr(10))||
                       case when instr(sqlerrm,'код [')>0 then
                         '---'||chr(13)||chr(10)||
                         'необхідно виправити помилку та виконати ВІДКАТ ще раз...'
                       else
                         ''
                       end);
      p_code := p_code+1;
--    p_open := 0;
      rollback;
      return;
    end;

--  здесь вроде-бы всё нормально, а если так, то commit;
    commit;
    bars_audit.info('drop_comp2cen: end');

  end drop_comp2cen;

