                         into   ASVO_TEMP_dpt_migr (dpt)
                                            values ('||to_char(otkat(k).deposit_id)||')';
      execute immediate 'insert
                         into   ASVO_TEMP_acc_migr (acc)
                                            values ('||to_char(otkat(k).acc)||')';
      execute immediate 'insert
                         into   ASVO_TEMP_acr_migr (acr)
