  procedure fix_compen  (p_tvbv in  char    ,
                         p_open out number  ,
                         p_code out number  ,
                         p_errmask  varchar2)
  is
    l_url_wapp           varchar2(256);
    l_Authorization_val  varchar2(256);
    l_walett_path        varchar2(256);
    l_walett_pass        varchar2(256);
    l_action             varchar2(32);
    g_response           wsm_mgr.t_response;
    l_result             varchar2(32767);
    l_clob               clob;
    l_timeout            number;
    l_tt                 char(3);
    l_err                varchar2(32767);
    l_branch             varchar2(30);
    l_login              varchar2(30);
    l_pass               varchar2(30);
    l_timeouts           varchar2(30);
    i                    int;
    j                    int;
    l_count              int;
  begin

    p_code := 0;
    p_open := 0;

    l_branch := sys_context('bars_context','user_branch');

--  l_tt   := '024';
    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_TT'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_tt;
    exception when others then
      l_tt := GetGlobalOption('CRKR_TT');
    end;
    if trim(l_tt) is null then
      l_tt := 'АСВ';
    end if;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_URL'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_url_wapp;
    exception when others then
      l_url_wapp := GetGlobalOption('CRKR_URL');
    end;
    if l_url_wapp is null then
--    l_url_wapp := 'http://url_undefined';
      l_url_wapp := 'http://10.10.10.101:9081/barsroot/api/crkr/';
    end if;
    if substr(l_url_wapp,-1)<>'/' then
      l_url_wapp := l_url_wapp||'/';
    end if;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_LOGIN'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_login;
    exception when others then
      l_login := GetGlobalOption('CRKR_LOGIN');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_PASS'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_pass;
    exception when others then
      l_pass := GetGlobalOption('CRKR_PASS');
    end;

    l_Authorization_val := 'Basic '||utl_raw.cast_to_varchar2(utl_encode.base64_encode(
                           utl_raw.cast_to_raw(l_login||':'||l_pass)));

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_WALLET_PATH'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_walett_path;
    exception when others then
      l_walett_path := GetGlobalOption('CRKR_WALLET_PATH');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_WALLET_PASS'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_walett_pass;
    exception when others then
      l_walett_pass := GetGlobalOption('CRKR_WALLET_PASS');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_TIMEOUT'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_timeouts;
    exception when others then
      l_timeouts := GetGlobalOption('CRKR_TIMEOUT');
    end;

    if l_timeouts is not null then
      begin
        l_timeout := greatest(to_number(l_timeouts),60);
      exception when others then
        l_timeout := 300;
      end;
    else
      l_timeout := 300;
    end if;

--  проверка, не украл ли кто-нибудь что-нибудь в базе ЦРКР

    l_action := 'verifycompen';

    begin
      bars_audit.info('service '||l_action||' start');
      utl_http.set_transfer_timeout(l_timeout);
      wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                              p_action       => l_action           ,
                              p_http_method  => wsm_mgr.G_HTTP_POST,
                              p_content_type => wsm_mgr.g_ct_json  ,
                              p_wallet_path  => l_walett_path      ,
                              p_wallet_pwd   => l_walett_pass);
      wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val);
      wsm_mgr.add_parameter  (p_name => 'mode'         , p_value => 'RU'               );
      wsm_mgr.add_parameter  (p_name => 'tvbv'         , p_value => p_TVBV             );
      wsm_mgr.add_parameter  (p_name => 'brmf'         , p_value => f_ourmfo           );
      wsm_mgr.execute_request(g_response);
      l_result := g_response.cdoc; -- если есть ответ - в clob будет
      bars_audit.info('service '||l_action||' end - '||substr(l_result,1,3000));
      if substr(l_result,1,4) in ('400 ','401 ','404 ','500 ') and length(l_result)>4 then
        bars_audit.error(p_errmask||'(1):'||chr(13)||chr(10)||
                         replace(substr(l_result,1,3000),chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        rollback;
        return;
      else
--      нарисовать код сличения людей из ЦРКР и коров из РУ
        l_result := replace(l_result,'"');
        l_err    := '';

        i := 1;
        j := 0;
        if f_ourmfo = '302076' then --для Вінниці. 01 і 23 закачувалось в різний час
          select nvl(trunc(sysdate)-min(o.vdat),0)+1
              into   j
              from   oper  o,
                     operw w
              where  o.tt=l_tt                                   and
                     o.vdat>trunc(sysdate)-180                   and
                     o.nazn like 'Списання сум компенсаційних %' and
                     o.sos>=1                                    and
                     substr(o.branch,2,6)=f_ourmfo               and
                     w.ref=o.ref                                 and
                     w.tag='TVBV '                               and
                     w.value=p_tvbv;
          else
            while i<=512
            loop
              select count(1)
              into   l_count
              from   oper  o,
                     operw w
              where  o.tt=l_tt                                   and
                     o.vdat>trunc(sysdate)-i                     and
                     o.nazn like 'Списання сум компенсаційних %' and
                     o.sos>=1                                    and
                     substr(o.branch,2,6)=f_ourmfo               and
                     w.ref=o.ref                                 and
                     w.tag='TVBV '                               and
                     w.value=p_tvbv;
              if l_count>0 then
                j := i;
                exit;
              end if;
              i := i*2;
            end loop;
        end if;

        if j>0 then
          for k in (select a.ob22                        ,
                           nvl(a.suma,0)        provodoki,
                           b.suma               vkladai  ,
                           nvl(a.suma,0)-b.suma diff
                    from   (select decode(a.ob22,'23','01',a.ob22) ob22,
                                   sum(o.s)                        suma
                            from   oper     o,
                                   operw    w,
                                   accounts a
                            where  o.tt=l_tt                                   and
                                   o.vdat>trunc(sysdate)-j /*60*/              and
                                   o.nazn like 'Списання сум компенсаційних %' and
                                   o.sos>=1                                    and
                                   substr(o.branch,2,6)=f_ourmfo               and
                                   w.ref=o.ref                                 and
                                   w.tag='TVBV '                               and
                                   w.value=p_tvbv                              and
                                   a.nls=o.nlsb                                and
                                   a.kv=980
                            group by decode(a.ob22,'23','01',a.ob22))                 a,
                           (select extractValue(value(dtl),'Rec/OB22')            ob22,
                                   to_number(extractValue(value(dtl),'Rec/SUMA')) suma
                            from   (select XMLType(l_result) xml from dual)      s,
                                   table(XMLSequence(s.xml.extract('crkr/Rec'))) dtl) b
                            where  a.ob22=b.ob22(+)
                    order by 1)
          loop
            if k.diff<>0 then
              l_err := l_err||'ОБ22='||k.ob22||', проводки: '||
                       ltrim(to_char(k.provodoki/100,'999999990D99'))||', вклади: '||
                       ltrim(to_char(k.vkladai/100,'999999990D99'))||chr(13)||chr(10);
              p_code := p_code+1;
            end if;
          end loop;
          if length(l_err)>0 then
            bars_audit.error(p_errmask||'(5) розходження при звірці:'||chr(13)||chr(10)||
                             l_err);
            return;
          end if;
        else
          bars_audit.error(p_errmask||'(6): фіксація неможлива,'||chr(13)||chr(10)||
                           'відсутні проводки списання сум компенсаційних коштів'||
                           chr(13)||chr(10)||'(можливо не виконаний п. "Згортання...")');
          p_code := p_code+1;
          return;
        end if;
      end if;
    exception when others then
      bars_audit.error(p_errmask||'(2):'||chr(13)||chr(10)||
                       replace(sqlerrm,chr(10),chr(13)||chr(10))||
                       ' - '||chr(13)||chr(10)||
                       replace(dbms_utility.format_error_backtrace,chr(10),
                       chr(13)||chr(10)));
      p_code := p_code+1;
      rollback;
      return;
    end;

    l_action := 'fixcompen';

    begin
      bars_audit.info('service '||l_action||' start');
      utl_http.set_transfer_timeout(l_timeout);
      wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                              p_action       => l_action           ,
                              p_http_method  => wsm_mgr.G_HTTP_POST,
                              p_content_type => wsm_mgr.g_ct_json  ,
                              p_wallet_path  => l_walett_path      ,
                              p_wallet_pwd   => l_walett_pass);
      wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val);
      wsm_mgr.add_parameter  (p_name => 'tvbv'         , p_value => p_TVBV             );
      wsm_mgr.add_parameter  (p_name => 'kf'           , p_value => f_ourmfo           );
      wsm_mgr.execute_request(g_response);
      l_result := g_response.cdoc; -- если есть ответ - в clob будет
      bars_audit.info('service '||l_action||' end - '||substr(l_result,1,3000));
      if substr(l_result,1,4) in ('400 ','401 ','404 ','500 ') and length(l_result)>4 then
        bars_audit.error(p_errmask||'(3):'||chr(13)||chr(10)||
                         replace(substr(l_result,1,3000),chr(10),chr(13)||chr(10)));
        p_code := p_code+1;
        rollback;
        return;
      else
        bars_audit.info('service '||l_action||' end '||l_result);
        begin
          p_open := p_open+to_number(l_result);
        exception when others then
          p_open := p_open+1;
        end;
      end if;
      update asvo_compen
      set    status=2
      where  tvbv=p_tvbv and
             status=1;
      p_open := greatest(p_open,sql%rowcount);
    exception when others then
      bars_audit.error(p_errmask||'(4):'||chr(13)||chr(10)||
                       replace(sqlerrm,chr(10),chr(13)||chr(10))||
                       ' - '||chr(13)||chr(10)||
                       replace(dbms_utility.format_error_backtrace,chr(10),
                       chr(13)||chr(10)));
      p_code := p_code+1;
      rollback;
      return;
    end;
    commit;

  end fix_compen;

