                        p_errmask     varchar2,
                        p_dfNOT       varchar2)
  is

    fff_    char(3);
    cnt_    number;

    type    cur is ref cursor;
    cur_    cur;
    sql_    varchar2(1024);

    kknot_  varchar2(18);

  begin

