--

  procedure load_ostcdAS65 (p_tvbv in  char    ,
                            p_open out number  ,
                            p_code out number  ,
                            p_errmask  varchar2,
                            p_dasvox   date)
  is
    TT_        oper.TT%type  :='АСВ';
    VOB_       oper.VOB%type :=6;
    REF_       oper.REF%type;
    KOTLD_     varchar2(14);
    KOTLN_     varchar2(14);
    KOTLZ_     varchar2(14);
    sumOSTD_   number;
    sumNPRD_   number;
    sumZPRD_   number;
    nlsNP_     varchar2(14);
    nmkd_      varchar2(38);
    nmsd_      varchar2(38);
    nmsn_      varchar2(38);
    nmsz_      varchar2(38);
    branch_    varchar2(32);
    kktokens_  varchar2_list;
    text_      varchar2(512);
    pd_        int;
    pn_        int;
    pz_        int;
    daos_      date;
    acc_       accounts.acc%type;

  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;
    pd_    := 0;
    pn_    := 0;
    pz_    := 0;

    select branch
    into   branch_
    from   ASVO_FFF_BRANCH
    where  FFF=p_tvbv and
           rownum<2;

--  bars_audit.info('load_ostcdptAS - @ branch_='||branch_);

--  депозиты

    bc.subst_branch(branch_);

    begin
      insert
      into   fdat (fdat)
           values (p_dasvox);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_dasvox);
    gl.amfo := f_ourmfo_g;

--  if gl.bdate is null then
--    gl.bdate := bankdate_g;
--  end if;

    for k in (select a.nls                 ,
                     a.kv                  ,
                     substr(a.nms,1,38) nms,
                     d.acc                 ,
                     d.limit               ,
                     d.deposit_id          ,
                     d.vidd                ,
                     d.nd                  ,
                     d.rnk
              from   dpt_deposit d,
                     accounts    a
              where  d.comments='Imported from ASVO6.5' and
                     d.acc=a.acc                        and
                     d.branch=branch_                   and
                     length(d.nd)-length(replace(d.nd,'_'))>=4)
    loop

--    bars_audit.info('load_ostcdptAS - 0 k.acc='||k.acc||', k.kv='||k.kv);

      begin
        select value
        into   KOTLD_
        from   accountsw
        where  tag='KOTLD' and acc=k.acc;
      exception when no_data_found then
        bars_audit.error(p_errmask||'(10): не найден котловой счёт для вклада '||
                         k.nd||' '||k.nls||'('||k.kv||') - '||sqlerrm||' '||
                         dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto nofound65;
      end;

      begin
        select trim(substr(nms,1,38)),
               daos                  ,
               acc
        into   nmsd_,
               daos_,
               acc_
        from   accounts
        where  nls=KOTLD_ and
               kv=k.kv    and
               DAZS is null;
      exception when OTHERS then
        if pd_=0 then
          bars_audit.error(p_errmask||'(1): не найден или закрыт счёт '||KOTLD_||'('||k.kv||') - '||
                           sqlerrm||' '||dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          pd_ := 1;
        end if;
--      rollback;
        goto nofound65;
      end;

      if daos_>p_dasvox then
        update accounts
        set    daos=p_dasvox
        where  acc=acc_;
      end if;

--    bars_audit.info('load_ostcdptAS - 1 k.acc='||k.acc);

