        rollback;
        goto nofound65;
      end;

--    bars_audit.info('load_ostcdptAS - 4');

      begin
        select to_number(value)
        into   sumNPRD_
        from   accountsw
        where  tag='NPR_D' and
               acc=k.acc;
      exception when OTHERS then
        sumNPRD_ := 0;
      end;

      if sumNPRD_>0 then

        select value
        into   KOTLN_
        from   accountsw
        where  tag='KOTLN' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
          into   nmsn_,
                 daos_,
                 acc_
          from   accounts
          where  nls=KOTLN_ and
                 kv=k.kv    and
