        exception when OTHERS then
          if pn_=0 then
            bars_audit.error(p_errmask||'(2): не найден или закрыт счёт '||KOTLN_||'('||k.kv||') - '||
                             sqlerrm||' '||dbms_utility.format_error_backtrace);
            p_code := p_code+1;
            pn_ := 1;
          end if;
          rollback;
          goto nofound65;
        end;

        if daos_>p_dasvox then
          update accounts
          set    daos=p_dasvox
          where  acc=acc_;
        end if;

        select a.nls
        into   nlsNP_
        from   accounts a,
               int_accn i
        where  i.acc=k.acc and
               id=1        and
               i.acra=a.acc;

--      bars_audit.info('load_ostcdptAS - 5a');

        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, KOTLN_, sumNPRD_,
                                         k.KV, nlsNP_, sumNPRD_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(6): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          rollback;
          goto nofound65;
        end;
      end if;

      if sumNPRD_<0 then

        select value
        into   KOTLN_
        from   accountsw
        where  tag='KOTLN' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
          into   nmsn_,
                 daos_,
                 acc_
          from   accounts
          where  nls=KOTLN_ and
                 kv=k.kv    and
                 DAZS is null;
        exception when OTHERS then
          if pn_=0 then
            bars_audit.error(p_errmask||'(3): не найден или закрыт счёт '||KOTLN_||'('||k.kv||') - '||
                             sqlerrm||' '||dbms_utility.format_error_backtrace);
            p_code := p_code+1;
            pn_ := 1;
          end if;
          rollback;
          goto nofound65;
        end;

        if daos_>p_dasvox then
          update accounts
          set    daos=p_dasvox
          where  acc=acc_;
        end if;

        select a.nls
        into   nlsNP_
        from   accounts a,
               int_accn i
        where  i.acc=k.acc and
               id=1        and
               i.acrb=a.acc;

--      bars_audit.info('load_ostcdptAS - 5b');

        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 0, k.KV, KOTLN_, -sumNPRD_,
                                         k.KV, nlsNP_, -sumNPRD_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(7): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          rollback;
          goto nofound65;
        end;

      end if;

--    bars_audit.info('load_ostcdptAS - 6  k.acc='||k.acc);

      begin
        select to_number(value)
        into   sumZPRD_
        from   accountsw
        where  tag='ZPR_D' and
               acc=k.acc;
      exception when OTHERS then
        sumZPRD_ := 0;
      end;

      if sumZPRD_>0 then

        select value
        into   KOTLZ_
        from   accountsw
        where  tag='KOTLZ' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
          into   nmsz_,
                 daos_,
                 acc_
          from   accounts
          where  nls=KOTLZ_ and
                 kv=k.kv    and
                 DAZS is null;
        exception when OTHERS then
          if pz_=0 then
            bars_audit.error(p_errmask||'(4): не найден или закрыт счёт '||KOTLZ_||'('||k.kv||') - '||
                             sqlerrm||' '||dbms_utility.format_error_backtrace);
            p_code := p_code+1;
            pz_ := 1;
          end if;
          rollback;
          goto nofound65;
        end;

        if daos_>p_dasvox then
          update accounts
          set    daos=p_dasvox
          where  acc=acc_;
        end if;

        select a.nls
        into   nlsNP_
        from   accounts a,
               int_accn i
        where  i.acc=k.acc and
               id=1        and
               i.acra=a.acc;

--      bars_audit.info('load_ostcdptAS - 7');

        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, KOTLZ_, sumZPRD_,
                                         k.KV, nlsNP_, sumZPRD_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(8): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          rollback;
          goto nofound65;
        end;

      end if;

--    bars_audit.info('load_ostcdptAS - 8');

      begin
