          execute immediate 'delete from DPT_DEPOSIT_CLOS   where RNK            in (select rnk from ASVO_TEMP_rnk_migr)';
--        execute immediate 'delete from DPT_INHERITORS     where INHERIT_CUSTID in (select rnk from ASVO_TEMP_rnk_migr)';
