  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;
    pd_    := 0;
    pn_    := 0;
    pz_    := 0;

    select branch
    into   branch_
    from   ASVO_FFF_BRANCH
    where  FFF=p_tvbv and
           rownum<2;

--  bars_audit.info('load_ostcdMEGA - @ branch_='||branch_);

--  депозиты

    bc.subst_branch(branch_);

    begin
      insert
      into   fdat (fdat)
           values (p_dasvox);
      commit;
    exception when OTHERS then
      null;
