      end if;

      if sumNPRD_<0 then

        select value
        into   KOTLN_
        from   accountsw
        where  tag='KOTLN' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
