  procedure copy_SBON_tables (p_tvbv in char)
  is
  begin

    if p_tvbv is not null and length(p_tvbv)=3 then
      begin
        execute immediate 'drop table ASVO_SBON_'||p_tvbv;
      exception when OTHERS then
