--

  procedure load_ostcdSBON (p_tvbv in  char    ,
                            p_open out number  ,
                            p_code out number  ,
                            p_errmask  varchar2,
                            p_dasvox   date)
  is
    TT_        oper.TT%type  :='АСВ';
    VOB_       oper.VOB%type :=6;
    REF_       oper.REF%type;
    KOTLD_     varchar2(14);
    KOTLN_     varchar2(14);
    KOTLZ_     varchar2(14);
    sumOSTD_   number;
    sumNPRD_   number;
    sumZPRD_   number;
    nlsNP_     varchar2(14);
    nmkd_      varchar2(38);
    nmsd_      varchar2(38);
    nmsn_      varchar2(38);
    nmsz_      varchar2(38);
    branch_    varchar2(32);
    kktokens_  varchar2_list;
    text_      varchar2(512);
    pd_        int;
    pn_        int;
    pz_        int;
    daos_      date;
    acc_       accounts.acc%type;

  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;
    pd_    := 0;
    pn_    := 0;
    pz_    := 0;

    select branch
    into   branch_
    from   ASVO_FFF_BRANCH
    where  FFF=p_tvbv and
           rownum<2;

--  bars_audit.info('load_ostcdSBON - @ branch_='||branch_);

--  депозиты

    bc.subst_branch(branch_);

    begin
      insert
      into   fdat (fdat)
           values (p_dasvox);
      commit;
    exception when OTHERS then
      null;
    end;

    gl.pl_dat(p_dasvox);
    gl.amfo := f_ourmfo_g;

    for k in (select a.nls                 ,
                     a.kv                  ,
                     substr(a.nms,1,38) nms,
                     d.acc                 ,
                     d.limit               ,
                     d.deposit_id          ,
                     d.vidd                ,
                     d.nd                  ,
                     d.rnk
              from   dpt_deposit d,
                     accounts    a
              where  d.comments='Imported from SBON' and
                     d.acc=a.acc                     and
                     d.branch=branch_                and
                     length(d.nd)-length(replace(d.nd,'_'))>=4)
    loop

--    bars_audit.info('load_ostcdSBON - 0 k.acc='||k.acc||', k.kv='||k.kv);

      begin
        select value
        into   KOTLD_
        from   accountsw
        where  tag='KOTLD' and acc=k.acc;
      exception when no_data_found then
        bars_audit.error(p_errmask||'(10): не найден котловой счёт для вклада '||
                         k.nd||' '||k.nls||'('||k.kv||') - '||sqlerrm||' '||
                         dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        goto nofoundSBON;
      end;

      begin
        select trim(substr(nms,1,38)),
               daos                  ,
               acc
        into   nmsd_,
               daos_,
               acc_
        from   accounts
        where  nls=KOTLD_ and
               kv=k.kv    and
               DAZS is null;
      exception when OTHERS then
        if pd_=0 then
          bars_audit.error(p_errmask||'(1): не найден или закрыт счёт '||KOTLD_||'('||k.kv||') - '||
                           sqlerrm||' '||dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          pd_ := 1;
        end if;
--      rollback;
        goto nofoundSBON;
      end;

      if daos_>p_dasvox then
        update accounts
        set    daos=p_dasvox
        where  acc=acc_;
      end if;

--    bars_audit.info('load_ostcdSBON - 1 k.acc='||k.acc);

      GL.REF
           (REF_);

--    bars_audit.info('load_ostcdSBON - 2');

      GL.IN_DOC3
           (REF_ , TT_    , VOB_   , REF_   , SYSDATE, GL.BDATE, 1,
            k.KV , k.limit, k.KV   , k.limit, NULL   , GL.BDATE, GL.BDATE,
            nmsd_, KOTLD_ , gl.AMFO,
            k.NMS, k.NLS  , gl.AMFO,
            'Розкриття депозитного рахунку з МЕГАБАНК',
            NULL, null, null, NULL, NULL, 0, NULL, null);

--    bars_audit.info('load_ostcdSBON - 3');

      begin
        GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, KOTLD_, k.limit,
                                         k.KV, k.NLS , k.limit);
      exception when OTHERS then
        bars_audit.error(p_errmask||'(5): '||sqlerrm||' - '||
                         dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        rollback;
        goto nofoundSBON;
      end;

--    bars_audit.info('load_ostcdSBON - 4');

      begin
        select to_number(value)
        into   sumNPRD_
        from   accountsw
        where  tag='NPR_D' and
               acc=k.acc;
      exception when OTHERS then
        sumNPRD_ := 0;
      end;

      if sumNPRD_>0 then

        select value
        into   KOTLN_
        from   accountsw
        where  tag='KOTLN' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
          into   nmsn_,
                 daos_,
                 acc_
          from   accounts
          where  nls=KOTLN_ and
                 kv=k.kv    and
                 DAZS is null;
        exception when OTHERS then
          if pn_=0 then
            bars_audit.error(p_errmask||'(2): не найден или закрыт счёт '||KOTLN_||'('||k.kv||') - '||
                             sqlerrm||' '||dbms_utility.format_error_backtrace);
            p_code := p_code+1;
            pn_ := 1;
          end if;
          rollback;
          goto nofoundSBON;
        end;

        if daos_>p_dasvox then
          update accounts
          set    daos=p_dasvox
          where  acc=acc_;
        end if;

        select a.nls
        into   nlsNP_
        from   accounts a,
               int_accn i
        where  i.acc=k.acc and
               id=1        and
               i.acra=a.acc;

--      bars_audit.info('load_ostcdSBON - 5a');

        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, KOTLN_, sumNPRD_,
                                         k.KV, nlsNP_, sumNPRD_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(6): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          rollback;
          goto nofoundSBON;
        end;
      end if;

      if sumNPRD_<0 then

        select value
        into   KOTLN_
        from   accountsw
        where  tag='KOTLN' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
          into   nmsn_,
                 daos_,
                 acc_
          from   accounts
          where  nls=KOTLN_ and
                 kv=k.kv    and
                 DAZS is null;
        exception when OTHERS then
          if pn_=0 then
            bars_audit.error(p_errmask||'(3): не найден или закрыт счёт '||KOTLN_||'('||k.kv||') - '||
                             sqlerrm||' '||dbms_utility.format_error_backtrace);
            p_code := p_code+1;
            pn_ := 1;
          end if;
          rollback;
          goto nofoundSBON;
        end;

        if daos_>p_dasvox then
          update accounts
          set    daos=p_dasvox
          where  acc=acc_;
        end if;

        select a.nls
        into   nlsNP_
        from   accounts a,
               int_accn i
        where  i.acc=k.acc and
               id=1        and
               i.acrb=a.acc;

--      bars_audit.info('load_ostcdSBON - 5b');

        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 0, k.KV, KOTLN_, -sumNPRD_,
                                         k.KV, nlsNP_, -sumNPRD_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(7): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          rollback;
          goto nofoundSBON;
        end;

      end if;

--    bars_audit.info('load_ostcdSBON - 6  k.acc='||k.acc);

      begin
        select to_number(value)
        into   sumZPRD_
        from   accountsw
        where  tag='ZPR_D' and
               acc=k.acc;
      exception when OTHERS then
        sumZPRD_ := 0;
      end;

      if sumZPRD_>0 then

        select value
        into   KOTLZ_
        from   accountsw
        where  tag='KOTLZ' and
               acc=k.acc;
        begin
          select trim(substr(nms,1,38)),
                 daos                  ,
                 acc
          into   nmsz_,
                 daos_,
                 acc_
          from   accounts
          where  nls=KOTLZ_ and
                 kv=k.kv    and
                 DAZS is null;
        exception when OTHERS then
          if pz_=0 then
            bars_audit.error(p_errmask||'(4): не найден или закрыт счёт '||KOTLZ_||'('||k.kv||') - '||
                             sqlerrm||' '||dbms_utility.format_error_backtrace);
            p_code := p_code+1;
            pz_ := 1;
          end if;
          rollback;
          goto nofoundSBON;
        end;

        if daos_>p_dasvox then
          update accounts
          set    daos=p_dasvox
          where  acc=acc_;
        end if;

        select a.nls
        into   nlsNP_
        from   accounts a,
               int_accn i
        where  i.acc=k.acc and
               id=1        and
               i.acra=a.acc;

--      bars_audit.info('load_ostcdSBON - 7');

        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, KOTLZ_, sumZPRD_,
                                         k.KV, nlsNP_, sumZPRD_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(8): '||sqlerrm||' - '||
                           dbms_utility.format_error_backtrace);
          p_code := p_code+1;
          rollback;
          goto nofoundSBON;
        end;

      end if;

--    bars_audit.info('load_ostcdSBON - 8');

      begin
        GL.PAY
             (2, REF_, GL.BDATE);
      exception when OTHERS then
        bars_audit.error(p_errmask||'(9): '||sqlerrm||' - '||
                         dbms_utility.format_error_backtrace);
        p_code := p_code+1;
        rollback;
        goto nofoundSBON;
      end;

--    bars_audit.info('load_ostcdSBON - 9');

      insert
      into   operw (ref,
                    tag,
                    value)
            values (REF_   ,
                    'ND   ',
                    k.deposit_id);

      update dpt_deposit
      set    comments='*Imported from SBON'
      where  deposit_id=k.deposit_id;

      delete
      from   oper_ext
      where  ref=REF_;

      commit;

--    bars_audit.info('load_ostcdSBON - k.nd='||k.nd);

      migraAS.get_tokens(k.nd,'_',kktokens_);
      text_ := 'Счёт депозита загружен';
      migraAS.asvo_log_write(kktokens_(1),
                             k.vidd      ,
                             k.kv        ,
                             p_tvbv      ,
                             null        ,
                             branch_     ,
                             KOTLD_      ,
                             KOTLN_      ,
                             KOTLZ_      ,
                             kktokens_(3),
                             kktokens_(4),
                             null        ,
                             null        ,
                             null        ,
                             null        ,
                             k.limit     ,
                             null        ,
                             sumNPRD_    ,
                             sumZPRD_    ,
                             1           ,
                             text_       ,
                             sysdate     ,
                             k.rnk       ,
                             k.deposit_id);
      p_open := p_open+1;

<<nofoundSBON>> null;

      dbms_application_info.set_client_info('load='||to_char(p_open)||
                                         ', error='||to_char(p_code));
    end loop;

--  dbms_application_info.set_client_info('pereocenka');

--  migraAS.pereocenka(p_tvbv,p_open,p_code,p_errmask,p_dasvox);
--  commit;

--  bc.set_context;
    toroot;

    dbms_application_info.set_client_info(' ');

    bars_alerter(1);

  end load_ostcdSBON;

