--

  procedure create_dptASIM (p_tvbv in  char    ,
                            p_open out number  ,
                            p_code out number  ,
                            p_errmask  varchar2,
                            p_dasvox   date)
  is

    FIO_         varchar2(60);
    IDCODE_      varchar2(10);
    DOCTYPE_     number(1);
    PASP_S_      varchar2(2);
    PASP_N_      varchar2(8);
    PASP_W_      varchar2(120);
    PASP_D_      date;
    BIRTHDAT_    date;
    BIRTHPL_     varchar2(120);
    SEX_         number(1);
    POSTIDX_     varchar2(10);
    REGION_      varchar2(30);
    DISTRICT_    varchar2(30);
    CITY_        varchar2(30);
    ADDRESS_     varchar2(120);
    PHONE_H_     varchar2(20);
    PHONE_J_     varchar2(20);
    LANDCOD_     number(5);
    REGDATE_     date;
    DEPCODE_     varchar2(16);
    DEPVIDNAME_  varchar2(120);
    ACC_CARD_    varchar2(10);
    DEPNAME_     varchar2(120);
    NLS_         varchar2(20);
    ID_          varchar2(7);
    DATO_        date;
    OST_         number(16);
    SUM_         number(16);
    DATN_        date;
    ATTR_        varchar2(16);
    MARK_        varchar2(1);
    VER_         number(4);
    KOD_OTD_     varchar2(10);
    BRANCH_      varchar2(30);
    BSD_         varchar2(7);
    OB22DE_      varchar2(2);
    BSN_         varchar2(7);
    OB22IE_      varchar2(7);
    BSD7_        varchar2(7);
    OB22D7_      varchar2(2);

    DZAGR_       date;
    tvbv_        char(3);
    fl_          int;
    DATPRC_      date;
    PRC_         number(8,4);

    type         cur is ref cursor;
    cur_         cur;
    sql_         varchar2(4000);

  begin

    p_open := 0;
    p_code := 0;

    sql_:='select FIO                 , --FIO_
                  IDCODE              , --IDCODE_
                  DOCTYPE             , --DOCTYPE_
                  PASP_S              , --PASP_S_
                  PASP_N              , --PASP_N_
                  PASP_W              , --PASP_W_
                  PASP_D              , --PASP_D_
                  BIRTHDAT            , --BIRTHDAT_
                  BIRTHPL             , --BIRTHPL_
                  SEX                 , --SEX_
                  POSTIDX             , --POSTIDX_
                  REGION              , --REGION_
                  DISTRICT            , --DISTRICT_
                  CITY                , --CITY_
                  ADDRESS             , --ADDRESS_
                  PHONE_H             , --PHONE_H_
                  PHONE_J             , --PHONE_J_
                  LANDCOD             , --LANDCOD_
                  REGDATE             , --REGDATE_
                  DEPCODE             , --DEPCODE_
                  DEPVIDNAME          , --DEPVIDNAME_
                  ACC_CARD            , --ACC_CARD_
                  DEPNAME             , --DEPNAME_
                  NLS                 , --NLS_
                  ID                  , --ID_
                  DATO                , --DATO_
                  OST                 , --OST_
                  SUM                 , --SUM_
                  DATN                , --DATN_
                  ATTR                , --ATTR_
                  MARK                , --MARK_
                  VER                 , --VER_
                  KOD_OTD             , --KOD_OTD_
                  BRANCH              , --BRANCH_
                  BSD                 , --BSD_
                  lpad(OB22DE,2,''0''), --OB22DE_
                  BSN                 , --BSN_
                  lpad(OB22IE,2,''0''), --OB22IE_
                  BSD7                , --BSD7_
                  lpad(OB22D7,2,''0'')  --OB22D7_
           from   ASVO_FDEP';
    open cur_ for sql_;

    loop

      fetch cur_ into FIO_       ,
                      IDCODE_    ,
                      DOCTYPE_   ,
                      PASP_S_    ,
                      PASP_N_    ,
                      PASP_W_    ,
                      PASP_D_    ,
                      BIRTHDAT_  ,
                      BIRTHPL_   ,
                      SEX_       ,
                      POSTIDX_   ,
                      REGION_    ,
                      DISTRICT_  ,
                      CITY_      ,
                      ADDRESS_   ,
                      PHONE_H_   ,
                      PHONE_J_   ,
                      LANDCOD_   ,
                      REGDATE_   ,
                      DEPCODE_   ,
                      DEPVIDNAME_,
                      ACC_CARD_  ,
                      DEPNAME_   ,
                      NLS_       ,
                      ID_        ,
                      DATO_      ,
                      OST_       ,
                      SUM_       ,
                      DATN_      ,
                      ATTR_      ,
                      MARK_      ,
                      VER_       ,
                      KOD_OTD_   ,
                      BRANCH_    ,
                      BSD_       ,
                      OB22DE_    ,
                      BSN_       ,
                      OB22IE_    ,
                      BSD7_      ,
                      OB22D7_;

      exit when cur_%notfound;

--    убираем нецифровые символы в полях PHONE_H, PHONE_J, PASP_N, IDCODE
--    убираем непечатные символы в полях ATTR, MARK

      PHONE_H_ := f_drop_undigital(PHONE_H_);
      PHONE_J_ := f_drop_undigital(PHONE_J_);
      PASP_N_  := f_drop_undigital(PASP_N_);
      IDCODE_  := f_drop_undigital(IDCODE_);
      ATTR_    := f_drop_nonprinting(ATTR_);
      MARK_    := f_drop_nonprinting(MARK_);

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      bars_audit.info(substr(p_errmask,1,length(p_errmask)-3)||'DEPCODE=' ||DEPCODE_ ||
                                                              ' BRANCH='  ||BRANCH_  ||
                                                              ' ACC_CARD='||ACC_CARD_||
                                                              ' NLS='     ||NLS_     ||
                                                              ' ID='      ||ID_      ||
                                                              ' ost='     ||ost_     ||
                                                              ' sum='     ||sum_     ||
                                                              ' ATTR='    ||ATTR_    ||
                                                              ' MARK='    ||MARK_    ||
                                                              ' KOD_OTD=' ||KOD_OTD_ ||
                                                              ' idcode='  ||IDCODE_  ||
                                                              ' fio='     ||FIO_);
      begin
        insert
        into   ASVO_IMMOBILE (TVBV      ,
                              FIO       ,
                              IDCODE    ,
                              DOCTYPE   ,
                              PASP_S    ,
                              PASP_N    ,
                              PASP_W    ,
                              PASP_D    ,
                              BIRTHDAT  ,
                              BIRTHPL   ,
                              SEX       ,
                              POSTIDX   ,
                              REGION    ,
                              DISTRICT  ,
                              CITY      ,
                              ADDRESS   ,
                              PHONE_H   ,
                              PHONE_J   ,
                              LANDCOD   ,
                              REGDATE   ,
                              DEPCODE   ,
                              DEPVIDNAME,
                              ACC_CARD  ,
                              DEPNAME   ,
                              NLS       ,
                              ID        ,
                              DATO      ,
                              OST       ,
                              SUM       ,
                              DATN      ,
                              ATTR      ,
                              MARK      ,
                              VER       ,
                              KOD_OTD   ,
                              BRANCH    ,
                              BSD       ,
                              OB22DE    ,
                              BSN       ,
                              OB22IE    ,
                              BSD7      ,
                              OB22D7    ,
                              FL        ,
                              DZAGR)
                      values (p_tvbv       ,
                              FIO_         ,
                              IDCODE_      ,
                              DOCTYPE_     ,
                              PASP_S_      ,
                              PASP_N_      ,
                              PASP_W_      ,
                              PASP_D_      ,
                              BIRTHDAT_    ,
                              BIRTHPL_     ,
                              SEX_         ,
                              POSTIDX_     ,
                              REGION_      ,
                              DISTRICT_    ,
                              CITY_        ,
                              ADDRESS_     ,
                              PHONE_H_     ,
                              PHONE_J_     ,
                              LANDCOD_     ,
                              REGDATE_     ,
                              DEPCODE_     ,
                              DEPVIDNAME_  ,
                              ACC_CARD_    ,
                              DEPNAME_     ,
                              NLS_         ,
                              ID_          ,
                              DATO_        ,
                              OST_         ,
                              SUM_         ,
                              DATN_        ,
                              ATTR_        ,
                              nvl(MARK_,' '),
                              VER_         ,
                              KOD_OTD_     ,
                              BRANCH_      ,
                              BSD_         ,
                              OB22DE_      ,
                              BSN_         ,
                              OB22IE_      ,
                              BSD7_        ,
                              OB22D7_      ,
                              -10          ,
                              sysdate);
        p_open := p_open+1;
        commit;
      EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
        begin
          select DZAGR,
                 tvbv ,
                 fl
          into   DZAGR_,
                 tvbv_ ,
                 fl_
          from   asvo_immobile
          where  KOD_OTD =KOD_OTD_       and
                 BRANCH  =BRANCH_        and
                 ACC_CARD=ACC_CARD_      and
                 MARK    =nvl(MARK_,' ') and
                 NLS     =NLS_           and
                 ID      =ID_;
          p_code := p_code+1;
          bars_audit.error(p_errmask          ||'(1): '           ||
                           'код ТВБВ='        ||tvbv_             ||
                           ', код отделения=' ||KOD_OTD_          ||
                           ', бранч='         ||BRANCH_           ||
                           ', код картотеки=' ||ACC_CARD_         ||
                           ', учетный символ='||MARK_             ||
                           ', номер вклада='  ||NLS_              ||
                           ', ID='            ||ID_               ||
                           ', ФИО='           ||FIO_              ||
                           ', ид.код='        ||IDCODE_           ||
                           ' - вклад УЖЕ загружен ('              ||
                           to_char(DZAGR_,'dd.mm.yyyy hh24:mi:ss')||
                           '), флаг обработки='||to_char(fl_));
        EXCEPTION WHEN no_data_found THEN
          p_code := p_code+1;
          bars_audit.error(p_errmask                ||'(0): '           ||
                           'код ТВБВ='              ||tvbv_             ||
                           ', код отделения='       ||KOD_OTD_          ||
                           ', бранч='               ||BRANCH_           ||
                           ', код картотеки='       ||ACC_CARD_         ||
                           ', учетный символ='      ||MARK_             ||
                           ', номер вклада='        ||NLS_              ||
                           ', ID='                  ||ID_               ||
                           ', ФИО='                 ||FIO_              ||
                           ', ид.код='              ||IDCODE_           ||
                           ' - неизвестная ошибка: '||sqlerrm);
--        tvbv_  := p_tvbv;
--        DZAGR_ := sysdate;
        end;
      end;

    end loop;
    close cur_;

    dbms_application_info.set_client_info('Загрузка процентных ставок...');

--  загрузка процентных ставок

    sql_:='select BRANCH  , --BRANCH_
                  ACC_CARD, --ACC_CARD_
                  MARK    , --MARK_
                  DATPRC  , --DATPRC_
                  PRC       --PRC_
           from   ASVO_FDPI';
    open cur_ for sql_;

    loop

      fetch cur_ into BRANCH_  ,
                      ACC_CARD_,
                      MARK_    ,
                      DATPRC_  ,
                      PRC_;

      exit when cur_%notfound;

      bars_audit.info(substr(p_errmask,1,length(p_errmask)-3)||'BRANCH='  ||BRANCH_                      ||
                                                              ' ACC_CARD='||ACC_CARD_                    ||
                                                              ' MARK='    ||MARK_                        ||
                                                              ' DATPRC='  ||to_char(DATPRC_,'dd.mm.yyyy')||
                                                              ' PRC='     ||to_char(PRC_));

      begin
        insert
        into   ASVO_IMMOBILE_PERCENT (TVBV    ,
                                      BRANCH  ,
                                      ACC_CARD,
                                      MARK    ,
                                      DATPRC  ,
                                      PRC)
                              values (p_tvbv        ,
                                      BRANCH_       ,
                                      ACC_CARD_     ,
                                      nvl(MARK_,' '),
                                      DATPRC_       ,
                                      PRC_);
        commit;
      EXCEPTION WHEN DUP_VAL_ON_INDEX THEN
        begin
          select PRC,
                 tvbv
          into   PRC_,
                 tvbv_
          from   asvo_immobile_percent
          where  BRANCH  =BRANCH_        and
                 ACC_CARD=ACC_CARD_      and
                 MARK    =nvl(MARK_,' ') and
                 DATPRC  =DATPRC_;
        EXCEPTION WHEN others THEN
          tvbv_  := p_tvbv;
        end;
        p_code := p_code+1;
        bars_audit.error(p_errmask          ||'(2): '          ||
                         'код ТВБВ='        ||tvbv_            ||
                         ', бранч='         ||BRANCH_          ||
                         ', код картотеки=' ||ACC_CARD_        ||
                         ', учетный символ='||MARK_            ||
                         ' - процентная ставка УЖЕ загружена ('||
                         to_char(DATPRC_,'dd.mm.yyyy')         ||
                         '), ставка='||to_char(PRC_));
      end;

    end loop;
    close cur_;

    dbms_application_info.set_client_info(' ');

  exception when others then
    bars_audit.error(p_errmask||'(Z): неожиданная ошибка - '||sqlerrm||' '||dbms_utility.format_error_backtrace||' ('||migraas.header_version||' '||migraas.body_version||')');
    p_code := p_code+1;

  end create_dptASIM;

--

  procedure drop_deposASIM (p_tvbv in  char    ,
                            p_open out number  ,
                            p_code out number  ,
                            p_errmask  varchar2,
                            p_dasvox   date)
  is

    type       cur is ref cursor;
    cur_       cur;
    sql_       varchar2(4000);

    KOD_OTD_   varchar2(10);
    BRANCH_    varchar2(30);
    ACC_CARD_  varchar2(10);
    MARK_      varchar2(1);
    NLS_       varchar2(20);
    ID_        varchar2(7);
    FIO_       varchar2(60);
    IDCODE_    varchar2(10);
    fl_        int;
    DATPRC_    date;

  begin

    p_open := 0;
    p_code := 0;

    sql_:='select KOD_OTD ,  --KOD_OTD_
                  BRANCH  ,  --BRANCH_
                  ACC_CARD,  --ACC_CARD_
                  MARK    ,  --MARK_
                  NLS     ,  --NLS_
                  ID      ,  --ID_
                  FIO     ,  --FIO_
                  IDCODE     --IDCODE_
           from   ASVO_FDEP';
    open cur_ for sql_;

    loop

      fetch cur_ into KOD_OTD_ ,
                      BRANCH_  ,
                      ACC_CARD_,
                      MARK_    ,
                      NLS_     ,
                      ID_      ,
                      FIO_     ,
                      IDCODE_;

      exit when cur_%notfound;

      dbms_application_info.set_client_info('open='||to_char(p_open)||
                                         ', error='||to_char(p_code));

      begin
        select fl
        into   fl_
        from   ASVO_IMMOBILE
        where  tvbv    =p_tvbv         and
               KOD_OTD =KOD_OTD_       and
               BRANCH  =BRANCH_        and
               ACC_CARD=ACC_CARD_      and
               MARK    =nvl(MARK_,' ') and
               NLS     =NLS_           and
               ID      =ID_;
        if fl_!=-10 then
          bars_audit.error(p_errmask          ||'(1): '  ||
                           'код ТВБВ='        ||p_tvbv   ||
                           ', код отделения=' ||KOD_OTD_ ||
                           ', бранч='         ||BRANCH_  ||
                           ', код картотеки=' ||ACC_CARD_||
                           ', учетный символ='||MARK_    ||
                           ', номер вклада='  ||NLS_     ||
                           ', ID='            ||ID_      ||
                           ', ФИО='           ||FIO_     ||
