    KOTL_     accounts.nls%type;
    nms_      accounts.nms%type;

  begin

    bars_alerter(0);

    p_open := 0;
    p_code := 0;

--  select branch
--  into   branch_
--  from   ASVO_FFF_BRANCH
--  where  FFF=p_tvbv and
--         rownum<2;

--  bars_audit.info('load_ostcdptAS - @ branch_='||branch_);

--  депозиты

--  bc.subst_branch(branch_);

    tokf;

    begin
